package 'PubSubSystem' {
	attribute def Message;
	attribute def Topic;
	attribute def Subscription;

	port def PublishPort {
		in topic : Topic;
		in message : Message;
	}

	port def SubscribePort {
		in topic : Topic;
		out subscription : Subscription;
	}

	port def ReceivePort {
		out topic : Topic;
		out message : Message;
	}

	part context {
		part producer {
			port publishPort : PublishPort;
			perform messaging.publishMessage {
				out topic = publishPort.topic;
				out message = publishPort.message;
			}
		}

		interface producerToServer connect producer.publishPort to server.publishPort {
			ref flow references messaging.publishFlow
				from source.topic to target.topic;
			ref flow references messaging.publishFlow
				from source.message to target.message;
		}

		part server {
			port publishPort : ~PublishPort;
			port subscribePort : ~SubscribePort;
			port distributePort : ReceivePort;

			perform messaging.routeMessage {
				in topic = publishPort.topic;
				in message = publishPort.message;
				in subscription = subscribePort.subscription;
				out distributeTopic = distributePort.topic;
				out distributeMessage = distributePort.message;
			}
		}

		interface serverToConsumer connect server.distributePort to consumer.receivePort {
			ref flow references messaging.distributeFlow
				from source.topic to target.topic;
			ref flow references messaging.distributeFlow
				from source.message to target.message;
		}

		interface consumerToServer connect consumer.subscribePort to server.subscribePort {
			ref flow references messaging.subscribeFlow
				from source.topic to target.topic;
			ref flow references messaging.subscribeFlow
				from source.subscription to target.subscription;
		}

		part consumer {
			port subscribePort : SubscribePort;
			port receivePort : ~ReceivePort;

			perform messaging.subscribe {
				out topic = subscribePort.topic;
				in subscription = subscribePort.subscription;
			}

			perform messaging.receiveMessage {
				in topic = receivePort.topic;
				in message = receivePort.message;
			}
		}
	}

	action messaging {
		action publishMessage { out topic : Topic; out message : Message; }
		succession flow publishFlow from publishMessage to routeMessage;

		action subscribe { out topic : Topic; in subscription : Subscription; }
		succession flow subscribeFlow from subscribe to routeMessage;

		action routeMessage { 
			in topic : Topic; 
			in message : Message; 
			in subscription : Subscription;
			out distributeTopic : Topic;
			out distributeMessage : Message;
		}
		succession flow distributeFlow from routeMessage to receiveMessage;

		action receiveMessage { in topic : Topic; in message : Message; }
	}
}

package 'VehicleFuelSystem' {
	attribute def Fuel;
	attribute def FuelTemperature;

	port def FuelSupplyPort {
		out fuel: Fuel;
		out fuelTemp: FuelTemperature;
	}

	port def FuelReturnPort {
		in fuel: Fuel;
		out fuelTemp: FuelTemperature;
	}

	part vehicle {
		part fuelTankAssembly {
			port supplyPort: FuelSupplyPort;
			port returnPort: ~FuelReturnPort;

			perform fuelFlow.supplyFuel {
				out fuel = supplyPort.fuel;
				out fuelTemp = supplyPort.fuelTemp;
			}

			perform fuelFlow.receiveFuel {
				in fuel = returnPort.fuel;
				out fuelTemp = returnPort.fuelTemp;
			}
		}

		part engine {
			port supplyPort: ~FuelSupplyPort;
			port returnPort: FuelReturnPort;

			perform fuelFlow.consumeFuel {
				in fuel = supplyPort.fuel;
				in fuelTemp = supplyPort.fuelTemp;
				out unusedFuel = returnPort.fuel;
				out returnTemp = returnPort.fuelTemp;
			}
		}

		interface fuelSupplyLine connect fuelTankAssembly.supplyPort to engine.supplyPort {
			ref flow references fuelFlow.supplyFlow
				from source.fuel to target.fuel;
			ref flow references fuelFlow.supplyTempFlow
				from source.fuelTemp to target.fuelTemp;
		}

		interface fuelReturnLine connect engine.returnPort to fuelTankAssembly.returnPort {
			ref flow references fuelFlow.returnFlow
				from source.fuel to target.fuel;
			ref flow references fuelFlow.returnTempFlow
				from source.fuelTemp to target.fuelTemp;
		}
	}

	action fuelFlow {
		action supplyFuel { out fuel: Fuel; out fuelTemp: FuelTemperature; }
		succession flow supplyFlow from supplyFuel.fuel to consumeFuel.fuel;
		succession flow supplyTempFlow from supplyFuel.fuelTemp to consumeFuel.fuelTemp;

		action consumeFuel { 
			in fuel: Fuel; 
			in fuelTemp: FuelTemperature;
			out unusedFuel: Fuel;
			out returnTemp: FuelTemperature;
		}
		succession flow returnFlow from consumeFuel.unusedFuel to receiveFuel.fuel;
		succession flow returnTempFlow from consumeFuel.returnTemp to receiveFuel.fuelTemp;

		action receiveFuel { in fuel: Fuel; out fuelTemp: FuelTemperature; }
	}
}

package 'PublishSubscribeSystem' {
	attribute def Topic;
	attribute def Message;
	attribute def SubscriptionRequest;

	port def SubscriptionPort {
		out subscriptionRequest : SubscriptionRequest;
	}

	port def MessagePublishPort {
		out message : Message;
	}

	port def MessageDeliveryPort {
		out message : Message;
	}

	part context {
		part consumer[1..*] {
			port subscriptionPort : SubscriptionPort;
			port messageDeliveryPort : ~MessageDeliveryPort;

			perform messageFlow.subscribe {
				out subscriptionRequest = subscriptionPort.subscriptionRequest;
			}

			perform messageFlow.receiveMessage {
				in message = messageDeliveryPort.message;
			}
		}

		interface consumerToServer connect consumer.subscriptionPort to server.subscriptionPort {
			ref flow references messageFlow.subscriptionFlow
				from source.subscriptionRequest to target.subscriptionRequest;
		}

		interface serverToConsumer connect server.messageDeliveryPort to consumer.messageDeliveryPort {
			ref flow references messageFlow.deliveryFlow
				from source.message to target.message;
		}

		part server {
			port subscriptionPort : ~SubscriptionPort;
			port messagePublishPort : ~MessagePublishPort;
			port messageDeliveryPort : MessageDeliveryPort;

			perform messageFlow.processSubscription {
				in subscriptionRequest = subscriptionPort.subscriptionRequest;
			}

			perform messageFlow.routeMessage {
				in message = messagePublishPort.message;
				out message = messageDeliveryPort.message;
			}
		}

		part producer[1..*] {
			port messagePublishPort : MessagePublishPort;

			perform messageFlow.publishMessage {
				out message = messagePublishPort.message;
			}
		}

		interface producerToServer connect producer.messagePublishPort to server.messagePublishPort {
			ref flow references messageFlow.publishFlow
				from source.message to target.message;
		}
	}

	action messageFlow {
		action subscribe { out subscriptionRequest : SubscriptionRequest; }
		succession flow subscriptionFlow from subscribe.subscriptionRequest to processSubscription.subscriptionRequest;
		action processSubscription { in subscriptionRequest : SubscriptionRequest; }

		action publishMessage { out message : Message; }
		succession flow publishFlow from publishMessage.message to routeMessage.message;
		action routeMessage { in message : Message; out message : Message; }
		succession flow deliveryFlow from routeMessage.message to receiveMessage.message;
		action receiveMessage { in message : Message; }
	}
}

