package 'FuelSystem' {
	attribute def Fuel;
	attribute def Temperature;

	port def FuelSupplyPort {
		out fuel: Fuel;
		out temperature: Temperature;
	}

	port def FuelReturnPort {
		in fuel: Fuel;
		out temperature: Temperature;
	}

	part fuelSystemContext {
		part fuelTankAssembly {
			port supplyPort: FuelSupplyPort;
			port returnPort: FuelReturnPort;

			perform manageFuel.supplyFuel {
				out fuel = supplyPort.fuel;
				out temperature = supplyPort.temperature;
			}

			perform manageFuel.receiveFuel {
				in fuel = returnPort.fuel;
				out temperature = returnPort.temperature;
			}
		}

		interface fuelSupplyInterface connect fuelTankAssembly.supplyPort to engine.supplyPort {
			ref flow references manageFuel.fuelSupplyFlow
				from source.fuel to target.fuel;
			ref flow references manageFuel.supplyTempFlow
				from source.temperature to target.temperature;
		}

		interface fuelReturnInterface connect engine.returnPort to fuelTankAssembly.returnPort {
			ref flow references manageFuel.fuelReturnFlow
				from source.fuel to target.fuel;
			ref flow references manageFuel.returnTempFlow
				from source.temperature to target.temperature;
		}

		part engine {
			port supplyPort: ~FuelSupplyPort;
			port returnPort: ~FuelReturnPort;

			perform manageFuel.consumeFuel {
				in fuel = supplyPort.fuel;
				in temperature = supplyPort.temperature;
				out returnFuel = returnPort.fuel;
				out returnTemperature = returnPort.temperature;
			}
		}
	}

	action manageFuel {
		action supplyFuel { out fuel: Fuel; out temperature: Temperature; }
		succession flow fuelSupplyFlow from supplyFuel.fuel to consumeFuel.fuel;
		succession flow supplyTempFlow from supplyFuel.temperature to consumeFuel.temperature;

		action consumeFuel { 
			in fuel: Fuel; 
			in temperature: Temperature;
			out returnFuel: Fuel;
			out returnTemperature: Temperature;
		}
		succession flow fuelReturnFlow from consumeFuel.returnFuel to receiveFuel.fuel;
		succession flow returnTempFlow from consumeFuel.returnTemperature to receiveFuel.temperature;

		action receiveFuel { in fuel: Fuel; out temperature: Temperature; }
	}
}

