	port def BatteryLevelPort {
		out batteryLevel : BatteryLevel;
	}

	port def ChargingPowerPort {
		in chargingPower : ChargingPower;
	}

	port def ChargingControlPort {
		out chargingStatus : ChargingStatus;
	}

	part context {
		part battery {
			port batteryLevelPort : BatteryLevelPort;
			port chargingPowerPort : ChargingPowerPort;

			perform automaticChargingControl.provideBatteryLevel {
				out batteryLevel = batteryLevelPort.batteryLevel;
			}

			perform automaticChargingControl.receiveCharge {
				in chargingPower = chargingPowerPort.chargingPower;
			}
		}

		interface batteryToController connect battery.batteryLevelPort to chargingController.batteryLevelPort {
			ref flow references automaticChargingControl.batteryLevelFlow
				from source.batteryLevel to target.batteryLevel;
		}

		part chargingController {
			port batteryLevelPort : ~BatteryLevelPort;
			port chargingControlPort : ChargingControlPort;

			perform automaticChargingControl.monitorAndControl {
				in batteryLevel = batteryLevelPort.batteryLevel;
				out chargingStatus = chargingControlPort.chargingStatus;
			}
		}

		interface controllerToCharger connect chargingController.chargingControlPort to charger.chargingControlPort {
			ref flow references automaticChargingControl.chargingControlFlow
				from source.chargingStatus to target.chargingStatus;
		}

		part charger {
			port chargingControlPort : ~ChargingControlPort;
			port chargingPowerPort : ~ChargingPowerPort;

			perform automaticChargingControl.supplyCharge {
				in chargingStatus = chargingControlPort.chargingStatus;
				out chargingPower = chargingPowerPort.chargingPower;
			}
		}

		interface chargerToBattery connect charger.chargingPowerPort to battery.chargingPowerPort {
			ref flow references automaticChargingControl.chargingPowerFlow
				from source.chargingPower to target.chargingPower;
		}
	}

	action automaticChargingControl {
		action provideBatteryLevel { out batteryLevel : BatteryLevel; }
		succession flow batteryLevelFlow from provideBatteryLevel.batteryLevel to monitorAndControl.batteryLevel;
		action monitorAndControl { in batteryLevel : BatteryLevel; out chargingStatus : ChargingStatus; }
		succession flow chargingControlFlow from monitorAndControl.chargingStatus to supplyCharge.chargingStatus;
		action supplyCharge { in chargingStatus : ChargingStatus; out chargingPower : ChargingPower; }
		succession flow chargingPowerFlow from supplyCharge.chargingPower to receiveCharge.chargingPower;
		action receiveCharge { in chargingPower : ChargingPower; }
	}
}

package 'BatteryChargingSystem' {
	attribute def BatteryLevel;
	attribute def ChargingPower;
	attribute def ChargingStatus;

	port def BatteryLevelPort {
		out batteryLevel : BatteryLevel;
	}

	port def ChargingPowerPort {
		in chargingPower : ChargingPower;
	}

	port def ChargingStatusPort {
		out chargingStatus : ChargingStatus;
	}

	part context {
		part charger {
			port chargingStatusPort : ChargingStatusPort;
			port chargingPowerPort : ~ChargingPowerPort;

			perform chargingProcess.supplyPower {
				in chargingStatus = chargingStatusPort.chargingStatus;
				out chargingPower = chargingPowerPort.chargingPower;
			}
		}

		interface chargerToBattery connect charger.chargingPowerPort to battery.chargingPowerPort {
			ref flow references chargingProcess.chargingPowerFlow
				from source.chargingPower to target.chargingPower;
		}

		part battery {
			port chargingPowerPort : ChargingPowerPort;
			port batteryLevelPort : BatteryLevelPort;

			perform chargingProcess.replenishBattery {
				in chargingPower = chargingPowerPort.chargingPower;
				out batteryLevel = batteryLevelPort.batteryLevel;
			}
		}

		interface batteryToController connect battery.batteryLevelPort to controller.batteryLevelPort {
			ref flow references chargingProcess.batteryLevelFlow
				from source.batteryLevel to target.batteryLevel;
		}

		part controller {
			port batteryLevelPort : ~BatteryLevelPort;
			port chargingStatusPort : ~ChargingStatusPort;

			perform chargingProcess.monitorAndControl {
				in batteryLevel = batteryLevelPort.batteryLevel;
				out chargingStatus = chargingStatusPort.chargingStatus;
			}
		}

		interface controllerToCharger connect controller.chargingStatusPort to charger.chargingStatusPort {
			ref flow references chargingProcess.chargingStatusFlow
				from source.chargingStatus to target.chargingStatus;
		}
	}

	action chargingProcess {
		action monitorAndControl { 
			in batteryLevel : BatteryLevel; 
			out chargingStatus : ChargingStatus;
		}
		succession flow chargingStatusFlow from monitorAndControl.chargingStatus to supplyPower.chargingStatus;

		action supplyPower { 
			in chargingStatus : ChargingStatus; 
			out chargingPower : ChargingPower;
		}
		succession flow chargingPowerFlow from supplyPower.chargingPower to replenishBattery.chargingPower;

		action replenishBattery { 
			in chargingPower : ChargingPower; 
			out batteryLevel : BatteryLevel;
		}
		succession flow batteryLevelFlow from replenishBattery.batteryLevel to monitorAndControl.batteryLevel;
	}
}

package 'CameraSystem' {
	attribute def Scene;
	attribute def FocusData;
	attribute def Image;
	attribute def Photo;

	port def ScenePort {
		out scene : Scene;
	}
	port def FocusDataPort {
		out focusData : FocusData;
	}
	port def ImagePort {
		out image : Image;
	}
	port def PhotoPort {
		out photo : Photo;
	}

	part context {
		part user {
			port scenePort : ScenePort;
			perform takePhoto.selectScene {
				out scene = scenePort.scene;
			}
		}

		interface userToCamera connect user.scenePort to camera.scenePort {
			ref flow references takePhoto.sceneFlow
				from source.scene to target.scene;
		}

		part camera {
			port scenePort : ~ScenePort;
			port photoPort : PhotoPort;

			part autofocusSubsystem {
				port scenePort : ~ScenePort;
				port focusDataPort : FocusDataPort;
				perform takePhoto.autoFocus {
					in scene = scenePort.scene;
					out focusData = focusDataPort.focusData;
				}
			}

			part imagingSubsystem {
				port focusDataPort : ~FocusDataPort;
				port imagePort : ImagePort;
				perform takePhoto.captureImage {
					in focusData = focusDataPort.focusData;
					out image = imagePort.image;
				}
			}

			interface autofocusToImaging connect autofocusSubsystem.focusDataPort to imagingSubsystem.focusDataPort {
				ref flow references takePhoto.focusFlow
					from source.focusData to target.focusData;
			}

			perform takePhoto.savePhoto {
				in image = imagingSubsystem.imagePort.image;
				out photo = photoPort.photo;
			}
		}
	}

	action takePhoto {
		action selectScene { out scene : Scene; }
		succession flow sceneFlow from selectScene.scene to autoFocus.scene;
		action autoFocus { in scene : Scene; out focusData : FocusData; }
		succession flow focusFlow from autoFocus.focusData to captureImage.focusData;
		action captureImage { in focusData : FocusData; out image : Image; }
		succession flow imageFlow from captureImage.image to savePhoto.image;
		action savePhoto { in image : Image; out photo : Photo; }
	}
}

