package 'VehicleSimulation' {
	attribute def Mass;
	attribute def Position;
	attribute def Velocity;
	attribute def Power;
	attribute def TimeStep;
	attribute def Trajectory;

	port def SimulationInputPort {
		in mass: Mass;
		in initialPosition: Position;
		in initialVelocity: Velocity;
		in timeStep: TimeStep;
		in powerSequence: Power[*];
	}

	port def SimulationOutputPort {
		out trajectory: Trajectory;
	}

	part context {
		part user {
			port simulationInputPort: SimulationInputPort;
			perform simulateVehicleMotion.provideInputs {
				out mass = simulationInputPort.mass;
				out initialPosition = simulationInputPort.initialPosition;
				out initialVelocity = simulationInputPort.initialVelocity;
				out timeStep = simulationInputPort.timeStep;
				out powerSequence = simulationInputPort.powerSequence;
			}
		}

		interface userToSimulator connect user.simulationInputPort to simulator.simulationInputPort {
			ref flow references simulateVehicleMotion.massFlow
				from source.mass to target.mass;
			ref flow references simulateVehicleMotion.initialPositionFlow
				from source.initialPosition to target.initialPosition;
			ref flow references simulateVehicleMotion.initialVelocityFlow
				from source.initialVelocity to target.initialVelocity;
			ref flow references simulateVehicleMotion.timeStepFlow
				from source.timeStep to target.timeStep;
			ref flow references simulateVehicleMotion.powerSequenceFlow
				from source.powerSequence to target.powerSequence;
		}

		part simulator {
			port simulationInputPort: ~SimulationInputPort;
			port simulationOutputPort: SimulationOutputPort;
			perform simulateVehicleMotion.calculateTrajectory {
				in mass = simulationInputPort.mass;
				in initialPosition = simulationInputPort.initialPosition;
				in initialVelocity = simulationInputPort.initialVelocity;
				in timeStep = simulationInputPort.timeStep;
				in powerSequence = simulationInputPort.powerSequence;
				out trajectory = simulationOutputPort.trajectory;
			}
		}
	}

	action simulateVehicleMotion {
		action provideInputs {
			out mass: Mass;
			out initialPosition: Position;
			out initialVelocity: Velocity;
			out timeStep: TimeStep;
			out powerSequence: Power[*];
		}
		succession flow massFlow from provideInputs.mass to calculateTrajectory.mass;
		succession flow initialPositionFlow from provideInputs.initialPosition to calculateTrajectory.initialPosition;
		succession flow initialVelocityFlow from provideInputs.initialVelocity to calculateTrajectory.initialVelocity;
		succession flow timeStepFlow from provideInputs.timeStep to calculateTrajectory.timeStep;
		succession flow powerSequenceFlow from provideInputs.powerSequence to calculateTrajectory.powerSequence;
		action calculateTrajectory {
			in mass: Mass;
			in initialPosition: Position;
			in initialVelocity: Velocity;
			in timeStep: TimeStep;
			in powerSequence: Power[*];
			out trajectory: Trajectory;
		}
	}
}

