package 'SensorManagementSystem' {
	attribute def SensorID;
	attribute def SensorStatus;
	attribute def UpdateCommand;
	attribute def ReadyState;

	port def StatusPort {
		out status : SensorStatus;
	}

	port def UpdatePort {
		in updateCmd : UpdateCommand;
	}

	part def Sensor {
		attribute id : SensorID;
		port statusPort : StatusPort;
		port updatePort : UpdatePort;

		perform maintainSensors.reportStatus {
			out status = statusPort.status;
		}

		perform maintainSensors.receiveUpdate {
			in updateCmd = updatePort.updateCmd;
		}
	}

	part context {
		part managementSystem {
			port statusPort[*] : ~StatusPort;
			port updatePort[*] : ~UpdatePort;

			perform maintainSensors.monitorSensors {
				in status[*] = statusPort.status;
				out readySensors[*] : ReadyState;
			}

			perform maintainSensors.updateReadySensors {
				in readySensors[*] : ReadyState;
				out updateCmd[*] = updatePort.updateCmd;
			}
		}

		part sensors[*] : Sensor;

		interface sensorToSystem[*] connect sensors.statusPort to managementSystem.statusPort {
			ref flow references maintainSensors.statusFlow
				from source.status to target.status;
		}

		interface systemToSensor[*] connect managementSystem.updatePort to sensors.updatePort {
			ref flow references maintainSensors.updateFlow
				from source.updateCmd to target.updateCmd;
		}
	}

	action maintainSensors {
		action reportStatus[*] { out status : SensorStatus; }
		succession flow statusFlow[*] from reportStatus.status to monitorSensors.status;

		action monitorSensors { 
			in status[*] : SensorStatus; 
			out readySensors[*] : ReadyState;
		}
		succession flow readyFlow from monitorSensors.readySensors to updateReadySensors.readySensors;

		action updateReadySensors { 
			in readySensors[*] : ReadyState;
			out updateCmd[*] : UpdateCommand;
		}
		succession flow updateFlow[*] from updateReadySensors.updateCmd to receiveUpdate.updateCmd;

		action receiveUpdate[*] { in updateCmd : UpdateCommand; }
	}
}

package 'VehicleStateManagement' {
	attribute def StartSignal;
	attribute def PowerOnSignal;
	attribute def ShutdownSignal;

	port def ControlSignalPort {
		in startSignal : StartSignal;
		in powerOnSignal : PowerOnSignal;
		in shutdownSignal : ShutdownSignal;
	}

	part vehicle {
		port controlPort : ControlSignalPort;

		exhibit state vehicleStates {
			entry; then off;

			state off;
			transition off_to_starting
				first off
				accept controlPort.startSignal
				then starting;

			state starting;
			transition starting_to_on
				first starting
				accept controlPort.powerOnSignal
				then on;
			transition starting_to_off
				first starting
				accept controlPort.shutdownSignal
				then off;

			state on;
			transition on_to_off
				first on
				accept controlPort.shutdownSignal
				then off;
		}
	}
}

package 'VehicleOperationalSystem' {
	attribute def VehicleStartSignal;
	attribute def VehicleOnSignal;
	attribute def VehicleOffSignal;

	state def VehicleStates {
		entry state off;
		state starting;
		state on;

		transition off_to_starting
			first off
			accept vehicleStartSignal : VehicleStartSignal
			then starting;

		transition starting_to_on
			first starting
			accept vehicleOnSignal : VehicleOnSignal
			then on;

		transition on_to_off
			first on
			accept vehicleOffSignal : VehicleOffSignal
			then off;
	}

	part vehicleController {
		exhibit vehicleStates : VehicleStates;
	}
}

package 'VehiclePowerManagement' {
	attribute def StartCmd;
	attribute def ShutdownCmd;
	attribute def VehicleStartedSignal;
	attribute def PowerSupply;
	attribute def DiagnosticData;
	attribute def ParkingBrakeSignal;

	port def StartCmdPort {
		out startCmd : StartCmd;
	}
	port def ShutdownCmdPort {
		out shutdownCmd : ShutdownCmd;
	}
	port def VehicleStartedSignalPort {
		in vehicleStartedSignal : VehicleStartedSignal;
	}
	port def PowerSupplyPort {
		out powerSupply : PowerSupply;
	}
	port def DiagnosticDataPort {
		out diagnosticData : DiagnosticData;
	}
	port def ParkingBrakeSignalPort {
		out parkingBrakeSignal : ParkingBrakeSignal;
	}

	part context {
		part user {
			port startCmdPort : StartCmdPort;
			port shutdownCmdPort : ShutdownCmdPort;
			perform managePowerStatus.sendStartCmd {
				out startCmd = startCmdPort.startCmd;
			}
			perform managePowerStatus.sendShutdownCmd {
				out shutdownCmd = shutdownCmdPort.shutdownCmd;
			}
		}

		interface userToSystem connect user.startCmdPort to powerManagementSystem.startCmdPort {
			ref flow references managePowerStatus.startCmdFlow
				from source.startCmd to target.startCmd;
		}

		interface userToSystemShutdown connect user.shutdownCmdPort to powerManagementSystem.shutdownCmdPort {
			ref flow references managePowerStatus.shutdownCmdFlow
				from source.shutdownCmd to target.shutdownCmd;
		}

		part powerManagementSystem {
			port startCmdPort : ~StartCmdPort;
			port shutdownCmdPort : ~ShutdownCmdPort;
			port vehicleStartedSignalPort : VehicleStartedSignalPort;
			port powerSupplyPort : PowerSupplyPort;
			port diagnosticDataPort : DiagnosticDataPort;
			port parkingBrakeSignalPort : ParkingBrakeSignalPort;

			perform managePowerStatus.switchToStartPhase {
				in startCmd = startCmdPort.startCmd;
			}
			perform managePowerStatus.enterPoweredOnState {
				in vehicleStartedSignal = vehicleStartedSignalPort.vehicleStartedSignal;
				out powerSupply = powerSupplyPort.powerSupply;
				out diagnosticData = diagnosticDataPort.diagnosticData;
			}
			perform managePowerStatus.executeSafetyOperations {
				in shutdownCmd = shutdownCmdPort.shutdownCmd;
				out parkingBrakeSignal = parkingBrakeSignalPort.parkingBrakeSignal;
			}
		}

		part vehicleFunctionalUnits {
			port powerSupplyPort : ~PowerSupplyPort;
			perform managePowerStatus.receivePower {
				in powerSupply = powerSupplyPort.powerSupply;
			}
		}

		interface systemToFunctionalUnits connect powerManagementSystem.powerSupplyPort to vehicleFunctionalUnits.powerSupplyPort {
			ref flow references managePowerStatus.powerSupplyFlow
				from source.powerSupply to target.powerSupply;
		}
	}

	action managePowerStatus {
		action sendStartCmd { out startCmd : StartCmd; }
		succession flow startCmdFlow from sendStartCmd.startCmd to switchToStartPhase.startCmd;
		action switchToStartPhase { in startCmd : StartCmd; }
		succession flow startPhaseFlow from switchToStartPhase to enterPoweredOnState;
		action enterPoweredOnState { 
			in vehicleStartedSignal : VehicleStartedSignal; 
			out powerSupply : PowerSupply;
			out diagnosticData : DiagnosticData;
		}
		succession flow powerSupplyFlow from enterPoweredOnState.powerSupply to receivePower.powerSupply;
		action receivePower { in powerSupply : PowerSupply; }
		action sendShutdownCmd { out shutdownCmd : ShutdownCmd; }
		succession flow shutdownCmdFlow from sendShutdownCmd.shutdownCmd to executeSafetyOperations.shutdownCmd;
		action executeSafetyOperations { 
			in shutdownCmd : ShutdownCmd;
			out parkingBrakeSignal : ParkingBrakeSignal;
		}
	}
}

package 'VehicleControl' {
	attribute def StartSignal;
	attribute def StartedSignal;
	attribute def ShutdownSignal;
	attribute def VehicleStatus;

	port def ControlSignalPort {
		in startSignal : StartSignal;
		in startedSignal : StartedSignal;
		in shutdownSignal : ShutdownSignal;
	}

	port def StatusPort {
		out vehicleStatus : VehicleStatus;
	}

	part vehicleSystem {
		port controlPort : ControlSignalPort;
		port statusPort : StatusPort;

		exhibit state vehicleStates {
			entry; then shutdown;

			state shutdown {
				entry assign statusPort.vehicleStatus = "shutdown";
				transition to startup
					when controlPort.startSignal;
			}

			state startup {
				entry assign statusPort.vehicleStatus = "startup";
				transition to running
					when controlPort.startedSignal;
			}

			state running {
				entry assign statusPort.vehicleStatus = "running";
				transition to shutdown
					when controlPort.shutdownSignal;
			}
		}
	}
}

package 'VehiclePowerManagement' {
    attribute def StartSignal;
    attribute def OnSignal;
    attribute def OffSignal;
    attribute def PowerState;

    port def ControlSignalPort {
        out startSignal: StartSignal;
        out onSignal: OnSignal;
        out offSignal: OffSignal;
    }

    port def PowerStatePort {
        out powerState: PowerState;
    }

    part context {
        part user {
            port controlSignalPort: ControlSignalPort;
            perform managePower.sendControlSignals {
                out startSignal = controlSignalPort.startSignal;
                out onSignal = controlSignalPort.onSignal;
                out offSignal = controlSignalPort.offSignal;
            }
        }

        interface userToVehicle connect user.controlSignalPort to vehicle.controlSignalPort {
            ref flow references managePower.startSignalFlow
                from source.startSignal to target.startSignal;
            ref flow references managePower.onSignalFlow
                from source.onSignal to target.onSignal;
            ref flow references managePower.offSignalFlow
                from source.offSignal to target.offSignal;
        }

        part vehicle {
            port controlSignalPort: ~ControlSignalPort;
            port powerStatePort: PowerStatePort;

            perform managePower.controlPowerState {
                in startSignal = controlSignalPort.startSignal;
                in onSignal = controlSignalPort.onSignal;
                in offSignal = controlSignalPort.offSignal;
                out powerState = powerStatePort.powerState;
            }
        }
    }

    action managePower {
        action sendControlSignals {
            out startSignal: StartSignal;
            out onSignal: OnSignal;
            out offSignal: OffSignal;
        }

        succession flow startSignalFlow from sendControlSignals.startSignal to controlPowerState.startSignal;
        succession flow onSignalFlow from sendControlSignals.onSignal to controlPowerState.onSignal;
        succession flow offSignalFlow from sendControlSignals.offSignal to controlPowerState.offSignal;

        action controlPowerState {
            in startSignal: StartSignal;
            in onSignal: OnSignal;
            in offSignal: OffSignal;
            out powerState: PowerState;
        }
    }
}

package 'VehicleHealthMonitoring' {
