		out warning: WarningSignal;
	}
	port def StatusPort {
		out status: HealthStatus;
	}
	port def MaintenancePort {
		in maintenanceComplete: MaintenanceStatus;
		out maintenanceRequired: MaintenanceStatus;
	}

	part context {
		part vehicle {
			port temperaturePort: TemperaturePort;
			port maintenancePort: ~MaintenancePort;
			perform monitorHealth.provideTemperature {
				out temperature = temperaturePort.temperature;
			}
			perform monitorHealth.performMaintenance {
				in maintenanceRequired = maintenancePort.maintenanceRequired;
				out maintenanceComplete = maintenancePort.maintenanceComplete;
			}
		}

		interface vehicleToMonitor connect vehicle.temperaturePort to healthMonitor.temperaturePort {
			ref flow references monitorHealth.temperatureFlow
				from source.temperature to target.temperature;
		}

		interface monitorToController connect healthMonitor.warningPort to vehicleController.warningPort {
			ref flow references monitorHealth.warningFlow
				from source.warning to target.warning;
		}

		interface monitorToVehicle connect healthMonitor.maintenancePort to vehicle.maintenancePort {
			ref flow references monitorHealth.maintenanceFlow
				from source.maintenanceRequired to target.maintenanceRequired;
		}

		part healthMonitor {
			port temperaturePort: ~TemperaturePort;
			port warningPort: WarningPort;
			port statusPort: StatusPort;
			port maintenancePort: MaintenancePort;

			perform monitorHealth.checkTemperature {
				in temperature = temperaturePort.temperature;
				out warning = warningPort.warning;
				out status = statusPort.status;
			}

			perform monitorHealth.scheduleMaintenance {
				out maintenanceRequired = maintenancePort.maintenanceRequired;
				in maintenanceComplete = maintenancePort.maintenanceComplete;
			}
		}

		part vehicleController {
			port warningPort: ~WarningPort;
			perform monitorHealth.receiveWarning {
				in warning = warningPort.warning;
			}
		}
	}

	action monitorHealth {
		action provideTemperature { out temperature: Temperature; }
		succession flow temperatureFlow from provideTemperature.temperature to checkTemperature.temperature;

		action checkTemperature { 
			in temperature: Temperature; 
			out warning: WarningSignal;
			out status: HealthStatus;
		}
		succession flow warningFlow from checkTemperature.warning to receiveWarning.warning;

		action receiveWarning { in warning: WarningSignal; }

		action scheduleMaintenance {
			out maintenanceRequired: MaintenanceStatus;
			in maintenanceComplete: MaintenanceStatus;
		}
		succession flow maintenanceFlow from scheduleMaintenance.maintenanceRequired to performMaintenance.maintenanceRequired;

		action performMaintenance {
			in maintenanceRequired: MaintenanceStatus;
			out maintenanceComplete: MaintenanceStatus;
		}
	}
}

package 'TimedServer' {
    attribute def Request;
    attribute def StartCmd;
    attribute def Time;

    port def RequestPort {
        in request : Request;
        in startCmd : StartCmd;
    }

    port def TimePort {
        in currentTime : Time;
    }

    part server {
        port requestPort : RequestPort;
        port timePort : TimePort;

        exhibit state serverBehavior {
            entry; then off;

            state off {
                transition startTransition
                    first off
                    accept startCmd : StartCmd via requestPort
                    then waiting;

                transition dailyReset
                    first waiting
                    accept currentTime : Time via timePort
                    if currentTime == "11:59:00"
                    then off;

                transition dailyResetFromResponse
                    first response
                    accept currentTime : Time via timePort
                    if currentTime == "11:59:00"
                    then off;
            }

            state waiting {
                transition requestTransition
                    first waiting
                    accept request : Request via requestPort
                    then response;
            }

            state response {
                entry / {
                    doc /* Wait for 5 minutes */
                }
                transition timeoutTransition
                    first response
                    after 5 [minute]
                    then waiting;
            }
        }
    }

    part clock {
        port timePort : ~TimePort;
        perform action {
            out currentTime = timePort.currentTime;
        }
    }

    interface clockToServer connect clock.timePort to server.timePort;
}

package 'VehicleStartStopSystem' {
	attribute def StartSignal;
	attribute def StopSignal;
	attribute def PowerOnSignal;
	attribute def BrakePedalSignal;
	attribute def ControllerStartSignal;
	attribute def ParkingBrakeSignal;
	attribute def PowerSupply;

	port def StartSignalPort {
		in startSignal : StartSignal;
	}
	port def StopSignalPort {
		in stopSignal : StopSignal;
	}
	port def PowerOnSignalPort {
		in powerOnSignal : PowerOnSignal;
	}
	port def BrakePedalPort {
		in brakePedalSignal : BrakePedalSignal;
	}
	port def ControllerStartPort {
		out controllerStartSignal : ControllerStartSignal;
	}
	port def ParkingBrakePort {
		out parkingBrakeSignal : ParkingBrakeSignal;
	}
	port def PowerSupplyPort {
		out powerSupply : PowerSupply;
	}

	part def VehicleStartStopController {
		port startSignalPort : StartSignalPort;
		port stopSignalPort : StopSignalPort;
		port powerOnSignalPort : PowerOnSignalPort;
		port brakePedalPort : BrakePedalPort;
		port controllerStartPort : ControllerStartPort;
		port parkingBrakePort : ParkingBrakePort;
		port powerSupplyPort : PowerSupplyPort;

		exhibit state vehicleStates {
			entry state off;
			state starting;
			state operating;

			transition off_to_starting
				first off
				accept startSignalPort.startSignal
				then starting;

			transition starting_to_operating
				first starting
				accept brakePedalPort.brakePedalSignal
				accept powerOnSignalPort.powerOnSignal
				do send controllerStartPort.controllerStartSignal
				then operating;

			transition operating_to_off
				first operating
				accept stopSignalPort.stopSignal
				do send parkingBrakePort.parkingBrakeSignal
				then off;
		}

		action performSelfCheck;
		action supplyPower {
			out powerSupply = powerSupplyPort.powerSupply;
		}

		state operating {
			entry performSelfCheck;
			do supplyPower;
		}
	}

	part vehicleSystem {
		part controller : VehicleStartStopController;
		part externalController {
			port controllerStartPort : ~ControllerStartPort;
		}
		part parkingBrake {
			port parkingBrakePort : ~ParkingBrakePort;
		}
		part vehicle {
			port powerSupplyPort : ~PowerSupplyPort;
		}

		interface controllerInterface connect controller.controllerStartPort to externalController.controllerStartPort;
		interface parkingBrakeInterface connect controller.parkingBrakePort to parkingBrake.parkingBrakePort;
		interface powerInterface connect controller.powerSupplyPort to vehicle.powerSupplyPort;
	}
}

package 'VehicleStartStopSystem' {
