	part context {
		part driver {
			port startSignalPort : StartSignalPort;
			port brakePedalPort : BrakePedalPort;
			port vehicleOnSignalPort : VehicleOnSignalPort;
			port shutdownSignalPort : ShutdownSignalPort;

			perform vehicleOperation.initiateStart {
				out startSignal = startSignalPort.startSignal;
			}
			perform vehicleOperation.pressBrake {
				out brakePedalSignal = brakePedalPort.brakePedalSignal;
			}
			perform vehicleOperation.sendVehicleOn {
				out vehicleOnSignal = vehicleOnSignalPort.vehicleOnSignal;
			}
			perform vehicleOperation.initiateShutdown {
				out shutdownSignal = shutdownSignalPort.shutdownSignal;
			}
		}

		interface driverToSystem connect driver.startSignalPort to startStopSystem.startSignalPort {
			ref flow references vehicleOperation.startSignalFlow
				from source.startSignal to target.startSignal;
		}
		interface driverBrakeToSystem connect driver.brakePedalPort to startStopSystem.brakePedalPort {
			ref flow references vehicleOperation.brakeSignalFlow
				from source.brakePedalSignal to target.brakePedalSignal;
		}
		interface driverVehicleOnToSystem connect driver.vehicleOnSignalPort to startStopSystem.vehicleOnSignalPort {
			ref flow references vehicleOperation.vehicleOnSignalFlow
				from source.vehicleOnSignal to target.vehicleOnSignal;
		}
		interface driverShutdownToSystem connect driver.shutdownSignalPort to startStopSystem.shutdownSignalPort {
			ref flow references vehicleOperation.shutdownSignalFlow
				from source.shutdownSignal to target.shutdownSignal;
		}

		part startStopSystem {
			port startSignalPort : ~StartSignalPort;
			port brakePedalPort : ~BrakePedalPort;
			port vehicleOnSignalPort : ~VehicleOnSignalPort;
			port shutdownSignalPort : ~ShutdownSignalPort;
			port startControlPort : StartControlPort;
			port parkingBrakePort : ParkingBrakePort;
			port powerSupplyPort : PowerSupplyPort;
			port selfCheckPort : SelfCheckPort;

			perform vehicleOperation.processStartup {
				in startSignal = startSignalPort.startSignal;
				in brakePedalSignal = brakePedalPort.brakePedalSignal;
				in vehicleOnSignal = vehicleOnSignalPort.vehicleOnSignal;
				out startControlSignal = startControlPort.startControlSignal;
			}
			perform vehicleOperation.performSelfCheck {
				out selfCheckStatus = selfCheckPort.selfCheckStatus;
			}
			perform vehicleOperation.supplyPower {
				out powerSupply = powerSupplyPort.powerSupply;
			}
			perform vehicleOperation.processShutdown {
				in shutdownSignal = shutdownSignalPort.shutdownSignal;
				out parkingBrakeSignal = parkingBrakePort.parkingBrakeSignal;
			}
		}

		interface systemToController connect startStopSystem.startControlPort to vehicleController.startControlPort {
			ref flow references vehicleOperation.startControlFlow
				from source.startControlSignal to target.startControlSignal;
		}
		interface systemToParkingBrake connect startStopSystem.parkingBrakePort to parkingBrake.parkingBrakePort {
			ref flow references vehicleOperation.parkingBrakeFlow
				from source.parkingBrakeSignal to target.parkingBrakeSignal;
		}

		part vehicleController {
			port startControlPort : ~StartControlPort;
			perform vehicleOperation.startVehicle {
				in startControlSignal = startControlPort.startControlSignal;
			}
		}

		part parkingBrake {
			port parkingBrakePort : ~ParkingBrakePort;
			perform vehicleOperation.applyParkingBrake {
				in parkingBrakeSignal = parkingBrakePort.parkingBrakeSignal;
			}
		}
	}

	action vehicleOperation {
		action initiateStart { out startSignal : StartSignal; }
		succession flow startSignalFlow from initiateStart.startSignal to processStartup.startSignal;

		action pressBrake { out brakePedalSignal : BrakePedalSignal; }
		succession flow brakeSignalFlow from pressBrake.brakePedalSignal to processStartup.brakePedalSignal;

		action sendVehicleOn { out vehicleOnSignal : VehicleOnSignal; }
		succession flow vehicleOnSignalFlow from sendVehicleOn.vehicleOnSignal to processStartup.vehicleOnSignal;

		action processStartup { 
			in startSignal : StartSignal; 
			in brakePedalSignal : BrakePedalSignal;
			in vehicleOnSignal : VehicleOnSignal;
			out startControlSignal : StartControlSignal; 
		}
		succession flow startControlFlow from processStartup.startControlSignal to startVehicle.startControlSignal;

		action startVehicle { in startControlSignal : StartControlSignal; }
		succession from startVehicle to performSelfCheck;
		succession from startVehicle to supplyPower;

		action performSelfCheck { out selfCheckStatus : SelfCheckStatus; }
		action supplyPower { out powerSupply : PowerSupply; }

		action initiateShutdown { out shutdownSignal : ShutdownSignal; }
		succession flow shutdownSignalFlow from initiateShutdown.shutdownSignal to processShutdown.shutdownSignal;

		action processShutdown { 
			in shutdownSignal : ShutdownSignal; 
			out parkingBrakeSignal : ParkingBrakeSignal;
		}
		succession flow parkingBrakeFlow from processShutdown.parkingBrakeSignal to applyParkingBrake.parkingBrakeSignal;

		action applyParkingBrake { in parkingBrakeSignal : ParkingBrakeSignal; }
	}
}

