			}
		}

		part speedSensor {
			port actualSpeedPort : ActualSpeedPort;
			perform cruiseOperation.readSpeed {
				out actualSpeed = actualSpeedPort.actualSpeed;
			}
		}

		interface speedSensorToCruiseController connect speedSensor.actualSpeedPort to cruiseController.actualSpeedPort {
			ref flow references cruiseOperation.actualSpeedFlow
				from source.actualSpeed to target.actualSpeed;
		}

		interface cruiseControllerToEngineController connect cruiseController.fuelControlCmdPort to engineController.fuelControlCmdPort {
			ref flow references cruiseOperation.fuelControlCmdFlow
				from source.fuelControlCmd to target.fuelControlCmd;
		}

		part engineController {
			port fuelControlCmdPort : ~FuelControlCmdPort;
			port engineFuelControlCmdPort : FuelControlCmdPort;
			perform cruiseOperation.processEngineControl {
				in fuelControlCmd = fuelControlCmdPort.fuelControlCmd;
				out fuelControlCmd = engineFuelControlCmdPort.fuelControlCmd;
			}
		}

		interface engineControllerToEngine connect engineController.engineFuelControlCmdPort to engine.fuelControlCmdPort {
			ref flow references cruiseOperation.engineFuelControlCmdFlow
				from source.fuelControlCmd to target.fuelControlCmd;
		}

		part engine {
			port fuelControlCmdPort : ~FuelControlCmdPort;
			perform cruiseOperation.controlFuelFlow {
				in fuelControlCmd = fuelControlCmdPort.fuelControlCmd;
			}
		}
	}

	action cruiseOperation {
		action sendSetSpeedCmd { out setSpeedCmd : SetSpeedCmd; }
		action readSpeed { out actualSpeed : ActualSpeed; }
		succession flow setSpeedCmdFlow from sendSetSpeedCmd.setSpeedCmd to processCruiseControl.setSpeedCmd;
		succession flow actualSpeedFlow from readSpeed.actualSpeed to processCruiseControl.actualSpeed;
		action processCruiseControl { in setSpeedCmd; in actualSpeed; out fuelControlCmd : FuelControlCmd; }
		succession flow fuelControlCmdFlow from processCruiseControl.fuelControlCmd to processEngineControl.fuelControlCmd;
		action processEngineControl { in fuelControlCmd; out fuelControlCmd : FuelControlCmd; }
		succession flow engineFuelControlCmdFlow from processEngineControl.fuelControlCmd to controlFuelFlow.fuelControlCmd;
		action controlFuelFlow { in fuelControlCmd : FuelControlCmd; }
	}
}

