package 'VehicleMassManagement' {
    attribute def Mass;
    attribute def ComponentID;

    part def MassComponent {
        attribute id: ComponentID;
        attribute ownMass: Mass;
        attribute totalMass: Mass;
    }

    part def SimpleComponent :> MassComponent {
        calc totalMass = ownMass;
    }

    part def CompositeComponent :> MassComponent {
        part subComponents: MassComponent[0..*];
        calc totalMass = ownMass + sum(subComponents.totalMass);
    }

    part def Engine :> SimpleComponent;
    part def Transmission :> SimpleComponent;
    part def Body :> SimpleComponent;

    part def Vehicle :> CompositeComponent {
        part engine: Engine;
        part transmission: Transmission;
        part body: Body;
        part otherComponents: MassComponent[0..*];

        bind subComponents = (engine, transmission, body, otherComponents);
    }

    part vehicleSystem {
        part vehicle: Vehicle;

        action calculateTotalMass {
            out totalVehicleMass: Mass = vehicle.totalMass;
        }

        action queryComponentMass {
            in componentId: ComponentID;
            out componentMass: Mass;
        }
    }
}

package 'VehicleQualityManagement' {
    attribute def Mass;
    attribute def SerialNumber;
    attribute def VIN;
    attribute def Threshold;

    part def Component {
        attribute serialNumber: SerialNumber;
        attribute mass: Mass;
        part components: Component[0..*];

        calc totalMass: Mass {
            mass + components.totalMass->sum()
        }

        calc filteredTotalMass: Mass (threshold: Threshold) {
            mass + components->select(c | c.mass >= threshold).totalMass->sum()
        }
    }

    part def Engine :> Component;
    part def Transmission :> Component;

    part def Vehicle :> Component {
        attribute vin: VIN;
        part engine: Engine[0..1];
        part transmission: Transmission[0..1];
    }

    part vehicleQualitySystem {
        part vehicle: Vehicle;

        attribute massThreshold: Threshold;

        calc vehicleTotalMass: Mass = vehicle.totalMass;
        calc vehicleFilteredMass: Mass = vehicle.filteredTotalMass(massThreshold);
    }
}

package 'MassAggregationSystem' {
    attribute def Mass;

    attribute def SimpleMass :> Mass;
    attribute def TotalMass :> Mass;

    abstract part def MassObject {
        attribute simpleMass : SimpleMass;
        attribute totalMass : TotalMass;
    }

    part def SimpleThing :> MassObject {
        assert constraint { totalMass == simpleMass }
    }

    part def CompositeThing :> MassObject {
        part subcomponents : MassObject[0..*];

        assert constraint {
            totalMass == simpleMass + sum(subcomponents.totalMass)
        }
    }

    calc def sum(masses : TotalMass[0..*]) : TotalMass {
        masses->reduce '+' ?? 0.0
    }
}

package 'MassManagementSystem' {
    attribute def Mass;
    attribute def MinMassThreshold;

    port def MassQueryPort {
        in minThreshold : MinMassThreshold;
        out totalMass : Mass;
    }

    port def SubcomponentMassPort {
        out mass : Mass;
    }

    part def MassObject {
        attribute basicMass : Mass;
        part subcomponents : MassObject[0..*];

        port massQueryPort : ~MassQueryPort;
        port subcomponentMassPort : SubcomponentMassPort[0..*];

        perform calculateMass {
            in minThreshold = massQueryPort.minThreshold;
            out totalMass = massQueryPort.totalMass;

            action computeOwnMass {
                out ownMass : Mass = basicMass;
            }

            action aggregateSubcomponentMass {
                in threshold : MinMassThreshold = minThreshold;
                in subMasses : Mass[0..*];
                out aggregatedMass : Mass;
            }

            action sumTotalMass {
                in ownMass : Mass;
                in subcomponentTotal : Mass;
                out total : Mass = ownMass + subcomponentTotal;
            }

            flow from computeOwnMass.ownMass to sumTotalMass.ownMass;
            flow from aggregateSubcomponentMass.aggregatedMass to sumTotalMass.subcomponentTotal;
            flow from sumTotalMass.total to totalMass;
        }

        interface subcomponentConnection : MassObject::subcomponents {
            connect massQueryPort to subcomponents.massQueryPort;
            connect subcomponents.subcomponentMassPort to subcomponentMassPort;
        }
    }

    part massManagementContext {
        part rootObject : MassObject;

        perform massAggregation {
            action setThreshold {
                out threshold : MinMassThreshold;
            }

            action queryTotalMass {
                in threshold : MinMassThreshold;
                out totalMass : Mass;
            }

            flow from setThreshold.threshold to queryTotalMass.threshold;
            bind queryTotalMass.threshold = rootObject.massQueryPort.minThreshold;
            bind queryTotalMass.totalMass = rootObject.massQueryPort.totalMass;
        }
    }
}

package 'VehicleDynamics' {
    attribute def Power;
    attribute def Acceleration;
    attribute def Speed;
    attribute def Position;
    attribute def Mass;
    attribute def Time;
    attribute def WheelPower;
    attribute def AeroDragCoeff;
    attribute def FrictionCoeff;
    attribute def Displacement;

    port def VehicleInputPort {
        in wheelPower: WheelPower;
        in aeroDragCoeff: AeroDragCoeff;
        in frictionCoeff: FrictionCoeff;
        in mass: Mass;
        in speed: Speed;
        in time: Time;
    }

    port def DynamicsOutputPort {
        out power: Power;
        out acceleration: Acceleration;
        out speed: Speed;
        out position: Position;
    }

    part context {
        part vehicleSystem {
            port inputPort: VehicleInputPort;
            port outputPort: DynamicsOutputPort;

            perform calculateDynamics.computePower {
                in wheelPower = inputPort.wheelPower;
                in aeroDragCoeff = inputPort.aeroDragCoeff;
                in frictionCoeff = inputPort.frictionCoeff;
                in mass = inputPort.mass;
                in speed = inputPort.speed;
                out power = outputPort.power;
            }

            perform calculateDynamics.computeAcceleration {
                in power = outputPort.power;
                in mass = inputPort.mass;
                in speed = inputPort.speed;
                out acceleration = outputPort.acceleration;
            }

            perform calculateDynamics.computeSpeed {
                in acceleration = outputPort.acceleration;
                in initialSpeed = inputPort.speed;
                in time = inputPort.time;
                out speed = outputPort.speed;
            }

            perform calculateDynamics.computePosition {
                in speed = outputPort.speed;
                in time = inputPort.time;
                out position = outputPort.position;
            }
        }
    }

    action calculateDynamics {
        action computePower {
            in wheelPower: WheelPower;
            in aeroDragCoeff: AeroDragCoeff;
            in frictionCoeff: FrictionCoeff;
            in mass: Mass;
            in speed: Speed;
            out power: Power;
        }

        succession flow powerFlow from computePower.power to computeAcceleration.power;

        action computeAcceleration {
            in power: Power;
            in mass: Mass;
            in speed: Speed;
            out acceleration: Acceleration;
        }

        succession flow accelerationFlow from computeAcceleration.acceleration to computeSpeed.acceleration;

        action computeSpeed {
            in acceleration: Acceleration;
            in initialSpeed: Speed;
            in time: Time;
            out speed: Speed;
        }

        succession flow speedFlow from computeSpeed.speed to computePosition.speed;

        action computePosition {
            in speed: Speed;
            in time: Time;
            out position: Position;
        }
    }
}

package 'VehicleDynamicsSimulation' {
    attribute def Power;
    attribute def Mass;
    attribute def Speed;
    attribute def Position;
    attribute def Acceleration;
    attribute def TimeStep;
    attribute def DragCoefficient;
    attribute def RollingResistanceCoefficient;

    port def VehicleParametersPort {
        in wheelPower: Power;
        in vehicleMass: Mass;
        in dragCoeff: DragCoefficient;
        in rollingCoeff: RollingResistanceCoefficient;
    }

    port def SimulationParametersPort {
        in initialSpeed: Speed;
        in initialPosition: Position;
        in timeStep: TimeStep;
    }

    port def DynamicsOutputPort {
        out acceleration: Acceleration;
        out speed: Speed;
        out position: Position;
    }

    part context {
        part user {
            port vehicleParamsPort: VehicleParametersPort;
            port simParamsPort: SimulationParametersPort;

            perform computeDynamics.provideVehicleParameters {
                out wheelPower = vehicleParamsPort.wheelPower;
                out vehicleMass = vehicleParamsPort.vehicleMass;
                out dragCoeff = vehicleParamsPort.dragCoeff;
                out rollingCoeff = vehicleParamsPort.rollingCoeff;
            }

            perform computeDynamics.provideSimulationParameters {
                out initialSpeed = simParamsPort.initialSpeed;
                out initialPosition = simParamsPort.initialPosition;
                out timeStep = simParamsPort.timeStep;
            }
        }

        interface userToSimulator connect user.vehicleParamsPort to simulator.vehicleParamsPort {
            ref flow references computeDynamics.vehicleParamsFlow
                from source.wheelPower to target.wheelPower;
            ref flow references computeDynamics.vehicleParamsFlow
                from source.vehicleMass to target.vehicleMass;
            ref flow references computeDynamics.vehicleParamsFlow
                from source.dragCoeff to target.dragCoeff;
            ref flow references computeDynamics.vehicleParamsFlow
                from source.rollingCoeff to target.rollingCoeff;
        }

        interface userToSimulator2 connect user.simParamsPort to simulator.simParamsPort {
            ref flow references computeDynamics.simParamsFlow
                from source.initialSpeed to target.initialSpeed;
            ref flow references computeDynamics.simParamsFlow
                from source.initialPosition to target.initialPosition;
            ref flow references computeDynamics.simParamsFlow
                from source.timeStep to target.timeStep;
        }

        part simulator {
            port vehicleParamsPort: ~VehicleParametersPort;
            port simParamsPort: ~SimulationParametersPort;
            port dynamicsOutputPort: DynamicsOutputPort;

            perform computeDynamics.calculateAvailablePower {
                in wheelPower = vehicleParamsPort.wheelPower;
                in dragCoeff = vehicleParamsPort.dragCoeff;
                in rollingCoeff = vehicleParamsPort.rollingCoeff;
                in currentSpeed = simParamsPort.initialSpeed;
                out availablePower: Power;
            }

            perform computeDynamics.calculateAcceleration {
                in availablePower;
                in vehicleMass = vehicleParamsPort.vehicleMass;
                out acceleration = dynamicsOutputPort.acceleration;
            }

            perform computeDynamics.updateSpeedAndPosition {
                in acceleration;
                in initialSpeed = simParamsPort.initialSpeed;
                in initialPosition = simParamsPort.initialPosition;
                in timeStep = simParamsPort.timeStep;
                out speed = dynamicsOutputPort.speed;
                out position = dynamicsOutputPort.position;
            }
        }
    }

    action computeDynamics {
        action provideVehicleParameters {
            out wheelPower: Power;
            out vehicleMass: Mass;
            out dragCoeff: DragCoefficient;
            out rollingCoeff: RollingResistanceCoefficient;
        }

        action provideSimulationParameters {
            out initialSpeed: Speed;
            out initialPosition: Position;
            out timeStep: TimeStep;
        }

        succession flow vehicleParamsFlow from provideVehicleParameters to calculateAvailablePower;
        succession flow simParamsFlow from provideSimulationParameters to calculateAvailablePower;

        action calculateAvailablePower {
            in wheelPower: Power;
            in dragCoeff: DragCoefficient;
            in rollingCoeff: RollingResistanceCoefficient;
            in currentSpeed: Speed;
            out availablePower: Power;
        }

        succession flow powerFlow from calculateAvailablePower.availablePower to calculateAcceleration.availablePower;

        action calculateAcceleration {
            in availablePower: Power;
            in vehicleMass: Mass;
            out acceleration: Acceleration;
        }

        succession flow accelerationFlow from calculateAcceleration.acceleration to updateSpeedAndPosition.acceleration;

        action updateSpeedAndPosition {
            in acceleration: Acceleration;
            in initialSpeed: Speed;
            in initialPosition: Position;
            in timeStep: TimeStep;
            out speed: Speed;
            out position: Position;
        }
    }
}

