        in vehicleMass: Mass;
        in drivePower: Power;
        in timeInterval: TimeInterval;
    }

    port def EnvironmentParameterPort {
        in dragCoefficient: DragCoefficient;
        in frictionCoefficient: FrictionCoefficient;
    }

