                from source.initialPosition to target.initialPosition;
            ref flow references calculateDynamics.inputFlow
                from source.initialVelocity to target.initialVelocity;
            ref flow references calculateDynamics.inputFlow
                from source.vehicleMass to target.vehicleMass;
            ref flow references calculateDynamics.inputFlow
                from source.drivePower to target.drivePower;
            ref flow references calculateDynamics.inputFlow
                from source.timeInterval to target.timeInterval;
        }

        part dynamicsSystem {
            port vehicleInputPort: ~VehicleInputPort;
            port environmentPort: ~EnvironmentParameterPort;
