		interface engineToController connect engine.massPort to massController.engineMassPort;
		interface gearboxToController connect gearbox.massPort to massController.gearboxMassPort;

		constraint massLimit {
			doc /* Total mass must not exceed maximum permitted mass */
			totalMass <= maxPermittedMass
		}
