package 'VehicleMassManagement' {
    attribute def MassValue :> ISQ::mass;
    attribute def ComplianceStatus;

    port def MassDataPort {
        in massValue : MassValue;
    }

    port def CompliancePort {
        out status : ComplianceStatus;
    }

    part def Vehicle {
        attribute totalMass : MassValue;
        attribute massLimit : MassValue = 2500 [kg];

        part chassis {
            attribute mass : MassValue;
            port massPort : MassDataPort;
        }

        part engine {
            attribute mass : MassValue;
            port massPort : MassDataPort;
        }

        part transmission {
            attribute mass : MassValue;
            port massPort : MassDataPort;
        }

        port compliancePort : CompliancePort;

        calc totalMass : MassValue = chassis.mass + engine.mass + transmission.mass;

        constraint massCompliance {
            totalMass <= massLimit
        }
    }

    part vehicleMassSystem {
        part vehicle : Vehicle;

        perform verifyMassCompliance {
            in chassisMass = vehicle.chassis.massPort.massValue;
            in engineMass = vehicle.engine.massPort.massValue;
            in transmissionMass = vehicle.transmission.massPort.massValue;

            calc totalVehicleMass : MassValue = chassisMass + engineMass + transmissionMass;
            calc isCompliant : ComplianceStatus = (totalVehicleMass <= 2500 [kg]);

            out complianceStatus = vehicle.compliancePort.status = isCompliant;
        }
    }
}

