			port chassisWeightPort : ~WeightDataPort;
			port engineWeightPort : ~WeightDataPort;
			port transmissionWeightPort : ~WeightDataPort;
			port warningPort : WarningPort;

			perform checkWeight.calculateTotalWeight {
				in chassisWeight = chassisWeightPort.weight;
				in engineWeight = engineWeightPort.weight;
				in transmissionWeight = transmissionWeightPort.weight;
				out totalWeight;
