			in chassisWeight : Weight;
			in engineWeight : Weight;
			in transmissionWeight : Weight;
			out totalWeight : Weight;
		}

		succession flow totalWeightFlow from calculateTotalWeight.totalWeight to validateWeight.totalWeight;

