package 'Vehicle System' {
    import ISQ::*;
    import SI::*;

    attribute def MassValue :> ISQ::mass;
    attribute def ForceValue :> ISQ::force;
    attribute def VelocityValue :> ISQ::speed;
    attribute def TimeValue :> ISQ::time;

    part def Chassis {
        attribute mass : MassValue;
    }

    part def Engine {
        attribute mass : MassValue;
    }

    part def Transmission {
        attribute mass : MassValue;
    }

    part def Vehicle {
        part chassis : Chassis;
        part engine : Engine;
        part transmission : Transmission;

        attribute totalMass : MassValue = chassis.mass + engine.mass + transmission.mass;

        constraint totalMassConstraint {
            totalMass <= 2500[kg]
        }

        attribute initialVelocity : VelocityValue;
        attribute finalVelocity : VelocityValue;
        attribute appliedForce : ForceValue;
        attribute timeDuration : TimeValue;

        constraint massPositive {
            totalMass > 0[kg]
        }

        constraint dynamicConstraint {
            (finalVelocity - initialVelocity) == (appliedForce * timeDuration) / totalMass
        }
    }
}

package 'VehicleMaintenanceSystem' {
    import ScalarValues::*;

    attribute def VehicleID;
    attribute def Temperature;
    attribute def MaintenanceStatus;
    attribute def HealthStatus;

    attribute nextMaintenanceTime : Real;
    attribute maintenanceInterval : Real;
    attribute maxAllowedTemperature : Temperature;
    attribute currentTime : Real;
    attribute actualMaintenanceStartTime : Real;
    attribute maintenanceDuration : Real;

    part def Vehicle {
        attribute vehicleID : VehicleID;
        attribute nextMaintenance : Real;
        attribute interval : Real;
        attribute maxTemp : Temperature;
        attribute status : MaintenanceStatus;
        attribute health : HealthStatus;

        state healthyState;
        state underMaintenanceState;

        transition healthyToMaintenance
            first healthyState
            then underMaintenanceState
            when currentTime >= nextMaintenance;

        transition maintenanceToHealthy
            first underMaintenanceState
            then healthyState
            when maintenanceDuration <= 48 * 3600;
    }

    part def MaintenanceScheduler {
        perform action scheduleNextMaintenance {
            in vehicle : Vehicle;
            in interval : Real;
            out nextTime : Real = currentTime + interval;
        }

        perform action checkMaintenanceTime {
            in vehicle : Vehicle;
            in currentTime : Real;
            out needsMaintenance : Boolean = currentTime >= vehicle.nextMaintenance;
        }
    }

    part vehicleMaintenanceSystem {
        part vehicle : Vehicle;
        part scheduler : MaintenanceScheduler;

        constraint maintenanceStartConstraint {
            actualMaintenanceStartTime > vehicle.nextMaintenance
        }

        constraint maintenanceInitiationConstraint {
            actualMaintenanceStartTime <= currentTime + 2
        }

        constraint maintenanceDurationConstraint {
            maintenanceDuration <= 48 * 3600
        }

        action performMaintenance {
            first start;
            then done;

            bind actualMaintenanceStartTime = start::startTime;
            bind maintenanceDuration = done::endTime - start::startTime;

            action updateNextMaintenance {
                vehicle.nextMaintenance := vehicle.nextMaintenance + vehicle.interval;
                vehicle.health := "normal";
                vehicle.status := "operational";
            }
        }
    }
}

