package 'Vehicle System' {
    attribute def Mass :> ISQ::mass;
    attribute def Torque :> ISQ::torque;
    attribute def Speed :> ISQ::speed;

    port def TorquePort {
        out torque: Torque;
    }

    port def SpeedPort {
        in speed: Speed;
    }

    requirement def MassRequirement {
        doc /* The vehicle mass must not exceed specified limits */
        attribute emptyMass: Mass;
        attribute fueledMass: Mass;
        require constraint { emptyMass <= 1500[kg] }
        require constraint { fueledMass <= 2000[kg] }
    }

    requirement def TorqueTransmissionRequirement {
        doc /* Engine must effectively transmit torque through clutch interface */
    }

    requirement def TorqueSpeedRequirement {
        doc /* Engine torque must vary with speed per performance table */
    }

    part vehicle {
        attribute emptyMass: Mass;
        attribute fueledMass: Mass;

        satisfy massReq: MassRequirement by vehicle {
            attribute redefines emptyMass = vehicle.emptyMass;
            attribute redefines fueledMass = vehicle.fueledMass;
        }

        part engine {
            port torquePort: TorquePort;
            port speedPort: SpeedPort;

            perform generateTorque {
                in speed = speedPort.speed;
                out torque = torquePort.torque;
            }

            satisfy torqueSpeedReq: TorqueSpeedRequirement by engine;
        }

        part transmission {
            port torquePort: ~TorquePort;
        }

        interface clutchInterface connect engine.torquePort to transmission.torquePort {
            satisfy torqueTransReq: TorqueTransmissionRequirement by clutchInterface;
        }
    }
}

package 'VehicleSystem' {
    attribute def Mass;
    attribute def Torque;
    attribute def Speed;

    port def TorquePort {
        out torque: Torque;
    }

    port def SpeedPort {
        in speed: Speed;
    }

    part vehicle {
        attribute curbMass: Mass;
        attribute fuelMass: Mass;
        attribute totalMass: Mass = curbMass + fuelMass;

        constraint massLimitFull {
            doc /* When fuel tank is full, total mass must not exceed 2000 kg */
            totalMass <= 2000 [kg]
        }

        constraint massLimitEmpty {
            doc /* When fuel tank is empty, total mass must not exceed 1500 kg */
            curbMass <= 1500 [kg]
        }

        part engine {
            port speedPort: SpeedPort;
            port torquePort: TorquePort;

            perform powerTransmission.generateTorque {
                in speed = speedPort.speed;
                out torque = torquePort.torque;
            }
        }

        part clutch {
            port torqueInPort: ~TorquePort;
            port torqueOutPort: TorquePort;

            perform powerTransmission.transmitTorque {
                in torqueIn = torqueInPort.torque;
                out torqueOut = torqueOutPort.torque;
            }
        }

        part gearbox {
            port torquePort: ~TorquePort;

            perform powerTransmission.receiveTorque {
                in torque = torquePort.torque;
            }
        }

        interface engineToClutch connect engine.torquePort to clutch.torqueInPort {
            ref flow references powerTransmission.engineTorqueFlow
                from source.torque to target.torque;
        }

        interface clutchToGearbox connect clutch.torqueOutPort to gearbox.torquePort {
            ref flow references powerTransmission.clutchTorqueFlow
                from source.torque to target.torque;
        }
    }

    action powerTransmission {
        action generateTorque { in speed: Speed; out torque: Torque; }
        succession flow engineTorqueFlow from generateTorque.torque to transmitTorque.torqueIn;
        action transmitTorque { in torqueIn: Torque; out torqueOut: Torque; }
        succession flow clutchTorqueFlow from transmitTorque.torqueOut to receiveTorque.torque;
        action receiveTorque { in torque: Torque; }
    }
}

package 'VehicleDynamicPerformanceAnalysis' {
    attribute def Mass;
    attribute def Length;
    attribute def Efficiency;
    attribute def FuelEconomy;
    attribute def Time;
    attribute def Position;
    attribute def Speed;
    attribute def Acceleration;
    attribute def Power;
    attribute def FuelConsumption;
    attribute def ComplianceStatus;

    attribute def VehicleConfiguration {
        attribute curbWeight: Mass;
        attribute payload: Mass;
        attribute wheelDiameter: Length;
        attribute transmissionEfficiency: Efficiency;
        attribute urbanFuelEconomy: FuelEconomy;
        attribute highwayFuelEconomy: FuelEconomy;
    }

    attribute def DriveCycle {
        attribute timePoints: Time[*];
        attribute positions: Position[*];
        attribute speeds: Speed[*];
    }

    attribute def EnergyTarget {
        attribute targetFuelEconomy: FuelEconomy;
    }

    attribute def PerformanceResults {
        attribute power: Power[*];
        attribute acceleration: Acceleration[*];
        attribute speed: Speed[*];
        attribute position: Position[*];
        attribute overallFuelEconomy: FuelEconomy;
        attribute meetsTarget: ComplianceStatus;
    }

    port def ConfigurationPort {
        in vehicleConfig: VehicleConfiguration;
        in driveCycle: DriveCycle;
        in energyTarget: EnergyTarget;
    }

    port def ResultsPort {
        out performanceResults: PerformanceResults;
    }

    part context {
        part user {
            port configPort: ConfigurationPort;
            perform analyzePerformance.configureAnalysis {
                out vehicleConfig = configPort.vehicleConfig;
                out driveCycle = configPort.driveCycle;
                out energyTarget = configPort.energyTarget;
            }
        }

        interface userToAnalysisSystem connect user.configPort to analysisSystem.configPort {
            ref flow references analyzePerformance.configFlow
                from source.vehicleConfig to target.vehicleConfig;
            ref flow references analyzePerformance.cycleFlow
                from source.driveCycle to target.driveCycle;
            ref flow references analyzePerformance.targetFlow
                from source.energyTarget to target.energyTarget;
        }

        part analysisSystem {
            port configPort: ~ConfigurationPort;
            port resultsPort: ResultsPort;

            perform analyzePerformance.calculateDynamics {
                in vehicleConfig = configPort.vehicleConfig;
                in driveCycle = configPort.driveCycle;
                out power: Power[*];
                out acceleration: Acceleration[*];
            }

            perform analyzePerformance.calculateFuelEconomy {
                in vehicleConfig = configPort.vehicleConfig;
                in driveCycle = configPort.driveCycle;
                in power: Power[*];
                out fuelEconomy: FuelEconomy;
            }

            perform analyzePerformance.verifyCompliance {
                in fuelEconomy: FuelEconomy;
                in energyTarget = configPort.energyTarget;
                out results = resultsPort.performanceResults;
            }
        }
    }

    action analyzePerformance {
        action configureAnalysis {
            out vehicleConfig: VehicleConfiguration;
            out driveCycle: DriveCycle;
            out energyTarget: EnergyTarget;
        }

        succession flow configFlow from configureAnalysis.vehicleConfig to calculateDynamics.vehicleConfig;
        succession flow cycleFlow from configureAnalysis.driveCycle to calculateDynamics.driveCycle;
        succession flow targetFlow from configureAnalysis.energyTarget to verifyCompliance.energyTarget;

        action calculateDynamics {
            in vehicleConfig: VehicleConfiguration;
            in driveCycle: DriveCycle;
            out power: Power[*];
            out acceleration: Acceleration[*];
        }

        succession flow powerFlow from calculateDynamics.power to calculateFuelEconomy.power;

        action calculateFuelEconomy {
            in vehicleConfig: VehicleConfiguration;
            in driveCycle: DriveCycle;
            in power: Power[*];
            out fuelEconomy: FuelEconomy;
        }

        succession flow economyFlow from calculateFuelEconomy.fuelEconomy to verifyCompliance.fuelEconomy;

        action verifyCompliance {
            in fuelEconomy: FuelEconomy;
            in energyTarget: EnergyTarget;
            out results: PerformanceResults;
        }
    }
}

package 'VehicleFuelEconomySimulation' {
    attribute def Speed;
    attribute def Distance;
    attribute def Time;
    attribute def Power;
    attribute def FuelConsumption;
    attribute def FuelEconomy;
    attribute def Mass;
    attribute def WheelDiameter;
    attribute def TransmissionEfficiency;
    attribute def DrivingScenario;
    attribute def Trajectory;
    attribute def Acceleration;

    port def VehicleParametersPort {
        in curbWeight: Mass;
        in load: Mass;
        in wheelDiameter: WheelDiameter;
        in transmissionEfficiency: TransmissionEfficiency;
    }

    port def DrivingConditionsPort {
        in scenario: DrivingScenario;
        in trajectory: Trajectory;
        in speedProfile: Speed[*];
    }

    port def SimulationResultsPort {
        out fuelEconomy: FuelEconomy;
        out fuelConsumption: FuelConsumption;
        out meetsRequirement: Boolean;
    }

    part context {
        part user {
            port vehicleParamsPort: VehicleParametersPort;
            port drivingConditionsPort: DrivingConditionsPort;
            port resultsPort: ~SimulationResultsPort;

            perform fuelEconomyAnalysis.setVehicleParameters {
                out curbWeight = vehicleParamsPort.curbWeight;
                out load = vehicleParamsPort.load;
                out wheelDiameter = vehicleParamsPort.wheelDiameter;
                out transmissionEfficiency = vehicleParamsPort.transmissionEfficiency;
            }

            perform fuelEconomyAnalysis.setDrivingScenario {
                out scenario = drivingConditionsPort.scenario;
                out trajectory = drivingConditionsPort.trajectory;
                out speedProfile = drivingConditionsPort.speedProfile;
            }
        }

        interface userToSimulator connect user.vehicleParamsPort to simulator.vehicleParamsPort;
        interface userToDrivingInput connect user.drivingConditionsPort to simulator.drivingConditionsPort;
        interface simulatorToUser connect simulator.resultsPort to user.resultsPort;

        part simulator {
            port vehicleParamsPort: ~VehicleParametersPort;
            port drivingConditionsPort: ~DrivingConditionsPort;
            port resultsPort: SimulationResultsPort;

            perform fuelEconomyAnalysis.calculateWheelPower {
                in curbWeight = vehicleParamsPort.curbWeight;
                in load = vehicleParamsPort.load;
                in wheelDiameter = vehicleParamsPort.wheelDiameter;
                in speedProfile = drivingConditionsPort.speedProfile;
                in trajectory = drivingConditionsPort.trajectory;
                out wheelPower: Power;
                out acceleration: Acceleration;
            }

            perform fuelEconomyAnalysis.calculateEnginePower {
                in wheelPower: Power;
                in transmissionEfficiency = vehicleParamsPort.transmissionEfficiency;
                out enginePower: Power;
            }

            perform fuelEconomyAnalysis.calculateFuelConsumption {
                in enginePower: Power;
                in scenario = drivingConditionsPort.scenario;
                out fuelConsumption = resultsPort.fuelConsumption;
                out fuelEconomy = resultsPort.fuelEconomy;
            }

            perform fuelEconomyAnalysis.verifyRequirement {
                in fuelEconomy = resultsPort.fuelEconomy;
                out meetsRequirement = resultsPort.meetsRequirement;
            }
        }
    }

    action fuelEconomyAnalysis {
        action setVehicleParameters {
            out curbWeight: Mass;
            out load: Mass;
            out wheelDiameter: WheelDiameter;
            out transmissionEfficiency: TransmissionEfficiency;
        }

        action setDrivingScenario {
            out scenario: DrivingScenario;
            out trajectory: Trajectory;
            out speedProfile: Speed[*];
        }

        succession flow vehicleParamsFlow from setVehicleParameters to calculateWheelPower;
        succession flow drivingConditionsFlow from setDrivingScenario to calculateWheelPower;

        action calculateWheelPower {
            in curbWeight: Mass;
            in load: Mass;
            in wheelDiameter: WheelDiameter;
            in speedProfile: Speed[*];
            in trajectory: Trajectory;
            out wheelPower: Power;
            out acceleration: Acceleration;
        }

        succession flow wheelPowerFlow from calculateWheelPower.wheelPower to calculateEnginePower.wheelPower;

        action calculateEnginePower {
            in wheelPower: Power;
            in transmissionEfficiency: TransmissionEfficiency;
            out enginePower: Power;
        }

        succession flow enginePowerFlow from calculateEnginePower.enginePower to calculateFuelConsumption.enginePower;

        action calculateFuelConsumption {
            in enginePower: Power;
            in scenario: DrivingScenario;
            out fuelConsumption: FuelConsumption;
            out fuelEconomy: FuelEconomy;
        }

        succession flow fuelEconomyFlow from calculateFuelConsumption.fuelEconomy to verifyRequirement.fuelEconomy;

        action verifyRequirement {
            in fuelEconomy: FuelEconomy;
            out meetsRequirement: Boolean;
        }
    }
}

