				from source.power to target.power;
			ref flow references selectOptimalEngine.engineDataFlow2
				from source.mass to target.mass;
			ref flow references selectOptimalEngine.engineDataFlow2
				from source.efficiency to target.efficiency;
			ref flow references selectOptimalEngine.engineDataFlow2
				from source.cost to target.cost;
			ref flow references selectOptimalEngine.engineDataFlow2
				from source.engineType to target.engineType;
		}

		part analyzer {
			port engineDataPort1: ~EngineDataPort;
			port engineDataPort2: ~EngineDataPort;
			port scorePort1: ScorePort;
			port scorePort2: ScorePort;
			port optimalEnginePort: OptimalEnginePort;

			perform selectOptimalEngine.calculatePerformance {
				in power1 = engineDataPort1.power;
				in mass1 = engineDataPort1.mass;
				in efficiency1 = engineDataPort1.efficiency;
				in cost1 = engineDataPort1.cost;
				in engineType1 = engineDataPort1.engineType;
				in power2 = engineDataPort2.power;
				in mass2 = engineDataPort2.mass;
				in efficiency2 = engineDataPort2.efficiency;
				in cost2 = engineDataPort2.cost;
				in engineType2 = engineDataPort2.engineType;
				out score1 = scorePort1.score;
				out score2 = scorePort2.score;
			}

			perform selectOptimalEngine.selectBestEngine {
				in score1 = scorePort1.score;
				in score2 = scorePort2.score;
				out optimalEngine = optimalEnginePort.optimalEngine;
				out optimalScore = optimalEnginePort.optimalScore;
			}
		}
	}

	action selectOptimalEngine {
		action provideEngineData { 
			out power: Power; 
			out mass: Mass; 
			out efficiency: Efficiency; 
			out cost: Cost;
			out engineType: EngineType;
		}
		succession flow engineDataFlow1 from provideEngineData to calculatePerformance;
		succession flow engineDataFlow2 from provideEngineData to calculatePerformance;
		action calculatePerformance { 
			in power1; in mass1; in efficiency1; in cost1; in engineType1;
			in power2; in mass2; in efficiency2; in cost2; in engineType2;
			out score1: Score; 
			out score2: Score;
		}
		succession flow scoreFlow from calculatePerformance to selectBestEngine;
		action selectBestEngine { 
			in score1; in score2;
			out optimalEngine: EngineType;
			out optimalScore: Score;
		}
	}
}

package 'VehicleWeightInspection' {
