package 'VehicleMassVerification' {
	attribute def Mass;
	attribute def VerificationResult;

	port def MassDataPort {
		out massData : Mass;
	}

	port def VerificationResultPort {
		out result : VerificationResult;
	}

	part context {
		part vehicle {
			attribute actualMass : Mass;
			port massDataPort : MassDataPort;

			perform verifyMass.provideMass {
				out massData = massDataPort.massData;
			}
		}

		interface vehicleToWeighingEquipment connect vehicle.massDataPort to weighingEquipment.massDataPort {
			ref flow references verifyMass.massDataFlow
				from source.massData to target.massData;
		}

		part weighingEquipment {
			port massDataPort : ~MassDataPort;
			port measuredMassPort : MassDataPort;

			perform verifyMass.measureMass {
				in massData = massDataPort.massData;
				out measuredMass = measuredMassPort.massData;
			}
		}

		interface weighingToVerificationUnit connect weighingEquipment.measuredMassPort to massVerificationUnit.measuredMassPort {
			ref flow references verifyMass.measuredMassFlow
				from source.massData to target.massData;
