package 'TransportationSystem' {
	attribute def PassengerCount;
	attribute def Location;
	attribute def VehicleState;
	attribute def EnvironmentalData;
	attribute def FuelLevel;

	port def EntryExitPort {
		in entryRequest : VehicleState;
		out exitConfirmation : VehicleState;
	}

	port def NavigationPort {
		in destination : Location;
		out currentLocation : Location;
	}

	port def EnvironmentPort {
		in environmentData : EnvironmentalData;
	}

	port def FuelPort {
		inout fuelLevel : FuelLevel;
	}

	part context {
		part driver {
			port entryExitPort : EntryExitPort;
			port navigationPort : NavigationPort;
			perform transport.operateVehicle {
				out destination = navigationPort.destination;
				in currentLocation = navigationPort.currentLocation;
			}
		}

		part passengers[0..4] {
			port entryExitPort : EntryExitPort;
			perform transport.travelInVehicle {
				out entryRequest = entryExitPort.entryRequest;
				in exitConfirmation = entryExitPort.exitConfirmation;
			}
		}

		interface userToVehicle connect driver.entryExitPort to vehicle.driverEntryExitPort {
			ref flow references transport.driverEntryFlow
				from source.entryRequest to target.entryRequest;
		}

		interface passengerToVehicle connect passengers.entryExitPort to vehicle.passengerEntryExitPort {
			ref flow references transport.passengerEntryFlow
				from source.entryRequest to target.entryRequest;
		}

		interface driverNavigation connect driver.navigationPort to vehicle.navigationPort {
			ref flow references transport.navigationFlow
				from source.destination to target.destination;
			ref flow references transport.locationFlow
				from target.currentLocation to source.currentLocation;
		}

		part vehicle {
			port driverEntryExitPort : ~EntryExitPort;
			port passengerEntryExitPort[0..4] : ~EntryExitPort;
			port navigationPort : ~NavigationPort;
			port environmentPort : EnvironmentPort;
			port fuelPort : FuelPort;

			perform transport.provideTransportation {
				in driverEntry = driverEntryExitPort.entryRequest;
				in passengerEntry = passengerEntryExitPort.entryRequest;
				in destination = navigationPort.destination;
				out currentLocation = navigationPort.currentLocation;
				in environmentData = environmentPort.environmentData;
				inout fuelLevel = fuelPort.fuelLevel;
			}
		}

		part environment {
			port environmentPort : ~EnvironmentPort;
			perform transport.provideEnvironmentalData {
				out environmentData = environmentPort.environmentData;
			}
		}

		interface vehicleToEnvironment connect vehicle.environmentPort to environment.environmentPort {
			ref flow references transport.environmentFlow
				from target.environmentData to source.environmentData;
		}

		part gasStation {
			port fuelPort : ~FuelPort;
			perform transport.provideFuel {
				inout fuelLevel = fuelPort.fuelLevel;
			}
		}

		interface vehicleToGasStation connect vehicle.fuelPort to gasStation.fuelPort {
			ref flow references transport.fuelFlow
				from source.fuelLevel to target.fuelLevel;
		}
	}

	action transport {
		action operateVehicle { out destination : Location; in currentLocation : Location; }
		action travelInVehicle { out entryRequest : VehicleState; in exitConfirmation : VehicleState; }
		action provideTransportation { 
			in driverEntry : VehicleState; 
			in passengerEntry : VehicleState;
			in destination : Location;
			out currentLocation : Location;
			in environmentData : EnvironmentalData;
			inout fuelLevel : FuelLevel;
		}
		action provideEnvironmentalData { out environmentData : EnvironmentalData; }
		action provideFuel { inout fuelLevel : FuelLevel; }

		succession flow driverEntryFlow from operateVehicle to provideTransportation;
		succession flow passengerEntryFlow from travelInVehicle.entryRequest to provideTransportation.passengerEntry;
		succession flow navigationFlow from operateVehicle.destination to provideTransportation.destination;
		succession flow locationFlow from provideTransportation.currentLocation to operateVehicle.currentLocation;
		succession flow environmentFlow from provideEnvironmentalData.environmentData to provideTransportation.environmentData;
		succession flow fuelFlow from provideTransportation.fuelLevel to provideFuel.fuelLevel;
	}
}

package 'TransportationSystem' {
	attribute def Location;
	attribute def Fuel;
	attribute def EnvironmentalConditions;
	attribute def VehicleControl;

	port def LocationPort {
		in location: Location;
	}

	port def FuelPort {
		in fuel: Fuel;
		out fuel: Fuel;
	}

	port def EnvironmentPort {
		in conditions: EnvironmentalConditions;
	}

	port def ControlPort {
		out control: VehicleControl;
	}

	part context {
		part driver {
			port controlPort: ControlPort;
			perform transportation.enterVehicle;
			perform transportation.operateVehicle {
				out control = controlPort.control;
			}
			perform transportation.exitVehicle;
		}

		part passengers[0..4] {
			perform transportation.enterVehicle;
			perform transportation.exitVehicle;
		}

		interface driverToVehicle connect driver.controlPort to vehicle.controlPort {
			ref flow references transportation.controlFlow
				from source.control to target.control;
		}

		part vehicle {
			port controlPort: ~ControlPort;
			port fuelPort: FuelPort;
			port environmentPort: EnvironmentPort;
			port startLocationPort: LocationPort;
			port destinationPort: LocationPort;

			perform transportation.transport {
				in control = controlPort.control;
				in fuel = fuelPort.fuel;
				in conditions = environmentPort.conditions;
				in startLocation = startLocationPort.location;
				in destination = destinationPort.location;
			}
		}

		part gasStation {
			port fuelPort: ~FuelPort;
			perform transportation.refuel {
				out fuel = fuelPort.fuel;
			}
		}

		part environment {
			port environmentPort: ~EnvironmentPort;
			perform transportation.provideConditions {
				out conditions = environmentPort.conditions;
			}
		}

		interface vehicleToGasStation connect vehicle.fuelPort to gasStation.fuelPort {
			ref flow references transportation.fuelFlow
				from target.fuel to source.fuel;
		}

		interface environmentToVehicle connect environment.environmentPort to vehicle.environmentPort {
			ref flow references transportation.environmentFlow
				from source.conditions to target.conditions;
		}
	}

	action transportation {
		action enterVehicle;
		action operateVehicle { out control: VehicleControl; }
		action transport { 
			in control: VehicleControl; 
			in fuel: Fuel;
			in conditions: EnvironmentalConditions;
			in startLocation: Location;
			in destination: Location;
		}
		action refuel { out fuel: Fuel; }
		action provideConditions { out conditions: EnvironmentalConditions; }
		action exitVehicle;

		succession flow controlFlow from operateVehicle.control to transport.control;
		succession flow fuelFlow from refuel.fuel to transport.fuel;
		succession flow environmentFlow from provideConditions.conditions to transport.conditions;
		succession enterVehicle then operateVehicle;
		succession operateVehicle then transport;
		succession transport then exitVehicle;
	}
}

package 'VehicleConfigurationManagement' {
    attribute def CylinderDiameter :> Real;

    part def Cylinder {
        attribute diameter : CylinderDiameter;
        assert constraint { diameter == 70.0 or diameter == 100.0 }
    }

    part def Engine {
        abstract;
        part cylinders : Cylinder[*] ordered;
    }

    part def FourCylinderEngine :> Engine {
        part :>> cylinders : Cylinder[4];
    }

    part def SixCylinderEngine :> Engine {
        part :>> cylinders : Cylinder[6];
    }

    part def Transmission {
        abstract;
    }

    part def ManualTransmission :> Transmission;

    part def AutomaticTransmission :> Transmission;

    part def Vehicle {
        part engine : Engine[1];
        part transmission : Transmission[1];

        assert constraint validCombination {
            (engine istype FourCylinderEngine and 
             (transmission istype ManualTransmission or transmission istype AutomaticTransmission))
            or
            (engine istype SixCylinderEngine and transmission istype AutomaticTransmission)
        }
    }
}

package 'ConfigurableEngine' {
    attribute def CylinderCount;
    attribute def CylinderDiameter;

    part def Cylinder {
        attribute diameter : CylinderDiameter;
    }

    part def Engine {
        attribute cylinderCount : CylinderCount;
        part cylinders : Cylinder[1..*];
    }

    part def FourCylinderEngine :> Engine {
        attribute redefines cylinderCount = 4;
        part redefines cylinders : Cylinder[4];
    }

    part def SixCylinderEngine :> Engine {
        attribute redefines cylinderCount = 6;
        part redefines cylinders : Cylinder[6];
    }

    part engineSystem {
        variant part selectedEngine : Engine[1] {
            variant fourCylEngine : FourCylinderEngine {
                part redefines cylinders {
                    variant attribute redefines diameter {
                        variant = 70;
                        variant = 100;
                    }
                }
            }
            variant sixCylEngine : SixCylinderEngine {
                part redefines cylinders {
                    variant attribute redefines diameter {
                        variant = 70;
                        variant = 100;
                    }
                }
            }
        }
    }
}

package 'AutomotiveConfiguration' {
    attribute def DiameterValue :> Real;

    part def Cylinder {
        attribute diameter : DiameterValue;
    }

    part def Engine {
        abstract;
        part cylinders : Cylinder[1..*];
    }

    part def FourCylinderEngine :> Engine {
        part redefines cylinders : Cylinder[4];
    }

    part def SixCylinderEngine :> Engine {
        part redefines cylinders : Cylinder[6];
    }

    part def Transmission {
        abstract;
    }

    part def ManualTransmission :> Transmission;

    part def AutomaticTransmission :> Transmission;

    part def Vehicle {
        part engine : Engine[1] {
            part redefines cylinders {
                attribute redefines diameter {
                    attribute diameterOptions : DiameterValue[2] = (70.0, 100.0);
                    constraint { diameter == diameterOptions[1] or diameter == diameterOptions[2] }
                }
            }
        }
        part transmission : Transmission[1];

        constraint fourCylinderRequiresManual {
            engine istype FourCylinderEngine implies transmission istype ManualTransmission
        }

        constraint sixCylinderRequiresAutomatic {
            engine istype SixCylinderEngine implies transmission istype AutomaticTransmission
        }
    }
}

package 'SystemAssembly' {
	attribute def MessageFormat;
	attribute def DataFormat;
	attribute def ProcessedData;
	attribute def StorageRequest;
	attribute def StorageResponse;

	port def MessageFormatPort {
		out messageFormat: MessageFormat;
	}
	port def DataFormatPort {
		out dataFormat: DataFormat;
	}
	port def ProcessedDataPort {
		out processedData: ProcessedData;
	}
	port def StorageRequestPort {
		out storageRequest: StorageRequest;
	}
	port def StorageResponsePort {
		out storageResponse: StorageResponse;
	}

	part systemAssembly {
		part softwareDesign {
			port messageFormatPort: MessageFormatPort;
			port dataFormatPort: DataFormatPort;
			perform systemOperation.defineFormats {
				out messageFormat = messageFormatPort.messageFormat;
				out dataFormat = dataFormatPort.dataFormat;
			}
		}

		part computerSubsystem {
			port messageFormatPort: ~MessageFormatPort;
			port dataFormatPort: ~DataFormatPort;
			port processedDataPort: ProcessedDataPort;
			port storageRequestPort: StorageRequestPort;
			port storageResponsePort: ~StorageResponsePort;
			perform systemOperation.integrateAndProcess {
				in messageFormat = messageFormatPort.messageFormat;
				in dataFormat = dataFormatPort.dataFormat;
				in storageResponse = storageResponsePort.storageResponse;
				out processedData = processedDataPort.processedData;
				out storageRequest = storageRequestPort.storageRequest;
			}
		}

		part storageSubsystem {
			port messageFormatPort: ~MessageFormatPort;
			port dataFormatPort: ~DataFormatPort;
			port processedDataPort: ~ProcessedDataPort;
			port storageRequestPort: ~StorageRequestPort;
			port storageResponsePort: StorageResponsePort;
			perform systemOperation.accessAndStore {
				in messageFormat = messageFormatPort.messageFormat;
				in dataFormat = dataFormatPort.dataFormat;
				in processedData = processedDataPort.processedData;
				in storageRequest = storageRequestPort.storageRequest;
				out storageResponse = storageResponsePort.storageResponse;
			}
		}

		interface softwareToComputer connect softwareDesign.messageFormatPort to computerSubsystem.messageFormatPort {
			ref flow references systemOperation.messageFormatFlow
				from source.messageFormat to target.messageFormat;
		}
		interface softwareToComputer2 connect softwareDesign.dataFormatPort to computerSubsystem.dataFormatPort {
			ref flow references systemOperation.dataFormatFlow
				from source.dataFormat to target.dataFormat;
		}
		interface softwareToStorage connect softwareDesign.messageFormatPort to storageSubsystem.messageFormatPort {
			ref flow references systemOperation.messageFormatToStorageFlow
				from source.messageFormat to target.messageFormat;
		}
		interface softwareToStorage2 connect softwareDesign.dataFormatPort to storageSubsystem.dataFormatPort {
			ref flow references systemOperation.dataFormatToStorageFlow
				from source.dataFormat to target.dataFormat;
		}
		interface computerToStorage connect computerSubsystem.processedDataPort to storageSubsystem.processedDataPort {
			ref flow references systemOperation.processedDataFlow
				from source.processedData to target.processedData;
		}
		interface computerToStorage2 connect computerSubsystem.storageRequestPort to storageSubsystem.storageRequestPort {
			ref flow references systemOperation.storageRequestFlow
				from source.storageRequest to target.storageRequest;
		}
		interface storageToComputer connect storageSubsystem.storageResponsePort to computerSubsystem.storageResponsePort {
			ref flow references systemOperation.storageResponseFlow
				from source.storageResponse to target.storageResponse;
		}
	}

	action systemOperation {
		action defineFormats { out messageFormat: MessageFormat; out dataFormat: DataFormat; }
		succession flow messageFormatFlow from defineFormats.messageFormat to integrateAndProcess.messageFormat;
		succession flow dataFormatFlow from defineFormats.dataFormat to integrateAndProcess.dataFormat;
		succession flow messageFormatToStorageFlow from defineFormats.messageFormat to accessAndStore.messageFormat;
		succession flow dataFormatToStorageFlow from defineFormats.dataFormat to accessAndStore.dataFormat;
		action integrateAndProcess { in messageFormat; in dataFormat; in storageResponse: StorageResponse; out processedData: ProcessedData; out storageRequest: StorageRequest; }
		succession flow processedDataFlow from integrateAndProcess.processedData to accessAndStore.processedData;
		succession flow storageRequestFlow from integrateAndProcess.storageRequest to accessAndStore.storageRequest;
		action accessAndStore { in messageFormat; in dataFormat; in processedData; in storageRequest; out storageResponse: StorageResponse; }
		succession flow storageResponseFlow from accessAndStore.storageResponse to integrateAndProcess.storageResponse;
	}
}

package 'PowertrainSystem' {
    attribute def Power;
    attribute def Torque;

    port def PowerPort {
        out power: Power;
    }

    port def TorquePort {
        in power: Power;
        out torque: Torque;
    }

    part def LogicalPowerProvider {
        port powerPort: PowerPort;
    }

    part def LogicalTorqueGenerator {
        port torquePort: TorquePort;
    }

    part def PhysicalPowerUnit {
        port powerPort: PowerPort;
        port torquePort: TorquePort;
    }

    part powertrainSystem {
        part logicalLayer {
            part powerProvider: LogicalPowerProvider;
            part torqueGenerator: LogicalTorqueGenerator;

            interface powerFlow connect powerProvider.powerPort to torqueGenerator.torquePort {
                flow of Power from source.power to target.power;
            }
        }

        part physicalLayer {
            part powerUnit: PhysicalPowerUnit;
        }

        allocation logicalToPhysical {
            allocate logicalLayer.powerProvider to physicalLayer.powerUnit;
            allocate logicalLayer.torqueGenerator to physicalLayer.powerUnit;
        }
    }
}

package 'PowerTransmissionSystem' {
    attribute def Power;
    attribute def Torque;

    port def PowerPort {
        out power: Power;
    }

    port def TorquePort {
        out torque: Torque;
    }

    part def LogicalSystem {
        part torqueGenerator {
            perform generateTorque {
                in power: Power;
                out torque: Torque;
            }
        }
    }

    part def PhysicalSystem {
        part powertrain {
            part engine {
                perform generateTorque {
                    in power: Power;
                    out torque: Torque;
                }
            }
        }
    }

    part system {
        part logical: LogicalSystem;
        part physical: PhysicalSystem;

        allocation logicalToPhysical {
            allocate logical.torqueGenerator to physical.powertrain;
            allocate logical.torqueGenerator.generateTorque to physical.powertrain.engine.generateTorque;
        }
    }

    action providePowerAndGenerateTorque {
        action deliverPower { out power: Power; }
        flow powerFlow from deliverPower.power to generateTorque.power;
        action generateTorque { in power: Power; out torque: Torque; }
    }
}

