package 'VehicleDynamicsSimulation' {
    attribute def Velocity;
    attribute def Position;
    attribute def Acceleration;
    attribute def TimeStep;

    port def SimulationInputPort {
        in initialVelocity : Velocity;
        in initialPosition : Position;
        in acceleration : Acceleration;
        in timeStep : TimeStep;
    }

    port def SimulationOutputPort {
        out finalVelocity : Velocity;
        out finalPosition : Position;
    }

    part context {
        part user {
            port simulationInputPort : SimulationInputPort;
            perform analyzeVehicleDynamics.provideInputs {
                out initialVelocity = simulationInputPort.initialVelocity;
                out initialPosition = simulationInputPort.initialPosition;
                out acceleration = simulationInputPort.acceleration;
                out timeStep = simulationInputPort.timeStep;
            }
        }

        interface userToSimulationSystem connect user.simulationInputPort to simulationSystem.simulationInputPort {
            ref flow references analyzeVehicleDynamics.inputFlow
                from source.initialVelocity to target.initialVelocity;
            ref flow references analyzeVehicleDynamics.inputFlow
                from source.initialPosition to target.initialPosition;
            ref flow references analyzeVehicleDynamics.inputFlow
                from source.acceleration to target.acceleration;
            ref flow references analyzeVehicleDynamics.inputFlow
                from source.timeStep to target.timeStep;
        }

        part simulationSystem {
            port simulationInputPort : ~SimulationInputPort;
            port simulationOutputPort : SimulationOutputPort;

            perform analyzeVehicleDynamics.calculateDynamics {
                in initialVelocity = simulationInputPort.initialVelocity;
                in initialPosition = simulationInputPort.initialPosition;
                in acceleration = simulationInputPort.acceleration;
                in timeStep = simulationInputPort.timeStep;
                out finalVelocity = simulationOutputPort.finalVelocity;
                out finalPosition = simulationOutputPort.finalPosition;
            }
        }

        interface simulationSystemToModelCenter connect simulationSystem.simulationOutputPort to modelCenter.simulationOutputPort {
            ref flow references analyzeVehicleDynamics.outputFlow
                from source.finalVelocity to target.finalVelocity;
            ref flow references analyzeVehicleDynamics.outputFlow
                from source.finalPosition to target.finalPosition;
        }

        part modelCenter {
            port simulationOutputPort : ~SimulationOutputPort;
            perform analyzeVehicleDynamics.processResults {
                in finalVelocity = simulationOutputPort.finalVelocity;
                in finalPosition = simulationOutputPort.finalPosition;
            }
        }
    }

    action analyzeVehicleDynamics {
        action provideInputs {
            out initialVelocity : Velocity;
            out initialPosition : Position;
            out acceleration : Acceleration;
            out timeStep : TimeStep;
        }

        succession flow inputFlow from provideInputs to calculateDynamics;

        action calculateDynamics {
            in initialVelocity : Velocity;
            in initialPosition : Position;
            in acceleration : Acceleration;
            in timeStep : TimeStep;
            out finalVelocity : Velocity;
            out finalPosition : Position;
        }

        succession flow outputFlow from calculateDynamics to processResults;

        action processResults {
            in finalVelocity : Velocity;
            in finalPosition : Position;
        }
    }
}

