package 'RiskManagementSystem' {
    attribute def Probability :> Real;
    attribute def SeverityLevel :> Real;
    attribute def SituationDescription :> String;
    attribute def CauseDescription :> String;
    attribute def FailureDescription :> String;
    attribute def RiskAssessment;
    attribute def CausalRelationship;

    part def Situation {
        attribute description : SituationDescription;
        ref cause : Cause[0..*];
        ref failure : Failure[0..*];
    }

    part def Cause {
        attribute description : CauseDescription;
        attribute probability : Probability;
        ref leadTo : Situation[0..*];
    }

    part def Failure {
        attribute description : FailureDescription;
        attribute severity : SeverityLevel;
        ref resultFrom : Situation[0..*];
    }

    part def RiskScenario {
        part situations : Situation[1..*];
        part causes : Cause[1..*];
        part failures : Failure[1..*];
        part causalLinks : CausalLink[0..*];
    }

    assoc def CausalLink {
        end source : Cause[1];
        end target : Situation[1];
        attribute relationship : CausalRelationship;
    }

    port def RiskDataPort {
        out riskAssessment : RiskAssessment;
    }

    port def ScenarioInputPort {
        in situationData : SituationDescription;
        in causeData : CauseDescription;
        in failureData : FailureDescription;
    }

    part riskManagementSystem {
        port scenarioInput : ScenarioInputPort;
        port riskOutput : RiskDataPort;

        part scenarioModeler {
            perform analyzeRisk.modelScenario {
                in situationData = scenarioInput.situationData;
                in causeData = scenarioInput.causeData;
                in failureData = scenarioInput.failureData;
            }
        }

        part riskAnalyzer {
            perform analyzeRisk.evaluateRisk {
                out riskAssessment = riskOutput.riskAssessment;
            }
        }
    }

    action analyzeRisk {
        action modelScenario {
            in situationData : SituationDescription;
            in causeData : CauseDescription;
            in failureData : FailureDescription;
        }
        action evaluateRisk {
            out riskAssessment : RiskAssessment;
        }
        succession flow modelToEvaluate from modelScenario to evaluateRisk;
    }
}

package 'RiskMetadataManagementSystem' {
    attribute def ScenarioID;
    attribute def ScenarioDescription;
    attribute def CauseID;
    attribute def CauseDescription;
    attribute def Probability;
    attribute def FailureID;
    attribute def FailureType;
    attribute def SeverityLevel;
    attribute def RelationshipID;
    attribute def RiskScenarioID;

    port def ScenarioDataPort {
        out scenarioID: ScenarioID;
        out scenarioDesc: ScenarioDescription;
    }

    port def CauseDataPort {
        out causeID: CauseID;
        out causeDesc: CauseDescription;
        out probability: Probability;
    }

    port def FailureDataPort {
        out failureID: FailureID;
        out failureType: FailureType;
        out severity: SeverityLevel;
    }

    port def RelationshipDataPort {
        out relationshipID: RelationshipID;
        out sourceScenario: ScenarioID;
        out targetScenario: ScenarioID;
    }

    port def RiskScenarioDataPort {
        out riskScenarioID: RiskScenarioID;
        out scenarios: ScenarioID[*];
        out causes: CauseID[*];
        out failures: FailureID[*];
    }

    part context {
        part user {
            port scenarioPort: ScenarioDataPort;
            port causePort: CauseDataPort;
            port failurePort: FailureDataPort;
            port relationshipPort: RelationshipDataPort;
            port riskScenarioPort: RiskScenarioDataPort;

            perform manageRiskMetadata.identifyScenarios {
                out scenarioID = scenarioPort.scenarioID;
                out scenarioDesc = scenarioPort.scenarioDesc;
            }

            perform manageRiskMetadata.detailCauses {
                out causeID = causePort.causeID;
                out causeDesc = causePort.causeDesc;
                out probability = causePort.probability;
            }

            perform manageRiskMetadata.associateFailures {
                out failureID = failurePort.failureID;
                out failureType = failurePort.failureType;
                out severity = failurePort.severity;
            }

            perform manageRiskMetadata.trackRelationships {
                out relationshipID = relationshipPort.relationshipID;
                out sourceScenario = relationshipPort.sourceScenario;
                out targetScenario = relationshipPort.targetScenario;
            }

            perform manageRiskMetadata.formRiskScenarios {
                out riskScenarioID = riskScenarioPort.riskScenarioID;
                out scenarios = riskScenarioPort.scenarios;
                out causes = riskScenarioPort.causes;
                out failures = riskScenarioPort.failures;
            }
        }

        interface userToSystem connect user.scenarioPort to riskSystem.scenarioPort {
            ref flow references manageRiskMetadata.scenarioFlow
                from source.scenarioID to target.scenarioID;
            ref flow references manageRiskMetadata.scenarioFlow
                from source.scenarioDesc to target.scenarioDesc;
        }

        interface userToCauseSystem connect user.causePort to riskSystem.causePort {
            ref flow references manageRiskMetadata.causeFlow
                from source.causeID to target.causeID;
            ref flow references manageRiskMetadata.causeFlow
                from source.causeDesc to target.causeDesc;
            ref flow references manageRiskMetadata.causeFlow
                from source.probability to target.probability;
        }

        interface userToFailureSystem connect user.failurePort to riskSystem.failurePort {
            ref flow references manageRiskMetadata.failureFlow
                from source.failureID to target.failureID;
            ref flow references manageRiskMetadata.failureFlow
                from source.failureType to target.failureType;
            ref flow references manageRiskMetadata.failureFlow
                from source.severity to target.severity;
        }

        interface userToRelationshipSystem connect user.relationshipPort to riskSystem.relationshipPort {
            ref flow references manageRiskMetadata.relationshipFlow
                from source.relationshipID to target.relationshipID;
            ref flow references manageRiskMetadata.relationshipFlow
                from source.sourceScenario to target.sourceScenario;
            ref flow references manageRiskMetadata.relationshipFlow
                from source.targetScenario to target.targetScenario;
        }

        interface userToRiskScenarioSystem connect user.riskScenarioPort to riskSystem.riskScenarioPort {
            ref flow references manageRiskMetadata.riskScenarioFlow
                from source.riskScenarioID to target.riskScenarioID;
            ref flow references manageRiskMetadata.riskScenarioFlow
                from source.scenarios to target.scenarios;
            ref flow references manageRiskMetadata.riskScenarioFlow
                from source.causes to target.causes;
            ref flow references manageRiskMetadata.riskScenarioFlow
                from source.failures to target.failures;
        }

        part riskSystem {
            port scenarioPort: ~ScenarioDataPort;
            port causePort: ~CauseDataPort;
            port failurePort: ~FailureDataPort;
            port relationshipPort: ~RelationshipDataPort;
            port riskScenarioPort: ~RiskScenarioDataPort;

            perform manageRiskMetadata.processScenarios {
                in scenarioID = scenarioPort.scenarioID;
                in scenarioDesc = scenarioPort.scenarioDesc;
            }

            perform manageRiskMetadata.processCauses {
                in causeID = causePort.causeID;
                in causeDesc = causePort.causeDesc;
                in probability = causePort.probability;
            }

            perform manageRiskMetadata.processFailures {
                in failureID = failurePort.failureID;
                in failureType = failurePort.failureType;
                in severity = failurePort.severity;
            }

            perform manageRiskMetadata.processRelationships {
                in relationshipID = relationshipPort.relationshipID;
                in sourceScenario = relationshipPort.sourceScenario;
                in targetScenario = relationshipPort.targetScenario;
            }

            perform manageRiskMetadata.processRiskScenarios {
                in riskScenarioID = riskScenarioPort.riskScenarioID;
                in scenarios = riskScenarioPort.scenarios;
                in causes = riskScenarioPort.causes;
                in failures = riskScenarioPort.failures;
            }
        }
    }

    action manageRiskMetadata {
        action identifyScenarios { 
            out scenarioID: ScenarioID; 
            out scenarioDesc: ScenarioDescription;
        }
        succession flow scenarioFlow from identifyScenarios to processScenarios;
        action processScenarios { 
            in scenarioID: ScenarioID; 
            in scenarioDesc: ScenarioDescription;
        }

        action detailCauses { 
            out causeID: CauseID; 
            out causeDesc: CauseDescription;
            out probability: Probability;
        }
        succession flow causeFlow from detailCauses to processCauses;
        action processCauses { 
            in causeID: CauseID; 
            in causeDesc: CauseDescription;
            in probability: Probability;
        }

        action associateFailures { 
            out failureID: FailureID; 
            out failureType: FailureType;
            out severity: SeverityLevel;
        }
        succession flow failureFlow from associateFailures to processFailures;
        action processFailures { 
            in failureID: FailureID; 
            in failureType: FailureType;
            in severity: SeverityLevel;
        }

        action trackRelationships { 
            out relationshipID: RelationshipID; 
            out sourceScenario: ScenarioID;
            out targetScenario: ScenarioID;
        }
        succession flow relationshipFlow from trackRelationships to processRelationships;
        action processRelationships { 
            in relationshipID: RelationshipID; 
            in sourceScenario: ScenarioID;
            in targetScenario: ScenarioID;
        }

        action formRiskScenarios { 
            out riskScenarioID: RiskScenarioID; 
            out scenarios: ScenarioID[*];
            out causes: CauseID[*];
            out failures: FailureID[*];
        }
        succession flow riskScenarioFlow from formRiskScenarios to processRiskScenarios;
        action processRiskScenarios { 
            in riskScenarioID: RiskScenarioID; 
            in scenarios: ScenarioID[*];
            in causes: CauseID[*];
            in failures: FailureID[*];
        }
    }
}

package 'RiskAnalysisSystem' {
	attribute def BatteryLevel;
	attribute def FailureProbability;
	attribute def RiskAlert;
	attribute def SeverityLevel;

	port def BatteryStatusPort {
		in batteryLevel: BatteryLevel;
		out failureProbability: FailureProbability;
	}

	port def RiskAlertPort {
		out riskAlert: RiskAlert;
		out severityLevel: SeverityLevel;
	}

	part def Battery {
		attribute currentLevel: BatteryLevel;
		attribute agingFactor: Real;
	}

	part context {
		part equipment {
			part battery: Battery;
			port batteryStatusPort: BatteryStatusPort;

			perform riskAnalysis.monitorBatteryLevel {
				out batteryLevel = batteryStatusPort.batteryLevel;
			}
		}

		interface equipmentToAnalyzer connect equipment.batteryStatusPort to riskAnalyzer.batteryStatusPort {
			ref flow references riskAnalysis.batteryLevelFlow
				from source.batteryLevel to target.batteryLevel;
		}

		part riskAnalyzer {
			port batteryStatusPort: ~BatteryStatusPort;
			port riskAlertPort: RiskAlertPort;

			perform riskAnalysis.detectBatteryAging {
				in batteryLevel = batteryStatusPort.batteryLevel;
				out failureProbability = batteryStatusPort.failureProbability;
			}

			perform riskAnalysis.analyzeFailureRisk {
				in failureProbability = batteryStatusPort.failureProbability;
				out riskAlert = riskAlertPort.riskAlert;
				out severityLevel = riskAlertPort.severityLevel;
			}
		}

		interface analyzerToUser connect riskAnalyzer.riskAlertPort to userInterface.riskAlertPort {
			ref flow references riskAnalysis.alertFlow
				from source.riskAlert to target.riskAlert;
			ref flow references riskAnalysis.severityFlow
				from source.severityLevel to target.severityLevel;
		}

		part userInterface {
			port riskAlertPort: ~RiskAlertPort;

			perform riskAnalysis.notifyUser {
				in riskAlert = riskAlertPort.riskAlert;
				in severityLevel = riskAlertPort.severityLevel;
			}
		}
	}

	action riskAnalysis {
		action monitorBatteryLevel { out batteryLevel: BatteryLevel; }
		succession flow batteryLevelFlow from monitorBatteryLevel.batteryLevel to detectBatteryAging.batteryLevel;

		action detectBatteryAging { 
			in batteryLevel: BatteryLevel; 
			out failureProbability: FailureProbability; 
		}
		succession flow probabilityFlow from detectBatteryAging.failureProbability to analyzeFailureRisk.failureProbability;

		action analyzeFailureRisk { 
			in failureProbability: FailureProbability; 
			out riskAlert: RiskAlert;
			out severityLevel: SeverityLevel;
		}
		succession flow alertFlow from analyzeFailureRisk.riskAlert to notifyUser.riskAlert;
		succession flow severityFlow from analyzeFailureRisk.severityLevel to notifyUser.severityLevel;

		action notifyUser { 
			in riskAlert: RiskAlert;
			in severityLevel: SeverityLevel;
		}
	}
}

package 'HierarchicalSystem' {
    attribute def Data;
    attribute def Control;
    attribute def Status;

    port def DataPort {
        in dataIn: Data;
        out dataOut: Data;
    }

    port def ControlPort {
        in controlIn: Control;
        out controlOut: Control;
    }

    port def StatusPort {
        out status: Status;
    }

    part system {
        doc /* Top-level system with hierarchical decomposition */

        port systemDataPort: DataPort;
        port systemControlPort: ControlPort;
        port systemStatusPort: StatusPort;

        part subsystemA {
            doc /* Subsystem A with well-defined interfaces */

            port dataPortA: DataPort;
            port controlPortA: ControlPort;
            port statusPortA: StatusPort;

            part componentA1 {
                doc /* Component A1 - independently analyzable */
                port dataPortA1: DataPort;
                port controlPortA1: ControlPort;
                port statusPortA1: StatusPort;
            }

            part componentA2 {
                doc /* Component A2 - independently analyzable */
                port dataPortA2: DataPort;
                port controlPortA2: ControlPort;
                port statusPortA2: StatusPort;
            }

            interface a1ToA2 connect componentA1.dataPortA1 to componentA2.dataPortA2;
            interface a1ControlToA2 connect componentA1.controlPortA1 to componentA2.controlPortA2;
        }

        part subsystemB {
            doc /* Subsystem B with well-defined interfaces */

            port dataPortB: DataPort;
            port controlPortB: ControlPort;
            port statusPortB: StatusPort;

            part componentB1 {
                doc /* Component B1 - independently analyzable */
                port dataPortB1: DataPort;
                port controlPortB1: ControlPort;
                port statusPortB1: StatusPort;
            }

            part componentB2 {
                doc /* Component B2 - independently analyzable */
                port dataPortB2: DataPort;
                port controlPortB2: ControlPort;
                port statusPortB2: StatusPort;
            }

            interface b1ToB2 connect componentB1.dataPortB1 to componentB2.dataPortB2;
            interface b1ControlToB2 connect componentB1.controlPortB1 to componentB2.controlPortB2;
        }

        interface systemToSubsystemA connect systemDataPort to subsystemA.dataPortA;
        interface systemControlToA connect systemControlPort to subsystemA.controlPortA;
        interface subsystemAToB connect subsystemA.dataPortA to subsystemB.dataPortB;
        interface subsystemAControlToB connect subsystemA.controlPortA to subsystemB.controlPortB;
        interface subsystemBToSystem connect subsystemB.dataPortB to systemDataPort;
        interface subsystemBStatusToSystem connect subsystemB.statusPortB to systemStatusPort;
    }

    view def StructuralDecompositionView {
        doc /* View for system engineers and IV&V to verify integrity and modular design */
        render system;
        expose system.**;
    }
}

package 'AutomotiveSystem' {
    attribute def SafetyLevel {
        attribute isMandatory: Boolean;
    }

    part def Component {
        attribute name: String;
        attribute isSafetyRelated: Boolean default false;
        attribute safetyLevel: SafetyLevel[0..1];
    }

    part def SafetyComponent :> Component {
        attribute redefines isSafetyRelated = true;
        attribute redefines safetyLevel: SafetyLevel[1];
    }

    part def FunctionalComponent :> Component {
        attribute redefines isSafetyRelated = false;
    }

    part def System :> Component {
        part subsystems: Component[*];
        part components: Component[*];
    }

    part automotiveSystem: System {
        attribute redefines name = "Automotive System Root";

        part chassisSubsystem: System {
            attribute redefines name = "Chassis Subsystem";

            part abs: SafetyComponent {
                attribute redefines name = "Anti-lock Braking System";
                attribute redefines safetyLevel {
                    attribute redefines isMandatory = false;
                }
            }

            part suspensionSystem: FunctionalComponent {
                attribute redefines name = "Suspension System";
            }
        }

        part safetySubsystem: System {
            attribute redefines name = "Safety Subsystem";

            part seatBelt: SafetyComponent {
                attribute redefines name = "Seat Belt";
                attribute redefines safetyLevel {
                    attribute redefines isMandatory = true;
                }
            }

            part bumper: SafetyComponent {
                attribute redefines name = "Bumper";
                attribute redefines safetyLevel {
                    attribute redefines isMandatory = true;
                }
            }

            part driverAirbag: SafetyComponent {
                attribute redefines name = "Driver Airbag";
                attribute redefines safetyLevel {
                    attribute redefines isMandatory = false;
                }
            }
        }

        part powertrainSubsystem: System {
            attribute redefines name = "Powertrain Subsystem";

            part engine: FunctionalComponent {
                attribute redefines name = "Engine";
            }

            part transmission: FunctionalComponent {
                attribute redefines name = "Transmission";
            }
        }
    }

    view def HierarchicalView {
        expose automotiveSystem.**;
    }

    view def SafetyComponentsView {
        filter automotiveSystem.** @ Component such that isSafetyRelated;
    }

    view def NonSafetyComponentsView {
        filter automotiveSystem.** @ Component such that !isSafetyRelated;
    }
}

