package 'FuelManagementSystem' {
	attribute def Fuel;
	attribute def Temperature;

	port def FuelSupplyPort {
		out fuel : Fuel;
	}

	port def FuelReturnPort {
		in fuel : Fuel;
	}

	port def TemperaturePort {
		out temperature : Temperature;
	}

	part context {
		part fuelTank {
			port fuelSupplyPort : FuelSupplyPort;
			port fuelReturnPort : FuelReturnPort;
			port temperaturePort : TemperaturePort;

			perform manageFuelFlow.supplyFuel {
				out fuel = fuelSupplyPort.fuel;
				out temperature = temperaturePort.temperature;
			}

			perform manageFuelFlow.receiveFuel {
				in fuel = fuelReturnPort.fuel;
			}
		}

		interface fuelSupplyInterface connect fuelTank.fuelSupplyPort to engine.fuelSupplyPort {
			ref flow references manageFuelFlow.fuelSupplyFlow
				from source.fuel to target.fuel;
		}

		interface fuelReturnInterface connect engine.fuelReturnPort to fuelTank.fuelReturnPort {
			ref flow references manageFuelFlow.fuelReturnFlow
				from source.fuel to target.fuel;
		}

		interface temperatureMonitorInterface connect fuelTank.temperaturePort to engine.temperaturePort {
			ref flow references manageFuelFlow.temperatureFlow
				from source.temperature to target.temperature;
		}

		part engine {
			port fuelSupplyPort : ~FuelSupplyPort;
			port fuelReturnPort : ~FuelReturnPort;
			port temperaturePort : ~TemperaturePort;

			perform manageFuelFlow.consumeFuel {
				in fuel = fuelSupplyPort.fuel;
				in temperature = temperaturePort.temperature;
				out fuel = fuelReturnPort.fuel;
			}
		}
	}

	action manageFuelFlow {
		action supplyFuel { out fuel : Fuel; out temperature : Temperature; }
		succession flow fuelSupplyFlow from supplyFuel.fuel to consumeFuel.fuel;
		succession flow temperatureFlow from supplyFuel.temperature to consumeFuel.temperature;
		action consumeFuel { in fuel : Fuel; in temperature : Temperature; out fuel : Fuel; }
		succession flow fuelReturnFlow from consumeFuel.fuel to receiveFuel.fuel;
		action receiveFuel { in fuel : Fuel; }
	}
}

