        part :>> frontAxleAssembly {
            part :>> axle : FrontAxle;
            part leftWheel :>> wheel1;
            part rightWheel :>> wheel2;
        }
        part :>> rearAxleAssembly {
            part :>> axle : RearAxle;
            part leftWheel :>> wheel1;
            part rightWheel :>> wheel2;
        }
    }
}
