package InformationClassificationSystem {

    enum def InformationClassificationLevel {
        enum Unclassified;
        enum Confidential;
        enum Secret;
    }

    enum def GradeLevel {
        enum A;
        enum B;
        enum C;
        enum D;
        enum F;
    }

    attribute def InformationClassification {
        attribute level : InformationClassificationLevel;
        attribute code : String;
        attribute color : String;
    }

    attribute def Grade {
        attribute level : GradeLevel;
        attribute points : Real;
    }

    part def InformationClassificationManagementSystem {
        attribute unclassifiedInfo : InformationClassification = InformationClassification {
            :>> level = InformationClassificationLevel::Unclassified;
            :>> code = "uncl";
            :>> color = "green";
        }

        attribute confidentialInfo : InformationClassification = InformationClassification {
            :>> level = InformationClassificationLevel::Confidential;
            :>> code = "conf";
            :>> color = "yellow";
        }

        attribute secretInfo : InformationClassification = InformationClassification {
            :>> level = InformationClassificationLevel::Secret;
            :>> code = "secr";
            :>> color = "red";
        }

        attribute gradeA : Grade = Grade {
            :>> level = GradeLevel::A;
            :>> points = 4.0;
        }

        attribute gradeB : Grade = Grade {
            :>> level = GradeLevel::B;
            :>> points = 3.0;
        }

        attribute gradeC : Grade = Grade {
            :>> level = GradeLevel::C;
            :>> points = 2.0;
        }

        attribute gradeD : Grade = Grade {
            :>> level = GradeLevel::D;
            :>> points = 1.0;
        }

        attribute gradeF : Grade = Grade {
            :>> level = GradeLevel::F;
            :>> points = 0.0;
        }
    }
}

