package WheelAssemblyStructure {

    part def WheelAssembly {
        part wheelBearingUnit : WheelBearingUnit;
        part tire : Tire;
        part hub : Hub;
        part boltConnections : BoltConnection[1..*];

        connection : BeadRimConnection connect tire.beads to hub.rims;
        connection : BoltMounting connect hub.mountingHoles to hub.threadedHoles via boltConnections;
    }

    part def WheelBearingUnit;

    part def Tire {
        part beads : Bead[2];
    }

    part def Bead;

    part def Hub {
        part rims : Rim[2];
        part mountingHoles : BoltMountingHole[1..*];
        part threadedHoles : ThreadedHole[1..*];
    }

    part def Rim;

    part def BoltMountingHole;

    part def ThreadedHole;

    part def BoltConnection;

    connection def BeadRimConnection {
        end bead : Bead[2];
        end rim : Rim[2];
    }

    connection def BoltMounting {
        end mountingHole : BoltMountingHole[1..*];
        end threadedHole : ThreadedHole[1..*];
        end bolt : BoltConnection[1..*];
    }
}

package FuelFlowManagementSystem {

    part def FuelTankAssembly {
        port fuelSupplyPort : FuelFlowPort;
        port fuelReturnPort : FuelFlowPort;

        attribute fuelLevel : Real;
        attribute temperature : Real;
    }

    part def Engine {
        port fuelInletPort : ~FuelFlowPort;
        port fuelReturnPort : ~FuelFlowPort;

        attribute operatingStatus : String;
        attribute fuelConsumptionRate : Real;
    }

    port def FuelFlowPort {
        attribute flowRate : Real;
        attribute temperature : Real;
        attribute pressure : Real;
    }

    interface def FuelTransferInterface {
        end supplierPort : FuelFlowPort;
        end consumerPort : ~FuelFlowPort;

        flow of Fuel from supplierPort to consumerPort;
        flow of TemperatureData from supplierPort to consumerPort;
    }

    item def Fuel {
        attribute volume : Real;
        attribute temperature : Real;
    }

    item def TemperatureData {
        attribute value : Real;
        attribute timestamp : String;
    }

    part fuelFlowSystem : FuelFlowManagementSystem {
        part fuelTank : FuelTankAssembly;
        part engine : Engine;

        interface fuelSupplyInterface : FuelTransferInterface 
            connect fuelTank.fuelSupplyPort to engine.fuelInletPort;

        interface fuelReturnInterface : FuelTransferInterface 
            connect engine.fuelReturnPort to fuelTank.fuelReturnPort;
    }

    part def FuelFlowManagementSystem {
        part fuelTankAssembly : FuelTankAssembly;
        part engineUnit : Engine;

        attribute systemStatus : String;
        attribute totalFuelFlow : Real;
    }
}

