package VehiclePowerTransmissionSystem {

    port def PowerPort {
        in item power : Real;
    }

    port def ControlPort {
        in item command : Real;
    }

    port def MechanicalPort {
        in item torque : Real;
        in item speed : Real;
    }

    port def RoadInterface {
        out item force : Real;
        in item resistance : Real;
    }

    part def Engine {
        port fuelCommand : ControlPort;
        port powerOut : PowerPort;
        port clutchConnection : MechanicalPort;
    }

    part def Clutch {
        port engineSide : MechanicalPort;
        port transmissionSide : MechanicalPort;
    }

    part def Transmission {
        port clutchIn : MechanicalPort;
        port driveshaftOut : MechanicalPort;
    }

    part def Driveshaft {
        port transmissionIn : MechanicalPort;
        port rearAxleOut : MechanicalPort;
    }

    part def Differential {
        port driveshaftIn : MechanicalPort;
        port leftOut : MechanicalPort;
        port rightOut : MechanicalPort;
    }

    part def Halfshaft {
        port differentialIn : MechanicalPort;
        port wheelOut : MechanicalPort;
    }

    part def Wheel {
        port axleIn : MechanicalPort;
        port road : RoadInterface;
    }

    part def RearAxleAssembly {
        part differential : Differential;
        part leftHalfshaft : Halfshaft;
        part rightHalfshaft : Halfshaft;

        port driveshaftIn : MechanicalPort;
        port leftWheelOut : MechanicalPort;
        port rightWheelOut : MechanicalPort;

        connection : Connect driveshaftIn to differential.driveshaftIn;
        connection : Connect differential.leftOut to leftHalfshaft.differentialIn;
        connection : Connect differential.rightOut to rightHalfshaft.differentialIn;
        connection : Connect leftHalfshaft.wheelOut to leftWheelOut;
        connection : Connect rightHalfshaft.wheelOut to rightWheelOut;
    }

    part def VehiclePowerSystem {
        part engine : Engine;
        part clutch : Clutch;
        part transmission : Transmission;
        part driveshaft : Driveshaft;
        part rearAxle : RearAxleAssembly;
        part leftRearWheel : Wheel;
        part rightRearWheel : Wheel;

        port fuelCommand : ControlPort;
        port leftRoadInterface : RoadInterface;
        port rightRoadInterface : RoadInterface;

        connection : Connect fuelCommand to engine.fuelCommand;
        connection : Connect engine.clutchConnection to clutch.engineSide;
        connection : Connect clutch.transmissionSide to transmission.clutchIn;
        connection : Connect transmission.driveshaftOut to driveshaft.transmissionIn;
        connection : Connect driveshaft.rearAxleOut to rearAxle.driveshaftIn;
        connection : Connect rearAxle.leftWheelOut to leftRearWheel.axleIn;
        connection : Connect rearAxle.rightWheelOut to rightRearWheel.axleIn;
        connection : Connect leftRearWheel.road to leftRoadInterface;
        connection : Connect rightRearWheel.road to rightRoadInterface;
    }
}

package SystemModel {

    part def C1 {
        port pb;
    }

    part def C2 {
        port pc;
    }

    part def C3 {
        port pd;
    }

    part def C4 {
        port pe;
    }

    part def B11 {
        part c1 : C1;
        part c2 : C2;
        port pe;

        connect c1.pb to c2.pc;
        bind pe = c1.pb;
    }

    part def B12 {
        part c3 : C3;
        part c4 : C4;
        port pf;

        bind pf = c3.pd;
    }

    part def System {
        part b11 : B11;
        part b12 : B12;

        connect b11.pe to b12.pf;
    }

    part def B21 {
        ref part c1 : C1;
        ref part c2 : C2;
        ref part c3 : C3;
        ref part c4 : C4;
    }

    part def B22 {
        ref part c1 : C1;
        ref part c2 : C2;
        ref part c3 : C3;
        ref part c4 : C4;
    }

    part def SystemAlternative {
        part b21 : B21;
        part b22 : B22;
    }
}

package PowertrainSystem {

    part def FuelCommand;
