    part def EngineStopCommand;
    part def Torque;
    part def DrivingForce;

    part def Engine {
        port fuelCmdIn : FuelCommand;
        port startCmdIn : EngineStartCommand;
        port stopCmdIn : EngineStopCommand;
        port torqueOut : Torque;
    }

    part def Transmission {
        port torqueIn : Torque;
        port torqueOut : Torque;
    }

    part def Driveshaft {
        port torqueIn : Torque;
        port torqueOut : Torque;
    }

    part def Wheel {
        port torqueIn : Torque;
        port forceOut : DrivingForce;
    }

    part def PowertrainControlSystem {
        part engine : Engine;
        part transmission : Transmission;
        part driveshaft : Driveshaft;
        part leftWheel : Wheel;
        part rightWheel : Wheel;

        port fuelCommandInput : FuelCommand;
        port engineStartInput : EngineStartCommand;
        port engineStopInput : EngineStopCommand;
        port leftWheelForce : DrivingForce;
        port rightWheelForce : DrivingForce;

        connect fuelCommandInput to engine.fuelCmdIn;
        connect engineStartInput to engine.startCmdIn;
        connect engineStopInput to engine.stopCmdIn;
        connect engine.torqueOut to transmission.torqueIn;
        connect transmission.torqueOut to driveshaft.torqueIn;
        connect driveshaft.torqueOut to leftWheel.torqueIn;
        connect driveshaft.torqueOut to rightWheel.torqueIn;
        connect leftWheel.forceOut to leftWheelForce;
        connect rightWheel.forceOut to rightWheelForce;
    }
}

package PowertrainSystem {

    part def FuelController {
        in port fuelCommand;
        out port startSignal;
        out port fuelFlow;
    }

    part def Engine {
        in port startSignal;
        in port fuelFlow;
        in port stallSignal;
        out port torqueOutput;
    }

    part def TorqueAmplifier {
        in port torqueInput;
        out port amplifiedTorque;
    }

    part def Transmission {
        in port torqueInput;
        out port transmittedTorque;
    }

    part def TorqueDistributor {
        in port torqueInput;
        out port wheelTorque;
    }

    part def DriveWheels {
        in port wheelTorque;
    }

    part def StallDetector {
        out port stallSignal;
    }

    part def PowertrainControlSystem {
        part fuelController : FuelController;
        part engine : Engine;
        part torqueAmplifier : TorqueAmplifier;
        part transmission : Transmission;
        part torqueDistributor : TorqueDistributor;
        part driveWheels : DriveWheels;
        part stallDetector : StallDetector;

        connection : fuelController.startSignal to engine.startSignal;
        connection : fuelController.fuelFlow to engine.fuelFlow;
        connection : engine.torqueOutput to torqueAmplifier.torqueInput;
        connection : torqueAmplifier.amplifiedTorque to transmission.torqueInput;
        connection : transmission.transmittedTorque to torqueDistributor.torqueInput;
        connection : torqueDistributor.wheelTorque to driveWheels.wheelTorque;
        connection : stallDetector.stallSignal to engine.stallSignal;
    }
}

