    part def CouplerMechanism {
        attribute isEngaged : Boolean = false;
    }

    connection def TowConnection {
        end vehicle : TowBall[1];
        end trailer : CouplerMechanism[1];
    }

    action def Hitch {
        in vehicle : Vehicle;
        in trailer : Trailer;
        out connection : TowConnection;
    }

    action def Unhitch {
        in connection : TowConnection;
        out vehicle : Vehicle;
        out trailer : Trailer;
    }

    part vehicleTrailerSystem : VehicleTrailerSystem {
        part vehicle : Vehicle;
        part trailer : Trailer;

        connection towConnection : TowConnection connect 
            vehicle.towBall to trailer.couplerMechanism;

        action hitchTrailer : Hitch {
            in vehicle = vehicleTrailerSystem.vehicle;
            in trailer = vehicleTrailerSystem.trailer;
            out connection = vehicleTrailerSystem.towConnection;
        }

        action unhitchTrailer : Unhitch {
            in connection = vehicleTrailerSystem.towConnection;
            out vehicle = vehicleTrailerSystem.vehicle;
            out trailer = vehicleTrailerSystem.trailer;
        }
    }
