package AutomobileAssemblyModel {

    part def VehicleAssembly {
        attribute vin : String;
        attribute assemblyStatus : String;
    }

    part def Gearbox {
        attribute serialNumber : String;
        attribute type : String;
    }

    part def Engine {
        attribute serialNumber : String;
        attribute displacement : Real;
        attribute type : String;
    }

    part def AssemblyWithoutEngine :> VehicleAssembly {
        part gearbox : Gearbox;
    }

    part def FullyAssembledVehicle :> VehicleAssembly {
        part gearbox : Gearbox;
        part engine : Engine;
    }

    action def InstallGearbox {
        in vehicleWithoutGearboxEngine : VehicleAssembly;
        in gearboxToInstall : Gearbox;
        out vehicleWithGearbox : AssemblyWithoutEngine;
    }

    action def InstallEngine {
        in vehicleWithGearbox : AssemblyWithoutEngine;
        in engineToInstall : Engine;
        out completeVehicle : FullyAssembledVehicle;
    }

    action def AssembleVehicle {
        in baseVehicle : VehicleAssembly;
        in gearbox : Gearbox;
        in engine : Engine;
        out assembledVehicle : FullyAssembledVehicle;

        action installGearboxStep : InstallGearbox {
            in vehicleWithoutGearboxEngine = baseVehicle;
            in gearboxToInstall = gearbox;
        }

        then action installEngineStep : InstallEngine {
            in vehicleWithGearbox = installGearboxStep.vehicleWithGearbox;
            in engineToInstall = engine;
        }

        bind assembledVehicle = installEngineStep.completeVehicle;
    }
}

package VehicleControlSystem {

    part def VehicleA {
        attribute temperature : Real;
        attribute brakePedalPressed : Boolean;
        attribute startSignalReceived : Boolean;
        attribute vehicleOnSignalReceived : Boolean;
        attribute overTempSignalSent : Boolean;
        attribute restoreSignalReceived : Boolean;
        attribute safetyThreshold : Real = 85.0;

        port healthOutput : Real;
        port controlInput : ControlSignal;
        port controlOutput : ControlSignal;
    }

    part def VehicleController {
        port commandInput : ControlSignal;
        port commandOutput : ControlSignal;
    }

    part def ControlSignal {
        attribute signalType : String;
        attribute value : Boolean;
    }

    state def VehicleStates {
        entry state off;
        state starting;
        state running {
            entry action {
                doc /* Perform self-checks */
                doc /* Supply power to vehicle */
            }
            exit action {
                doc /* Apply parking brake */
            }
        }
        state healthManagement {
            state normal;
            state degraded;
            state maintenance;

            transition normal_to_degraded
                first normal
                then degraded
                guard temperature > safetyThreshold
                effect overTempSignalSent := true;

            transition degraded_to_normal
                first degraded
                then normal
                guard restoreSignalReceived;

            transition maintenance_to_normal
                first maintenance
                then normal
                guard restoreSignalReceived;
        }

        transition off_to_starting
            first off
            then starting
            guard brakePedalPressed & startSignalReceived;

        transition starting_to_running
            first starting
            then running
            guard vehicleOnSignalReceived;

        transition running_to_off
            first running
            then off;
    }

    state def ControllerStates {
        entry state idle;
        state active;
        state shutdown;

        transition idle_to_active
            first idle
            then active
            guard startSignalReceived;

        transition active_to_shutdown
            first active
            then shutdown;

        transition shutdown_to_idle
            first shutdown
            then idle;
    }

    part vehicleSystem : VehicleControlSystem {
        part vehicle : VehicleA {
            exhibit state vehicleStates : VehicleStates;
        }

        part controller : VehicleController {
            exhibit state controllerStates : ControllerStates;
        }

        connection : Connect
            from vehicle.controlOutput
            to controller.commandInput;

        connection : Connect
            from controller.commandOutput
            to vehicle.controlInput;

        action monitorHealth {
            in item temp : Real = vehicle.temperature;
            out item healthStatus : Real;

            healthStatus := temp;
