package VehicleSystem {

    part def Vehicle {
        attribute fuel : Real;
        attribute maintenanceInterval : Real;
        attribute temperatureThreshold : Real;
        attribute currentTemperature : Real;
        attribute isOverheating : Boolean;

        port vehicleStatusPort : VehicleStatusPort;
        port controlPort : ControlPort;
        port sensorPort : SensorPort;
    }

    part def VehicleController {
        port statusReceivePort : VehicleStatusPort;
        port controlSendPort : ControlPort;
    }

    port def VehicleStatusPort {
        out statusSignal : StatusSignal;
        out warningSignal : WarningSignal;
    }

    port def ControlPort {
        in startSignal : StartSignal;
        in shutdownSignal : ShutdownSignal;
        in brakePedalPressed : Boolean;
    }

    port def SensorPort {
        in temperatureReading : Real;
    }

    enum def VehicleState {
        enum shutdown;
        enum normal;
        enum degraded;
    }

    item def StatusSignal {
        attribute state : VehicleState;
    }

    item def WarningSignal {
        attribute warningType : String;
    }

    item def StartSignal;
    item def ShutdownSignal;

    state def VehicleStateMachine {
        entry state shutdown;
        state normal;
        state degraded;

        transition startup
            first shutdown
            then normal
            guard brakePedalPressed and startSignalReceived;

        transition overheatDetected
            first normal
            then degraded
            guard isOverheating;

        transition recovered
            first degraded
            then normal
            guard not isOverheating;

        transition shutdownTransition
            first normal
            then shutdown
            guard shutdownSignalReceived;
    }

    action def SelfInspection {
        in vehicle : Vehicle;
        out inspectionPassed : Boolean;
    }

    action def PowerSupplyProcedure {
        in vehicle : Vehicle;
        out powerSupplied : Boolean;
    }

    action def SendWarning {
        in warningType : String;
        out warning : WarningSignal;
    }

    action def PerformMaintenance {
        in vehicle : Vehicle;
        out maintenanceCompleted : Boolean;
    }

    action def FaultRecovery {
        in vehicle : Vehicle;
        out recoveryCompleted : Boolean;
    }

    part vehicleSystemModel : Vehicle {
        part controller : VehicleController;

        connection : Connect(
            vehicleStatusPort, 
            controller.statusReceivePort
        );

        connection : Connect(
            controller.controlSendPort,
            controlPort
        );
    }
}

