package AutomotiveConfigurationSystem {

    part def Component1;
    part def Component2;
    part def Component3;
    part def Component4;
    part def Component5;
    part def Component6;

    part def SubsystemA {
        variant part config1 {
            part c1 : Component1;
            part c2 : Component2;
        }
        variant part config2 {
            part c2 : Component2;
            part c3 : Component3;
        }
    }

    part def SubsystemB {
        variant part config1 {
            part c4 : Component4;
            part c5 : Component5;
        }
        variant part config2 {
            part c5 : Component5;
            part c6 : Component6;
        }
    }

    part def Automobile {
        part subsystemA : SubsystemA;
        part subsystemB : SubsystemB;

        constraint configurationConstraint {
            (subsystemA.config2 == null) or (subsystemB.config1 != null);
        }
    }

    part configurationA : Automobile {
        part :>> subsystemA {
            part :>> config1;
        }
        part :>> subsystemB {
            part :>> config1;
        }
    }

    part configurationB : Automobile {
        part :>> subsystemA {
            part :>> config2;
        }
        part :>> subsystemB {
            part :>> config1;
        }
    }
}

package VehicleSystem {

    part def Part1;
    part def Part2;
    part def Part3 {
        port p1;
    }
    part def Part4;
    part def Part5 {
        port p2;
    }
    part def Part6;

    abstract part def SubsystemA {
        abstract part components[*];
        abstract port subsystemPortA;
    }

    abstract part def SubsystemB {
        abstract part components[*];
        abstract port subsystemPortB;
    }

    part def SubsystemAVariant1 :> SubsystemA {
        part components[2] {
            part component1 : Part1;
            part component2 : Part3;
        }
    }

    part def SubsystemAVariant2 :> SubsystemA {
        part components[3] {
            part component1 : Part2;
            part component2 : Part4;
            part component3 : Part5;
        }
    }

    part def SubsystemBVariant1 :> SubsystemB {
        part components[2] {
            part component1 : Part5;
            part component2 : Part6;
        }
    }

    part def SubsystemBVariant2 :> SubsystemB {
        part components[3] {
            part component1 : Part3;
            part component2 : Part4;
            part component3 : Part6;
        }
    }

    abstract part def VehiclePlatform {
        abstract part subsystemA : SubsystemA;
        abstract part subsystemB : SubsystemB;

        abstract connection subsystemConnection connect subsystemA.subsystemPortA to subsystemB.subsystemPortB;
    }

    part def VehicleConfigA :> VehiclePlatform {
        part subsystemA : SubsystemAVariant1;
        part subsystemB : SubsystemBVariant1;
    }

    part def VehicleConfigB :> VehiclePlatform {
        part subsystemA : SubsystemAVariant2;
        part subsystemB : SubsystemBVariant2;
    }
}

