package VehicleManagementSystem {
    import ScalarValues::*;

    // Value types and units
    attribute def MassValue :> Real {
        attribute unit : String = "kg";
    }

    attribute def TorqueValue :> Real {
        attribute unit : String = "Nm";
    }

    // Enumerations
    enum def FuelStatus {
        enum fullFuel;
        enum emptyFuel;
    }

    enum def MassGrade {
        enum grade2000kg;
        enum grade2500kg;
    }

    // Part definitions
    part def Vehicle {
        attribute actualMass : MassValue;
        attribute maximumMass : MassValue;
        attribute fuelStatus : FuelStatus;
        attribute massGrade : MassGrade;

        part engine : Engine;
        part transmission : Transmission;

        interface driveInterface : DriveInterface connect engine to transmission;
    }

    part def Engine {
        attribute outputTorque : TorqueValue;

        port torqueOutput : TorquePort;
    }

    part def Transmission {
        port torqueInput : TorquePort;
    }

    // Port definitions
    port def TorquePort {
        attribute torque : TorqueValue;
    }

    // Interface definitions
    interface def DriveInterface {
        end engineEnd : Engine;
        end transmissionEnd : Transmission;
    }

    // Requirements
    requirement def VehicleMassRequirement {
        doc /* Vehicle shall not exceed maximum mass limit */

        attribute massLimit : MassValue;

        require constraint {
            doc /* Actual mass shall be less than or equal to maximum mass */
        }
    }

    requirement def FuelStatusRequirement {
        doc /* Vehicle mass requirements shall consider fuel status */

        attribute requiredFuelStatus : FuelStatus;
    }

    requirement def PowerDeliveryRequirement {
        doc /* Engine shall deliver torque to transmission via drive interface */

        attribute minimumTorque : TorqueValue;
    }

    requirement def ReliabilityRequirement {
        doc /* Vehicle shall maintain specified performance throughout lifecycle */

        attribute reliabilityTarget : Real;
    }

    // Requirement usages
    requirement vehicleMass2000kg : VehicleMassRequirement {
        :>> massLimit = 2000.0;
    }

    requirement vehicleMass2500kg : VehicleMassRequirement {
        :>> massLimit = 2500.0;
    }

    requirement fullFuelMassReq : FuelStatusRequirement {
        :>> requiredFuelStatus = FuelStatus::fullFuel;
    }

    requirement emptyFuelMassReq : FuelStatusRequirement {
        :>> requiredFuelStatus = FuelStatus::emptyFuel;
    }

    requirement powerDelivery : PowerDeliveryRequirement {
        :>> minimumTorque = 200.0;
    }

    requirement vehicleReliability : ReliabilityRequirement {
        :>> reliabilityTarget = 0.95;
    }

    // Allocations
    allocation vehicleMassAllocation : VehicleMassRequirement allocate to Vehicle;
    allocation powerDeliveryAllocation : PowerDeliveryRequirement allocate to Engine;
    allocation reliabilityAllocation : ReliabilityRequirement allocate to Vehicle;
}

package VehicleMassComplianceSystem {

    part def WeighingEquipment {
        attribute serialNumber : String;
        attribute accuracy : Real;
    }

    part def Vehicle {
        attribute vehicleID : String;
        attribute actualMass : Real;
    }

    part def TestOperator {
        attribute operatorID : String;
        attribute name : String;
    }

    part def ComplianceResult {
        attribute isCompliant : Boolean;
        attribute testedVehicleID : String;
        attribute measuredMass : Real;
        attribute maxPermissibleMass : Real;
    }

    part def VehicleMassComplianceVerificationSystem {
        attribute maxPermissibleMass : Real = 2500.0;

        part weighingEquipment : WeighingEquipment;
        part testOperator : TestOperator;
        part vehicles : Vehicle[*];
        part complianceResults : ComplianceResult[*];

        action measureVehicleMass {
            in vehicle : Vehicle;
            in operator : TestOperator;
            in equipment : WeighingEquipment;
            out measuredMass : Real;
        }

        action compareWithLimit {
            in measuredMass : Real;
            in maxLimit : Real;
            out isCompliant : Boolean {
                isCompliant = measuredMass <= maxLimit;
            }
        }

        action verifyCompliance {
            in vehicle : Vehicle;
            out result : ComplianceResult;

            perform measureVehicleMass {
                in vehicle = vehicle;
                in operator = testOperator;
                in equipment = weighingEquipment;
                out measuredMass;
            }
            then perform compareWithLimit {
                in measuredMass = measuredMass;
                in maxLimit = maxPermissibleMass;
                out isCompliant;
            }
            then assign result {
                result.isCompliant = isCompliant;
                result.testedVehicleID = vehicle.vehicleID;
                result.measuredMass = measuredMass;
                result.maxPermissibleMass = maxPermissibleMass;
            }
        }

        action performSequentialTesting {
            for vehicle in vehicles {
                perform verifyCompliance {
                    in vehicle = vehicle;
                    out result;
                }
                then assign complianceResults = complianceResults + result;
            }
        }
    }
}

