package FuelEconomySystem {
    import ScalarValues::*;

    attribute def MilesPerGallon :> Real {
        doc /* Fuel economy measured in miles per gallon */
    }

    attribute def Miles :> Real {
        doc /* Distance in miles */
    }

    attribute def Pounds :> Real {
        doc /* Weight in pounds */
    }

    attribute def CubicInches :> Real {
        doc /* Volume in cubic inches */
    }

    enum def DrivingCondition {
        urban;
        highway;
    }

    attribute def USGallon :> CubicInches {
        doc /* US customary gallon */
        attribute redefines num = 231;
    }

    requirement def FuelEconomyRequirement {
        doc /* Vehicle fuel economy requirements */

        attribute urbanMinimum : MilesPerGallon = 25;
        attribute highwayMinimum : MilesPerGallon = 30;
        attribute testLoad : Pounds = 1000;

        requirement urbanRequirement {
            doc /* Urban driving fuel economy requirement */
            require constraint {
                measuredUrbanFuelEconomy >= urbanMinimum
            }
        }

        requirement highwayRequirement {
            doc /* Highway driving fuel economy requirement */
            require constraint {
                measuredHighwayFuelEconomy >= highwayMinimum
            }
        }
    }

    part def Vehicle {
        attribute powertrain : String;
        attribute baseWeight : Pounds;
        attribute fuelCapacity : USGallon;

        port fuelInput;
        port powerOutput;
    }

    part def FuelEconomyTestSystem {
        part vehicle : Vehicle;

        attribute additionalLoad : Pounds = 1000;
        attribute drivingCondition : DrivingCondition;

        attribute measuredFuelEconomy : MilesPerGallon;

        action performFuelEconomyTest {
            in vehicle : Vehicle;
            in testCondition : DrivingCondition;
            in load : Pounds;
            out fuelEconomy : MilesPerGallon;
        }
    }

    part fuelEconomySystem {
        doc /* System to ensure vehicle fuel economy meets requirements */

        part testSystem : FuelEconomyTestSystem;
        part vehicleUnderTest : Vehicle;

        satisfy requirement : FuelEconomyRequirement;

        attribute measuredUrbanFuelEconomy : MilesPerGallon;
        attribute measuredHighwayFuelEconomy : MilesPerGallon;

        action testUrbanFuelEconomy {
            testSystem.performFuelEconomyTest(
                vehicleUnderTest,
                DrivingCondition::urban,
                1000
            );
            then measuredUrbanFuelEconomy := testSystem.measuredFuelEconomy;
        }

        action testHighwayFuelEconomy {
            testSystem.performFuelEconomyTest(
                vehicleUnderTest,
                DrivingCondition::highway,
                1000
            );
            then measuredHighwayFuelEconomy := testSystem.measuredFuelEconomy;
        }
    }
}

