package SecurityClassificationSystem {

    enum def SecurityLevel {
        enum Unclassified;
        enum Confidential;
        enum Secret;
    }

    attribute def SecurityClassification {
        attribute level : SecurityLevel;
    }

    part def Component {
        attribute classification : SecurityClassification;
    }

    part def ClassificationSystem {
        part components : Component[*];

        attribute def displayClassification(comp : Component) : SecurityLevel {
            return comp.classification.level;
        }
    }

    requirement def SecurityClassificationRequirement {
        doc /* The system is required to classify and label each component to meet information security management requirements */

        requirement classifyComponents {
            doc /* Every component must be clearly assigned a security classification level */
            subject components : Component[*];
        }

        requirement recognizeConfidential {
            doc /* When a component is marked as "Confidential," the system should be able to recognize and display its corresponding classification attribute */
            assume comp : Component;
            require constraint { comp.classification.level == SecurityLevel::Confidential }
        }
    }
}

