package VehiclePowertrainConfiguration {

    part def Vehicle {
        attribute isHighPerformance : Boolean;
        part engine : Engine[1];
        part transmission : Transmission[1];
    }

    abstract part def Engine;
    part def FourCylinderEngine :> Engine;
    part def SixCylinderEngine :> Engine;

    abstract part def Transmission;
    part def ManualTransmission :> Transmission;
    part def AutomaticTransmission :> Transmission;

    constraint def HighPerformanceRequiresV6 {
        doc /* If the vehicle is a high-performance model, it must be equipped with a six-cylinder engine */
        in vehicle : Vehicle;
        vehicle.isHighPerformance implies vehicle.engine istype SixCylinderEngine
    }

    constraint def StandardRequiresV4 {
        doc /* If the vehicle is not a high-performance model, a four-cylinder engine must be used */
        in vehicle : Vehicle;
        not vehicle.isHighPerformance implies vehicle.engine istype FourCylinderEngine
    }

    constraint def ValidPowertrainCombinations {
        doc /* Only two valid powertrain combinations are permitted */
        in vehicle : Vehicle;
        (vehicle.engine istype FourCylinderEngine and vehicle.transmission istype ManualTransmission) or
        (vehicle.engine istype SixCylinderEngine and vehicle.transmission istype AutomaticTransmission)
    }
}

