package PhysicalQuantityManagementSystem {

    import ISQ::*;
    import SI::*;

    part def PhysicalQuantitySystem {
        attribute systemName : String;
        attribute version : String;

        part quantities : PhysicalQuantity[1..*];
        part units : MeasurementUnit[1..*];
        part dataManager : DataManager;
        part unitConverter : UnitConverter;
    }

    abstract part def PhysicalQuantity {
        attribute name : String;
        attribute symbol : String;
        attribute dimension : String;
        attribute value : Real;
        attribute unit : MeasurementUnit;
    }

    part def BaseQuantity :> PhysicalQuantity {
        attribute isBase : Boolean = true;
    }

    part def DerivedQuantity :> PhysicalQuantity {
        attribute isBase : Boolean = false;
        attribute derivationFormula : String;
    }

    abstract part def MeasurementUnit {
        attribute name : String;
        attribute symbol : String;
        attribute quantity : PhysicalQuantity;
    }

    part def BaseUnit :> MeasurementUnit {
        attribute conversionFactor : Real = 1.0;
    }

    part def DerivedUnit :> MeasurementUnit {
        attribute baseUnits : BaseUnit[1..*];
        attribute exponents : Real[1..*];
    }

    part def Length :> BaseQuantity {
        attribute name redefines name = "length";
        attribute symbol redefines symbol = "L";
        attribute dimension redefines dimension = "L";
    }

    part def Mass :> BaseQuantity {
        attribute name redefines name = "mass";
        attribute symbol redefines symbol = "M";
        attribute dimension redefines dimension = "M";
    }

    part def Time :> BaseQuantity {
        attribute name redefines name = "time";
        attribute symbol redefines symbol = "T";
        attribute dimension redefines dimension = "T";
    }

    part def ElectricCurrent :> BaseQuantity {
        attribute name redefines name = "electric current";
        attribute symbol redefines symbol = "I";
        attribute dimension redefines dimension = "I";
    }

    part def Temperature :> BaseQuantity {
        attribute name redefines name = "temperature";
        attribute symbol redefines symbol = "";
        attribute dimension redefines dimension = "";
    }

    part def AmountOfSubstance :> BaseQuantity {
        attribute name redefines name = "amount of substance";
        attribute symbol redefines symbol = "N";
        attribute dimension redefines dimension = "N";
    }

    part def LuminousIntensity :> BaseQuantity {
        attribute name redefines name = "luminous intensity";
        attribute symbol redefines symbol = "J";
        attribute dimension redefines dimension = "J";
    }

    part def DataManager {
        attribute dataStore : PhysicalQuantity[0..*];

        action storeQuantity {
            in quantity : PhysicalQuantity;
            out success : Boolean;
        }

        action retrieveQuantity {
            in quantityName : String;
            out quantity : PhysicalQuantity;
        }

        action validateData {
            in quantity : PhysicalQuantity;
            out isValid : Boolean;
        }

        action ensureConsistency {
            out consistencyStatus : Boolean;
        }
    }

    part def UnitConverter {
        action convert {
            in sourceValue : Real;
            in sourceUnit : MeasurementUnit;
            in targetUnit : MeasurementUnit;
            out targetValue : Real;
        }

        action validateConversion {
            in sourceUnit : MeasurementUnit;
            in targetUnit : MeasurementUnit;
            out isConvertible : Boolean;
        }
    }

    part def ExtensionManager {
        action importQuantity {
            in externalQuantity : PhysicalQuantity;
            out importedQuantity : PhysicalQuantity;
        }

        action extendSystem {
            in newQuantity : PhysicalQuantity;
            in newUnit : MeasurementUnit;
            out success : Boolean;
        }
    }

    part physicalQuantitySystemInstance : PhysicalQuantitySystem {
        part lengthQuantity : Length;
        part massQuantity : Mass;
        part timeQuantity : Time;
        part currentQuantity : ElectricCurrent;
        part temperatureQuantity : Temperature;
        part substanceQuantity : AmountOfSubstance;
        part luminousQuantity : LuminousIntensity;

        part extensionManager : ExtensionManager;
    }
}

