package HeadlampTiltAdjustmentSystem {
    import ISQ::*;
    import SI::*;
    import ScalarValues::*;

    part def HeadlampTiltAdjustmentKnob {
        attribute tiltAngle : ScalarValues::Real;

        constraint tiltAngleRange {
            (tiltAngle >= 50.0) & (tiltAngle <= 80.0)
        }
    }

    part def HeadlampBeam {
        attribute beamTiltAngle : ScalarValues::Real;
        attribute unit : SI::deg = SI::deg;
    }

    part def TiltAdjustmentVerificationSystem {
        part knob : HeadlampTiltAdjustmentKnob;
        part beam : HeadlampBeam;

        attribute isValid : ScalarValues::Boolean;

        constraint validateTiltAngle {
            beam.beamTiltAngle == knob.tiltAngle
        }

        constraint checkCompliance {
            isValid == ((beam.beamTiltAngle >= 50.0) & (beam.beamTiltAngle <= 80.0))
        }
    }

    part tiltAdjustmentSystem : TiltAdjustmentVerificationSystem;
}

package DataTypes {
    datatype UnsignedInteger :> ScalarValues::Integer {
        constraint { self >= 0 }
    }

    datatype UnsignedFloat :> ScalarValues::Real {
        constraint { self >= 0.0 }
    }

    datatype DateTime :> ScalarValues::String;

    datatype String :> ScalarValues::String;

    datatype Boolean :> ScalarValues::Boolean;

    enum def StatusLevel {
        enum critical;
        enum warning;
        enum normal;
    }

    enum def ColorLabel {
        enum red;
        enum yellow;
        enum green;
    }

    attribute def StatusColor {
        attribute status : StatusLevel;
        attribute color : ColorLabel;

        constraint statusToColorMapping {
            (status == StatusLevel::critical implies color == ColorLabel::red) and
            (status == StatusLevel::warning implies color == ColorLabel::yellow) and
            (status == StatusLevel::normal implies color == ColorLabel::green)
        }
    }

    enum def DiameterSize {
        enum small;
        enum medium;
        enum large;
    }

    attribute def Diameter {
        attribute size : DiameterSize default DiameterSize::small;
        attribute value : UnsignedFloat;

        constraint sizeToValueMapping {
            (size == DiameterSize::small implies value == 60.0) and
            (size == DiameterSize::medium implies value == 70.0) and
            (size == DiameterSize::large implies value == 80.0)
        }
    }
}

