package AlloyManagementSystem {
    import ISQ::*;
    import SI::*;
    import ScalarValues::*;

    // Define tensile strength unit
    attribute def TensileStrengthUnit :> DerivedUnit {
        :>> symbol = "N/mm";
        :>> unitPowerFactor[0] {
            :>> unit = N;
            :>> exponent = 1;
        }
        :>> unitPowerFactor[1] {
            :>> unit = mm;
            :>> exponent = -2;
        }
    }

    // Base substance definition
    abstract item def Substance {
        attribute name : String;
        attribute atomicMass : Real;
    }

    // Material definition extending substance
    abstract item def Material :> Substance {
        attribute tensileStrength : Real;
    }

    // Metal definition
    item def Metal :> Material;

    // Alloy component definition
    item def AlloyComponent {
        ref material : Material;
        attribute massFraction : Real;
    }

    // Alloy definition
    item def Alloy :> Material {
        part components : AlloyComponent[2..*];

        constraint {
            sum(components.massFraction) == 1.0
        }
    }

    // Define specific metals
    item iron : Metal {
        :>> name = "Iron";
        :>> atomicMass = 55.845;
        :>> tensileStrength = 250.0;
    }

    item carbon : Metal {
        :>> name = "Carbon";
        :>> atomicMass = 12.011;
        :>> tensileStrength = 130.0;
    }

    item manganese : Metal {
        :>> name = "Manganese";
        :>> atomicMass = 54.938;
        :>> tensileStrength = 650.0;
    }

    // Define steel 980 alloy
    item steel980 : Alloy {
        :>> name = "Steel 980";
        :>> tensileStrength = 980.0;

        :>> components {
            item : AlloyComponent {
                :>> material = iron;
                :>> massFraction = 0.982;
            }
            item : AlloyComponent {
                :>> material = carbon;
                :>> massFraction = 0.008;
            }
            item : AlloyComponent {
                :>> material = manganese;
                :>> massFraction = 0.010;
            }
        }
    }
}

