package PubSubSystem {

    part def Message {
        attribute content : String;
        attribute timestamp : String;
        attribute topicName : String;
    }

    part def Topic {
        attribute name : String;
        attribute description : String;
    }

    part def Producer {
        port publishPort : PublishInterface;
    }

    part def Consumer {
        port subscribePort : SubscribeInterface;
        attribute subscribedTopics : String[*];
    }

    part def Server {
        port producerPort : PublishInterface;
        port consumerPort : SubscribeInterface;
        attribute topics : Topic[*];
        attribute subscriptions : Subscription[*];
    }

    part def Subscription {
        attribute topicName : String;
        attribute consumerId : String;
    }

    interface def PublishInterface {
        in item publishMessage : Message;
    }

    interface def SubscribeInterface {
        in item subscribeTopic : String;
        out item receiveMessage : Message;
    }

    part def PubSubSystemModel {
        part producers : Producer[*];
        part consumers : Consumer[*];
        part server : Server;

        interface : PublishInterface connect producers.publishPort to server.producerPort;
        interface : SubscribeInterface connect consumers.subscribePort to server.consumerPort;
    }
}

package VehicleFuelSystem {

    part def FuelTankAssembly {
        attribute fuelLevel : Real;
        attribute fuelTemperature : Real;

        port fuelSupplyPort : FuelFlow;
        port fuelReturnPort : ~FuelFlow;
    }

    part def Engine {
        attribute fuelConsumptionRate : Real;

        port fuelInletPort : ~FuelFlow;
        port fuelOutletPort : FuelFlow;
    }

    part def FuelLine {
        port inPort : ~FuelFlow;
        port outPort : FuelFlow;
    }

    interface def FuelFlow {
        attribute flowRate : Real;
        attribute temperature : Real;
    }

    part def Vehicle {
        part fuelTank : FuelTankAssembly;
        part engine : Engine;
        part supplyLine : FuelLine;
        part returnLine : FuelLine;

        interface supplyConnection : FuelFlow connect fuelTank.fuelSupplyPort to supplyLine.inPort;
        interface supplyToEngine : FuelFlow connect supplyLine.outPort to engine.fuelInletPort;
        interface returnFromEngine : FuelFlow connect engine.fuelOutletPort to returnLine.inPort;
        interface returnConnection : FuelFlow connect returnLine.outPort to fuelTank.fuelReturnPort;
    }
}

package PublishSubscribeSystem {

    part def Producer {
        port publishPort : PublishPort;
    }

    part def Server {
        port receivePublishPort : ~PublishPort;
        port receiveSubscribePort : ~SubscribePort;
        port deliverPort : DeliverPort;

        attribute topics : String[*];
        attribute subscriptions : Subscription[*];
    }

    part def Consumer {
        port subscribePort : SubscribePort;
        port receivePort : ~DeliverPort;
    }

    part def Subscription {
        attribute topic : String;
        attribute consumer : Consumer;
    }

    port def PublishPort {
        out message : Message;
    }

    port def SubscribePort {
        out subscriptionRequest : SubscriptionRequest;
    }

    port def DeliverPort {
        out message : Message;
    }

    item def Message {
        attribute topic : String;
        attribute content : String;
    }

    item def SubscriptionRequest {
        attribute topic : String;
    }

    part pubSubSystem : PublishSubscribeSystem {
        part producers : Producer[*];
        part server : Server;
        part consumers : Consumer[*];

        interface publishInterface : PublishPort connect producers.publishPort to server.receivePublishPort;
        interface subscribeInterface : SubscribePort connect consumers.subscribePort to server.receiveSubscribePort;
        interface deliverInterface : DeliverPort connect server.deliverPort to consumers.receivePort;
    }
}

package TransportationSystem {

    part def Vehicle {
        attribute fuelLevel : Real;
        attribute maxTankCapacity : Real;
        attribute currentPassengers : Integer;

        port driverPort : DriverInterface;
        port passengerPort : PassengerInterface[0..4];
        port fuelPort : FuelInterface;
    }

    part def Driver {
        port vehicleControl : DriverInterface;
    }

    part def Passenger {
        port vehicleAccess : PassengerInterface;
    }

    part def GasStation {
        port fuelSupply : FuelInterface;
    }

    part def Environment {
        attribute conditions : String;
    }

    interface def DriverInterface {
        in item command : String;
        out item status : String;
    }

    interface def PassengerInterface {
        in item boardingRequest : Boolean;
        out item boardingStatus : Boolean;
    }

    interface def FuelInterface {
        in item fuelRequest : Real;
        out item fuelDelivered : Real;
    }

    part transportationSystem : TransportationSystem {
        part vehicle : Vehicle;
        part driver : Driver;
        part passengers : Passenger[0..4];
        part gasStation : GasStation;
        part environment : Environment;

        connection : Connect(driver.vehicleControl, vehicle.driverPort);
        connection : Connect(passengers.vehicleAccess, vehicle.passengerPort);
        connection : Connect(vehicle.fuelPort, gasStation.fuelSupply);
    }

    action def EnterVehicle {
        in actor : Driver;
        in passengers : Passenger[0..*];
        out vehicle : Vehicle;
    }

    action def OperateVehicle {
        in driver : Driver;
        in vehicle : Vehicle;
        out travelCompleted : Boolean;
    }

    action def RefuelVehicle {
        in vehicle : Vehicle;
        in gasStation : GasStation;
        out fuelLevel : Real;
    }

    action def ExitVehicle {
        in vehicle : Vehicle;
        in driver : Driver;
        in passengers : Passenger[0..*];
    }

    action transportationProcess : Action {
        first start;

        then action enterVehicle : EnterVehicle {
            in actor = driver;
            in passengers = passengers;
            out vehicle = vehicle;
        }

        then action operateVehicle : OperateVehicle {
            in driver = driver;
            in vehicle = vehicle;
        }

        then decide fuelCheck {
            if vehicle.fuelLevel < vehicle.maxTankCapacity * 0.1 
                then refuel
                else continueTravel;
        }

        action refuel : RefuelVehicle {
            in vehicle = vehicle;
            in gasStation = gasStation;
        }
        then merge continueTravel;

        action continueTravel : Action;

        then action exitVehicle : ExitVehicle {
            in vehicle = vehicle;
            in driver = driver;
            in passengers = passengers;
        }

        then done;
    }
}

