package PhotoCaptureSystem {

    part def Camera {
        attribute focusDistance : Real;
        attribute aperture : Real;
        attribute shutterSpeed : Real;
    }

    part def Scene {
        attribute location : String;
        attribute lighting : Real;
        attribute distance : Real;
    }

    part def Image {
        attribute resolution : String;
        attribute format : String;
        attribute quality : Real;
    }

    part def User {
        attribute userId : String;
    }

    part def PhotoCaptureSystemDef {
        part user : User;
        part camera : Camera;
        part selectedScene : Scene;
        part focusedImage : Image;
        part finalPhoto : Image;

        action selectScene {
            in item userInput : Scene;
            out item selected : Scene;
        }

        action performAutoFocus {
            in item scene : Scene;
            out item focused : Image;

            bind scene = selectedScene;
            bind focused = focusedImage;
        }

        action capturePhoto {
            in item focusedImg : Image;
            out item photo : Image;

            bind focusedImg = focusedImage;
            bind photo = finalPhoto;
        }

        action obtainPhoto {
            first selectScene
                then performAutoFocus
                then capturePhoto;
        }
    }
}

package CameraSystem {

    part def Camera;
    part def Scene;
    part def Image;

    action def Focus {
        in scene : Scene;
        out focusedImage : Image;
    }

    action def Shoot {
        in focusedImage : Image;
        out finalImage : Image;
    }

    action def PhotographyWorkflow {
        in selectedScene : Scene;
        out photograph : Image;

        action focusing : Focus {
            in scene = selectedScene;
        }

        action shooting : Shoot {
            in focusedImage = focusing.focusedImage;
        }

        flow focusing.focusedImage to shooting.focusedImage;

        bind photograph = shooting.finalImage;
    }

    part cameraSystem : Camera {
        perform PhotographyWorkflow;
    }
}

package PhotographySystem {

    part def Camera;
    part def User;
    part def Scene;
    part def Image;
    part def Photograph;

    action def SelectScene {
        in user : User;
        in scene : Scene;
        out selectedScene : Scene;
    }

    action def Focus {
        in scene : Scene;
        out image : Image;
    }

    action def Shoot {
        in image : Image;
        out photograph : Photograph;
    }

    action def CaptureWorkflow {
        in user : User;
        in scene : Scene;
        out photograph : Photograph;

        action selectScene : SelectScene {
            in user = CaptureWorkflow::user;
            in scene = CaptureWorkflow::scene;
        }

        flow selectScene.selectedScene to focus.scene;

        action focus : Focus;

        flow focus.image to shoot.image;

        action shoot : Shoot;

        flow shoot.photograph to photograph;
    }

    part photographySystem : Camera {
        part user : User;
        part scene : Scene;

        perform CaptureWorkflow {
            in user = photographySystem::user;
            in scene = photographySystem::scene;
        }
    }
}

package AutomatedPhotographySystem {

    part def Scene;
    part def FocusedImage;
    part def Photograph;

    action def ProvideShooting {
        in scene : Scene;
    }

    action def PerformFocus {
        in scene : Scene;
        out focusedImage : FocusedImage;
    }

    action def ConvertToPhotograph {
        in focusedImage : FocusedImage;
        out photograph : Photograph;
    }

    action def AutomatePhotography {
        in inputScene : Scene;
        out finalPhotograph : Photograph;

        action provideShooting : ProvideShooting {
            in scene = inputScene;
        }

        action performFocus : PerformFocus {
            in scene = inputScene;
        }

        action convertToPhotograph : ConvertToPhotograph {
            in focusedImage = performFocus.focusedImage;
        }

        flow from provideShooting to performFocus;
        flow from performFocus.focusedImage to convertToPhotograph.focusedImage;

        bind finalPhotograph = convertToPhotograph.photograph;
    }

    part photographySystem {
        perform AutomatePhotography;
    }
}

package PhotographyWorkflow {
    part def Scene;
    part def Image;
    part def Picture;

    action def Focus {
        in scene : Scene;
        out image : Image;
    }

    action def Shoot {
        in image : Image;
        out picture : Picture;
    }

    action def PhotographyProcess {
        in inputScene : Scene;
        out outputPicture : Picture;

        action focus : Focus {
            in scene = inputScene;
        }

        action shoot : Shoot {
            in image = focus.image;
        }

        bind outputPicture = shoot.picture;
    }
}

package PhotographySystem {

    part def Camera {
        attribute isFocused : Boolean = false;

        action def focusScene {
            in scene : Scene;
            out focusResult : Boolean;
        }

        action def capturePhoto {
            in scene : Scene;
            out photograph : Photograph;
        }
    }

    item def Scene;
    item def Photograph;

    part def PhotographyWorkflow {
        part camera : Camera;

        action shootingProcess {
            in selectedScene : Scene;
            out finalImage : Photograph;

            action focus : camera.focusScene {
                in scene = selectedScene;
            }

            action capture : camera.capturePhoto {
                in scene = selectedScene;
            }

            flow focus.focusResult to decide;

            action decide {
                in focusStatus : Boolean;

                if focusStatus {
                    then capture;
                }
            }

            flow capture.photograph to finalImage;

            first focus then decide;
        }
    }
}

package CameraSystem {

    part def Camera;
    part def User;
    part def Scene;
    part def Image;
    part def Photo;

    attribute def FocusQuality;

    action def SelectScene {
        in user : User;
        in scene : Scene;
        out selectedScene : Scene;
    }

    action def PerformFocusing {
        in scene : Scene;
        out focusedImage : Image;
    }

    action def EvaluateFocusQuality {
        in image : Image;
        out quality : FocusQuality;
        out isWellFocused : Boolean;
    }

    action def ConvertToPhoto {
        in focusedImage : Image;
        out photo : Photo;
    }

    action def CapturePhoto {
        in scene : Scene;
        out photo : Photo;

        action selectScene : SelectScene {
            in user = CapturePhoto::user;
            in scene = CapturePhoto::scene;
        }

        then action focus : PerformFocusing {
            in scene = selectScene.selectedScene;
        }

        then action evaluate : EvaluateFocusQuality {
            in image = focus.focusedImage;
        }

        then action shoot : ConvertToPhoto {
            in focusedImage = focus.focusedImage;
        } if evaluate.isWellFocused;

        bind photo = shoot.photo;
    }

    part cameraSystem : Camera {
        part user : User;

        perform action photographyWorkflow : CapturePhoto {
            in user = cameraSystem.user;
        }
    }
}

package CameraSystem {

    part def Camera {
        attribute currentScene : String;
        attribute focusedImage : String;
        attribute finalPhoto : String;

        action takePhoto {
            first start;
            then acquireScene;
            then focus;
            then shoot;
            then display;
            then done;
        }

        action acquireScene {
            out scene : String;
        }

        action focus {
            in scene : String;
            out image : String;
        }

        action shoot {
            in image : String;
            out photo : String;
        }

        action display {
            in photo : String;
        }

        flow from acquireScene.scene to focus.scene;
        flow from focus.image to shoot.image;
        flow from shoot.photo to display.photo;
    }

    part cameraInstance : Camera;
}

package AutomaticBrakingControlSystem {

    part def Vehicle;
    part def BrakingControlSystem;
    part def BrakePedal;
    part def BrakePressureSensor;
    part def TractionSensor;
    part def BrakeActuator;
    part def IgnitionKey;

    attribute def BrakePressure : Real;
    attribute def TractionLevel : Real;
    attribute def ModulationFrequency : Real;
    attribute def KeyPosition : String;
    attribute def SystemStatus : String;

    part vehicle : Vehicle {
        part brakingSystem : BrakingControlSystem {
            attribute status : SystemStatus;
            attribute currentBrakePressure : BrakePressure;
            attribute currentModulationFreq : ModulationFrequency;

            part brakePedal : BrakePedal {
                attribute pedalPressure : BrakePressure;
            }

            part pressureSensor : BrakePressureSensor {
                attribute detectedPressure : BrakePressure;
            }

            part tractionSensor : TractionSensor {
                attribute roadAdhesion : TractionLevel;
            }

            part brakeActuator : BrakeActuator {
                attribute appliedPressure : BrakePressure;
                attribute modulationFreq : ModulationFrequency;
            }
        }

        part ignitionKey : IgnitionKey {
            attribute position : KeyPosition;
        }
    }

    action def MonitorBrakePedal {
        in sensorInput : BrakePressure;
        out detectedPressure : BrakePressure;
    }

    action def MonitorTraction {
        in roadCondition : TractionLevel;
        out tractionData : TractionLevel;
    }

    action def AdjustBrakePressure {
        in currentPressure : BrakePressure;
        in traction : TractionLevel;
        out adjustedPressure : BrakePressure;
        out frequency : ModulationFrequency;
    }

    action def ActivateSystem {
        in keyPos : KeyPosition;
        out systemActive : SystemStatus;
    }

    action automaticBrakingControl {
        first start;
        then activate : ActivateSystem {
            in keyPos = vehicle.ignitionKey.position;
            out systemActive = vehicle.brakingSystem.status;
        }
        then loop {
            action monitorPedal : MonitorBrakePedal {
                in sensorInput = vehicle.brakingSystem.brakePedal.pedalPressure;
                out detectedPressure = vehicle.brakingSystem.pressureSensor.detectedPressure;
            }
            then monitorTraction : MonitorTraction {
                in roadCondition = vehicle.brakingSystem.tractionSensor.roadAdhesion;
                out tractionData;
            }
            then adjustBrake : AdjustBrakePressure {
                in currentPressure = vehicle.brakingSystem.pressureSensor.detectedPressure;
                in traction = monitorTraction.tractionData;
                out adjustedPressure = vehicle.brakingSystem.brakeActuator.appliedPressure;
                out frequency = vehicle.brakingSystem.brakeActuator.modulationFreq;
            }
        }
        then done;
    }
}

package BatteryChargingControlSystem {

    part def BatteryChargingController {
        attribute batteryLevel : Real;
        attribute isCharging : Boolean;

        port batteryMonitor : BatteryMonitorPort;
        port chargerControl : ChargerControlPort;
    }

    part def Battery {
        attribute currentLevel : Real;
        attribute maxCapacity : Real = 100.0;

        port levelOutput : BatteryLevelPort;
        port chargeInput : ChargingPort;
    }

    part def Charger {
        attribute chargingEnabled : Boolean;
        attribute chargingCurrent : Real;

        port controlInput : ChargerControlPort;
        port powerOutput : ChargingPort;
    }

    port def BatteryMonitorPort {
        out batteryLevel : Real;
    }

    port def ChargerControlPort {
        in enableCharging : Boolean;
    }

    port def BatteryLevelPort {
        out level : Real;
    }

    port def ChargingPort {
        in chargingPower : Real;
    }

    part batteryChargingSystem : BatteryChargingController {
        part battery : Battery;
        part charger : Charger;

        connect battery.levelOutput to batteryMonitor;
        connect chargerControl to charger.controlInput;
        connect charger.powerOutput to battery.chargeInput;

        state idle {
            entry action {
                isCharging = false;
            }

            transition to charging
                when batteryLevel < 100.0;
        }

        state charging {
            entry action {
                isCharging = true;
            }

            transition to idle
