package BatteryChargingSystem {

    part def Battery {
        attribute batteryLevel : Real;
    }

    part def ChargingController {
        attribute isCharging : Boolean;
    }

    part def ChargingSystem {
        part battery : Battery;
        part controller : ChargingController;

        action def MonitorBatteryLevel {
            in batteryRef : Battery;
            out currentLevel : Real;
        }

        action def ReplenishBattery {
            inout batteryRef : Battery;
        }

        action def StartCharging {
            inout controllerRef : ChargingController;
        }

        action def StopCharging {
            inout controllerRef : ChargingController;
        }

        action chargingProcess {
            first start;

            action start : StartCharging {
                inout controllerRef = controller;
            }

            then loop;

            action loop : MonitorBatteryLevel {
                in batteryRef = battery;
                out currentLevel;
            }

            then decide;

            action decide {
                if currentLevel < 100.0 then charge
                else stop
            }

            action charge : ReplenishBattery {
                inout batteryRef = battery;
            }
            then loop;

            action stop : StopCharging {
                inout controllerRef = controller;
            }
            then done;
        }
    }
}

