package VehicleMotionSimulation {
    import ScalarValues::*;

    part def Vehicle {
        attribute mass : Real;
        attribute position : Real;
        attribute velocity : Real;
    }

    part def SimulationParameters {
        attribute timeStep : Real;
        attribute powerInputSequence : Real[*];
    }

    part def SimulationResults {
        attribute positionTrajectory : Real[*];
    }

    part def VehicleMotionSimulator {
        part vehicle : Vehicle;
        part parameters : SimulationParameters;
        part results : SimulationResults;

        attribute currentTime : Real;
        attribute currentPowerInput : Real;
        attribute currentForce : Real;
        attribute currentAcceleration : Real;

        action simulateMotion {
            in vehicleMass : Real;
            in initialPosition : Real;
            in initialVelocity : Real;
            in timeInterval : Real;
            in powerSequence : Real[*];
            out trajectoryOutput : Real[*];

            bind vehicle.mass = vehicleMass;
            bind vehicle.position = initialPosition;
            bind vehicle.velocity = initialVelocity;
            bind parameters.timeStep = timeInterval;
            bind parameters.powerInputSequence = powerSequence;

            action calculateTrajectory {
                for i in 1..size(powerSequence) {
                    then action updateMotion {
                        bind currentPowerInput = powerSequence[i];
                        bind currentForce = currentPowerInput / vehicle.velocity;
                        bind currentAcceleration = currentForce / vehicle.mass;
                        bind vehicle.velocity = vehicle.velocity + currentAcceleration * parameters.timeStep;
                        bind vehicle.position = vehicle.position + vehicle.velocity * parameters.timeStep;
                        bind results.positionTrajectory[i] = vehicle.position;
                    }
                }
            }

            bind trajectoryOutput = results.positionTrajectory;
        }
    }
}

