package SensorManagementSystem {

    part def Sensor {
        attribute id : String;
        attribute status : SensorStatus;
        attribute lastUpdateTime : String;
    }

    enum def SensorStatus {
        enum ready;
        enum busy;
        enum offline;
        enum updating;
    }

    part def SensorManager {
        part sensors : Sensor[0..*];

        action def monitorSensors {
            in sensorList : Sensor[0..*];
            out readySensors : Sensor[0..*];
        }

        action def updateSensor {
            in sensor : Sensor;
            out updatedSensor : Sensor;
        }

        action def traverseSensors {
            in allSensors : Sensor[0..*];
            out processedSensors : Sensor[0..*];
        }
    }

    part sensorManagementSystem : SensorManager {

        action performMonitoring : monitorSensors {
            in sensorList = sensors;
        }

        action performUpdate : updateSensor;

        action performTraversal : traverseSensors {
            in allSensors = sensors;
        }

        state sensorStates {
            entry state idle;
            state monitoring;
            state updating;

            transition idle_to_monitoring
                first idle
                then monitoring;

            transition monitoring_to_updating
                first monitoring
                then updating
                when sensors->exists(s | s.status == SensorStatus::ready);

            transition updating_to_monitoring
                first updating
                then monitoring;
        }
    }
}

