package VehicleHealthMonitoringSystem {

    part def VehicleHealthMonitor {
        attribute currentTemperature : Real;
        attribute maxAllowableTemperature : Real;
        attribute maintenanceInterval : Real;
        attribute lastMaintenanceTime : Real;
        attribute nextMaintenanceTime : Real;

        port temperatureSensorPort : ~TemperatureSensorInterface;
        port controllerPort : ~VehicleControllerInterface;
    }

    part def TemperatureSensor {
        attribute temperature : Real;
        port sensorOutput : TemperatureSensorInterface;
    }

    part def VehicleController {
        attribute warningReceived : Boolean;
        port controllerInput : VehicleControllerInterface;
    }

    interface def TemperatureSensorInterface {
        end supplierPort;
        end consumerPort;
    }

    interface def VehicleControllerInterface {
        end supplierPort;
        end consumerPort;
    }

    state def VehicleHealthStates {
        entry state;
        state normal;
        state degraded;
        state maintenance;

        transition initialToNormal
            first entry
            then normal;

        transition normalToDegraded
            first normal
            accept overTemperatureEvent
            then degraded;

        transition degradedToNormal
            first degraded
            accept temperatureNormalEvent
            then normal;

        transition normalToMaintenance
            first normal
            accept maintenanceTimeReachedEvent
            then maintenance;

        transition maintenanceToNormal
            first maintenance
            accept maintenanceCompleteEvent
            then normal;
    }

    event overTemperatureEvent;
    event temperatureNormalEvent;
    event maintenanceTimeReachedEvent;
    event maintenanceCompleteEvent;

    action def monitorTemperature {
        in currentTemp : Real;
        in maxTemp : Real;
        out isOverTemp : Boolean;

        action compareTemperature {
            isOverTemp := currentTemp > maxTemp;
        }
    }

    action def sendOverTemperatureWarning {
        out warning : Boolean;
        warning := true;
    }

    action def checkMaintenanceSchedule {
        in currentTime : Real;
        in nextMaintTime : Real;
        out maintenanceNeeded : Boolean;

        maintenanceNeeded := currentTime >= nextMaintTime;
    }

    action def updateMaintenanceSchedule {
        in maintInterval : Real;
        in currentTime : Real;
        out nextMaintTime : Real;

        nextMaintTime := currentTime + maintInterval;
    }

    constraint maintenanceDuration {
        doc /* Maintenance status lasts for 48 hours */
    }

    part vehicleHealthSystem : VehicleHealthMonitor {
        exhibit state vehicleStates : VehicleHealthStates;

        part tempSensor : TemperatureSensor;
        part controller : VehicleController;

        interface : TemperatureSensorInterface connect 
            tempSensor.sensorOutput to vehicleHealthSystem.temperatureSensorPort;

        interface : VehicleControllerInterface connect
            vehicleHealthSystem.controllerPort to controller.controllerInput;
    }
}

package ServerSystem {
    import ScalarValues::*;
    import ISQ::*;
    import Time::*;

    part def Server {
        attribute localClock : Time;
        port requestPort;

        exhibit state serverBehavior {
            entry; then off;

            state off {
                transition startTransition
                    first off
                    accept start via requestPort
                    then waiting;
            }

            state waiting {
                transition requestTransition
                    first waiting
                    accept request via requestPort
                    then response;

                transition dailyResetTransition
                    first waiting
                    when localClock == Time(11, 59, 0)
                    then off;
            }

            state response {
                transition timeoutTransition
                    first response
                    after 5 [min]
                    then waiting;

                transition dailyResetFromResponse
                    first response
                    when localClock == Time(11, 59, 0)
                    then off;
            }
        }
    }

    part serverInstance : Server;
}

package VehicleStartStopSystem {

    part def Vehicle {
        attribute isOff : Boolean = true;
        attribute isStarting : Boolean = false;
        attribute isOperating : Boolean = false;
        attribute brakePedalDepressed : Boolean = false;
        attribute parkingBrakeApplied : Boolean = true;
    }

    part def Controller {
        attribute startSignalReceived : Boolean = false;
    }

    part def StartStopControlSystem {
        part vehicle : Vehicle;
        part controller : Controller;

        action def detectBrakePedal {
            in brakePedalStatus : Boolean;
            out brakePedalDepressed : Boolean;
        }

        action def sendStartSignalToController {
            out startSignal : Boolean;
        }

        action def performSelfCheck {
            out checkResult : Boolean;
        }

        action def supplyPower {
            out powerSupplied : Boolean;
        }

        action def applyParkingBrake {
            out parkingBrakeApplied : Boolean;
        }

        state def VehicleStates {
            entry state off;
            state starting;
            state operating;

            transition off_to_starting {
                source off;
                target starting;
                guard startSignalReceived;
            }

            transition starting_to_operating {
                source starting;
                target operating;
                guard brakePedalDepressed and powerOnSignalReceived;
                effect sendStartSignalToController;
            }

            transition operating_to_off {
                source operating;
                target off;
                guard stopSignalReceived;
                effect applyParkingBrake;
            }
        }

        action controlVehicleStartStop {
            in startSignal : Boolean;
            in powerOnSignal : Boolean;
            in stopSignal : Boolean;

            bind detectBrakePedal.brakePedalStatus = vehicle.brakePedalDepressed;
            bind sendStartSignalToController.startSignal = controller.startSignalReceived;

            first start;
            then merge continueOperation;

            action start {
                vehicle.isOff := true;
                vehicle.parkingBrakeApplied := true;
            }

            action processStartSignal {
                in startSignal : Boolean;
                if (startSignal and vehicle.isOff) {
                    vehicle.isOff := false;
                    vehicle.isStarting := true;
                }
            }

            action processStartup {
                in powerOnSignal : Boolean;
                perform detectBrakePedal;
                if (vehicle.isStarting and vehicle.brakePedalDepressed and powerOnSignal) {
                    perform sendStartSignalToController;
                    vehicle.isStarting := false;
                    vehicle.isOperating := true;
                    vehicle.parkingBrakeApplied := false;
                }
            }

            action operatingMode {
                if (vehicle.isOperating) {
                    perform performSelfCheck;
                    perform supplyPower;
                }
            }

            action processStopSignal {
                in stopSignal : Boolean;
                if (stopSignal and vehicle.isOperating) {
                    perform applyParkingBrake;
                    vehicle.isOperating := false;
                    vehicle.isOff := true;
                }
            }

            flow {
                first processStartSignal;
                then processStartup;
                then operatingMode;
                then processStopSignal;
                then continueOperation;
            }
        }
    }
}

package VehicleStartStopSystem {

    part def VehicleController;
    part def BrakePedal;
    part def ParkingBrake;

    part def VehicleStartStopControlSystem {
        port startSignalPort;
        port vehicleOnSignalPort;
        port shutdownSignalPort;
        port brakeStatusPort;
        port startControlPort;
        port parkingBrakeControlPort;
        port powerSupplyPort;

        attribute vehicleState : String;
        attribute isBrakeDepressed : Boolean;
        attribute isSelfCheckComplete : Boolean;
    }

    part vehicleStartStopSystem : VehicleStartStopControlSystem {
        part vehicleController : VehicleController;
        part brakePedal : BrakePedal;
        part parkingBrake : ParkingBrake;

        state off {
            entry action {
                vehicleState := "off";
            }

            transition startupTransition
                first off
                accept startSignalPort
                then startup;
        }

        state startup {
            entry action {
                vehicleState := "startup";
            }

            transition toStarted
                first startup
                if isBrakeDepressed and vehicleOnSignalPort
                do send startControlPort
                then started;
        }

        state started {
            entry action {
                vehicleState := "started";
                send powerSupplyPort;
            }

            do action selfCheck {
                isSelfCheckComplete := true;
            }

            transition toOff
                first started
                accept shutdownSignalPort
                then shuttingDown;
        }

        state shuttingDown {
            entry action {
                send parkingBrakeControlPort;
                vehicleState := "shuttingDown";
            }

            transition finalShutdown
                first shuttingDown
                then off;
        }
    }
}

