package VehicleMassModel {
    import ScalarValues::*;
    import Quantities::*;
    import MeasurementReferences::*;
    import SI::*;

    attribute def MassValue :> ScalarQuantityValue {
        attribute quantityDimension :>> num = 1;
        attribute measurementReference :>> mRef = kg;
    }

    abstract part def Component {
        attribute id : String;
        attribute mass : MassValue;
    }

    abstract part def CompositeComponent :> Component {
        part subComponents : Component[0..*];

        attribute totalMass : MassValue = mass + sum(subComponents.totalMass);
    }

    part def SimpleComponent :> Component {
        attribute totalMass : MassValue = mass;
    }

