        part engine : Engine;
        part transmission : Transmission;
        part body : Body;

        bind subComponents = (engine, transmission, body);
    }
}

package VehicleMassManagementSystem {

    attribute def MassValue :> ISQ::mass;
    attribute def SerialNumber :> String;
    attribute def VIN :> String;

    part def Component {
        attribute serialNumber : SerialNumber;
        attribute mass : MassValue;
        part subComponents : Component[0..*];

        attribute totalMass : MassValue = mass + subComponents.totalMass->sum();

        attribute def filterByMass(threshold : MassValue) : Component[0..*] = 
            subComponents->select(c | c.totalMass >= threshold);
    }

    part def Engine :> Component {
        attribute engineType : String;
    }

    part def Transmission :> Component {
        attribute transmissionType : String;
    }

    part def Vehicle :> Component {
        attribute vin : VIN redefines serialNumber;

        part engine : Engine[1];
        part transmission : Transmission[1];
        part otherComponents : Component[0..*];

        attribute redefines totalMass : MassValue = 
            mass + engine.totalMass + transmission.totalMass + otherComponents.totalMass->sum();
    }

    part vehicleMassManagementSystem {
        part vehicles : Vehicle[0..*];

        attribute calculateTotalMass(v : Vehicle) : MassValue = v.totalMass;

        attribute filterComponentsByMass(v : Vehicle, threshold : MassValue) : Component[0..*] =
            v.filterByMass(threshold);
    }
}

