package MassManagementSystem {

    attribute def MassValue :> ISQ::mass;

    attribute def MinimumMassThreshold :> ISQ::mass;

    part def MassObject {
        attribute basicMass : MassValue;
        part subcomponents : MassObject[0..*];

        attribute totalMass : MassValue;

        calc def calculateTotalMass {
            in object : MassObject;
            return : MassValue;
        }

        calc totalMassCalculation : calculateTotalMass {
            in object = MassObject::self;
            return = object.basicMass + sum(object.subcomponents.totalMass);
        }

        bind totalMass = totalMassCalculation.return;
    }

    part def FilteredMassObject :> MassObject {
        attribute massThreshold : MinimumMassThreshold;

        calc def calculateFilteredTotalMass {
            in object : FilteredMassObject;
            in threshold : MinimumMassThreshold;
            return : MassValue;
        }

        calc filteredTotalMassCalculation : calculateFilteredTotalMass {
            in object = FilteredMassObject::self;
            in threshold = massThreshold;
            return = object.basicMass + sum(
                object.subcomponents
                    .select(sc : MassObject | sc.totalMass >= threshold)
                    .totalMass
            );
        }

        attribute filteredTotalMass : MassValue;
        bind filteredTotalMass = filteredTotalMassCalculation.return;
    }

    part def MassManagementSystem {
        part managedObjects : MassObject[0..*];
        part filteredObjects : FilteredMassObject[0..*];

        action aggregateMass {
            in objects : MassObject[0..*];
            out totalSystemMass : MassValue;

            bind totalSystemMass = sum(objects.totalMass);
        }

        action filterAndAggregateMass {
            in objects : FilteredMassObject[0..*];
            in threshold : MinimumMassThreshold;
            out filteredSystemMass : MassValue;

            bind filteredSystemMass = sum(objects.filteredTotalMass);
        }
    }
}

package VehicleDynamicsSystem {
    import ScalarValues::*;

    part def VehicleDynamicsCalculator {
        attribute wheelPower : Real;
        attribute aerodynamicDragCoefficient : Real;
        attribute frictionCoefficient : Real;
        attribute vehicleMass : Real;
        attribute currentSpeed : Real;
        attribute initialSpeed : Real;
        attribute time : Real;

        attribute actualAvailablePower : Real;
        attribute acceleration : Real;
        attribute speed : Real;
        attribute position : Real;

        calc actualAvailablePower : Real = wheelPower - (aerodynamicDragCoefficient * currentSpeed * currentSpeed * currentSpeed) - (frictionCoefficient * vehicleMass * 9.81 * currentSpeed);

        calc acceleration : Real = actualAvailablePower / (vehicleMass * currentSpeed);

        calc speed : Real = initialSpeed + (acceleration * time);

        calc position : Real = initialSpeed * time + (0.5 * acceleration * time * time);
    }

    part vehicleDynamicsSystem : VehicleDynamicsCalculator;
}

package VehicleDynamicsSimulation {
    import ScalarValues::*;

    part def Vehicle {
        attribute mass : Real;
        attribute dragCoefficient : Real;
        attribute rollingResistanceCoefficient : Real;
        attribute frontalArea : Real;
        attribute wheelRadius : Real;
    }

    part def DrivingConditions {
        attribute initialSpeed : Real;
        attribute initialPosition : Real;
        attribute timeStep : Real;
        attribute wheelEndPower : Real;
        attribute airDensity : Real;
        attribute gravitationalAcceleration : Real;
    }

    part def DynamicState {
        attribute currentSpeed : Real;
        attribute currentPosition : Real;
        attribute currentAcceleration : Real;
        attribute aerodynamicDrag : Real;
        attribute rollingResistance : Real;
        attribute availableDrivingForce : Real;
    }

    part def VehicleDynamicsSystem {
        part vehicle : Vehicle;
        part conditions : DrivingConditions;
        part state : DynamicState;

        action computeAerodynamicDrag {
            in vehicleData : Vehicle;
            in currentSpeed : Real;
            in airDensity : Real;
            out dragForce : Real;
        }

        action computeRollingResistance {
            in vehicleData : Vehicle;
            in gravitationalAcceleration : Real;
            out resistanceForce : Real;
        }

        action computeAvailableDrivingForce {
            in wheelEndPower : Real;
            in currentSpeed : Real;
            out drivingForce : Real;
        }

        action computeAcceleration {
            in availableForce : Real;
            in dragForce : Real;
            in resistanceForce : Real;
            in mass : Real;
            out acceleration : Real;
        }

        action updateSpeed {
            in currentSpeed : Real;
            in acceleration : Real;
            in timeStep : Real;
            out newSpeed : Real;
        }

        action updatePosition {
            in currentPosition : Real;
            in currentSpeed : Real;
            in acceleration : Real;
            in timeStep : Real;
            out newPosition : Real;
        }

        action simulateTimeStep {
            in vehicle : Vehicle;
            in conditions : DrivingConditions;
            inout state : DynamicState;

            perform computeAerodynamicDrag {
                in vehicleData = vehicle;
                in currentSpeed = state.currentSpeed;
                in airDensity = conditions.airDensity;
                out dragForce = state.aerodynamicDrag;
            }

            perform computeRollingResistance {
                in vehicleData = vehicle;
                in gravitationalAcceleration = conditions.gravitationalAcceleration;
                out resistanceForce = state.rollingResistance;
            }

            perform computeAvailableDrivingForce {
                in wheelEndPower = conditions.wheelEndPower;
                in currentSpeed = state.currentSpeed;
                out drivingForce = state.availableDrivingForce;
            }

            perform computeAcceleration {
                in availableForce = state.availableDrivingForce;
                in dragForce = state.aerodynamicDrag;
                in resistanceForce = state.rollingResistance;
                in mass = vehicle.mass;
                out acceleration = state.currentAcceleration;
            }

            perform updateSpeed {
                in currentSpeed = state.currentSpeed;
                in acceleration = state.currentAcceleration;
                in timeStep = conditions.timeStep;
                out newSpeed = state.currentSpeed;
            }

            perform updatePosition {
                in currentPosition = state.currentPosition;
                in currentSpeed = state.currentSpeed;
                in acceleration = state.currentAcceleration;
                in timeStep = conditions.timeStep;
                out newPosition = state.currentPosition;
            }
        }
    }
}

package VehicleDynamicsSystem {
    import SI::*;
    import ScalarValues::*;

    part def Vehicle {
        attribute mass : Real;
        attribute wheelPower : Real;
        attribute dragCoefficient : Real;
        attribute rollingResistanceCoefficient : Real;
        attribute frontalArea : Real;
        attribute currentSpeed : Real;
        attribute currentPosition : Real;
        attribute currentAcceleration : Real;
        attribute timeStep : Real;
    }

    part def DynamicsCalculator {
        in vehicleData : Vehicle;
        out updatedVehicle : Vehicle;

        private attribute airDensity : Real = 1.225;
        private attribute gravity : Real = 9.81;

        action calculateForces {
            in vehicle : Vehicle;
            out drivingForce : Real;
            out dragForce : Real;
            out rollingResistance : Real;
            out netForce : Real;

            drivingForce := vehicle.wheelPower / vehicle.currentSpeed;
            dragForce := 0.5 * airDensity * vehicle.dragCoefficient * vehicle.frontalArea * vehicle.currentSpeed * vehicle.currentSpeed;
            rollingResistance := vehicle.rollingResistanceCoefficient * vehicle.mass * gravity;
            netForce := drivingForce - dragForce - rollingResistance;
        }

        action updateDynamics {
            in vehicle : Vehicle;
            in netForce : Real;
            out newAcceleration : Real;
            out newSpeed : Real;
            out newPosition : Real;

            newAcceleration := netForce / vehicle.mass;
            newSpeed := vehicle.currentSpeed + newAcceleration * vehicle.timeStep;
            newPosition := vehicle.currentPosition + vehicle.currentSpeed * vehicle.timeStep + 0.5 * newAcceleration * vehicle.timeStep * vehicle.timeStep;
        }
    }

    part def SimulationController {
        attribute simulationTime : Real;
        attribute maxSimulationTime : Real;

        part vehicle : Vehicle;
        part calculator : DynamicsCalculator;

        action runSimulation {
            loop while (simulationTime < maxSimulationTime) {
                perform calculator.calculateForces {
                    in vehicle := self.vehicle;
                    out drivingForce;
                    out dragForce;
                    out rollingResistance;
                    out netForce;
                }

                perform calculator.updateDynamics {
                    in vehicle := self.vehicle;
                    in netForce := netForce;
                    out newAcceleration;
                    out newSpeed;
                    out newPosition;
                }

                self.vehicle.currentAcceleration := newAcceleration;
                self.vehicle.currentSpeed := newSpeed;
                self.vehicle.currentPosition := newPosition;
                self.simulationTime := self.simulationTime + self.vehicle.timeStep;
            }
        }
    }

    part vehicleDynamicsSystem : SimulationController {
        doc /* This system models and computes vehicle driving dynamics based on user input parameters */
    }
}

package VehicleLongitudinalDynamics {
    import ScalarValues::*;

    part def Vehicle {
        attribute mass : Real;
        attribute dragCoefficient : Real;
        attribute frictionCoefficient : Real;
        attribute frontalArea : Real;
        attribute airDensity : Real;
    }

    part def DynamicState {
        attribute position : Real;
        attribute velocity : Real;
        attribute acceleration : Real;
        attribute time : Real;
    }

    part def DrivingConditions {
        attribute wheelPower : Real;
        attribute timeInterval : Real;
    }

    part def LongitudinalDynamicsSystem {
        part vehicle : Vehicle;
        part initialState : DynamicState;
        part finalState : DynamicState;
        part conditions : DrivingConditions;

        attribute dragForce : Real = 0.5 * vehicle.airDensity * vehicle.dragCoefficient * vehicle.frontalArea * initialState.velocity ** 2;
        attribute frictionForce : Real = vehicle.frictionCoefficient * vehicle.mass * 9.81;
        attribute tractionForce : Real = conditions.wheelPower / initialState.velocity;
        attribute netForce : Real = tractionForce - dragForce - frictionForce;

        constraint accelerationCalculation {
            finalState.acceleration == netForce / vehicle.mass
        }

        constraint velocityUpdate {
            finalState.velocity == initialState.velocity + finalState.acceleration * conditions.timeInterval
        }

        constraint positionUpdate {
            finalState.position == initialState.position + initialState.velocity * conditions.timeInterval + 0.5 * finalState.acceleration * conditions.timeInterval ** 2
        }

        constraint timeUpdate {
            finalState.time == initialState.time + conditions.timeInterval
        }
    }
}

