        attribute maxMassLimit : Real;

        part engine : Engine;
        part transmission : Transmission;
        part clutch : Clutch;

        constraint totalMassCalculation {
            totalMass == curbWeight + fuelMass
        }

        constraint massLimit {
            totalMass <= maxMassLimit
        }

        constraint fuelMassPositive {
            fuelMass > 0
        }

        interface clutchInterface connect engine.torqueOutput to clutch.torqueInput;
        interface transmissionInterface connect clutch.torqueOutput to transmission.torqueInput;
    }

    part def Engine {
        attribute speed : Real;
        attribute torque : Real;
        port torqueOutput : TorquePort;

        action generateTorque {
            in engineSpeed : Real;
            out engineTorque : Real;
        }
    }

    part def Clutch {
        port torqueInput : TorquePort;
        port torqueOutput : TorquePort;

        action transmitTorque {
            in inputTorque : Real;
            out outputTorque : Real;
        }
    }

    part def Transmission {
        port torqueInput : TorquePort;

        action receiveTorque {
            in torque : Real;
        }
    }

    port def TorquePort {
        attribute torqueValue : Real;
    }
}

package VehicleSystem {

    part def Vehicle {
        attribute dryMass : Real;
        attribute fuelMass : Real;
        attribute totalMass : Real;

        part engine : Engine;
        part transmission : Transmission;
        part clutch : Clutch;
        part fuelTank : FuelTank;
    }

    part def Engine {
        attribute generatedTorque : Real;
        attribute speed : Real;
    }

    part def Transmission {
        attribute inputTorque : Real;
    }

    part def Clutch {
        attribute transmittedTorque : Real;
    }

    part def FuelTank {
        attribute capacity : Real;
        attribute currentFuelMass : Real;
    }

    interface def TorqueTransmissionInterface {
        attribute torque : Real;
    }

    connection def TorqueConnection {
        end enginePort : TorqueTransmissionInterface;
        end transmissionPort : TorqueTransmissionInterface;
    }

    requirement def MassRequirements {
        doc /* The vehicle mass must not exceed specified limits */

        requirement fullTankMassLimit {
            doc /* With full fuel tank, total mass must not exceed 2000 kg */
            require constraint { totalMass <= 2000 }
        }

        requirement emptyTankMassLimit {
            doc /* With empty fuel tank, total mass must not exceed 1500 kg */
            require constraint { totalMass <= 1500 }
        }
    }

    requirement def TorqueTransmissionRequirement {
        doc /* Engine must transmit torque to transmission via clutch */
        subject engine : Engine;
        subject transmission : Transmission;
        subject clutch : Clutch;

        require constraint { engine.generatedTorque == clutch.transmittedTorque }
        require constraint { clutch.transmittedTorque == transmission.inputTorque }
    }

    requirement def EnginePerformanceRequirement {
        doc /* Engine must deliver torque according to speed-torque curve */
        subject engine : Engine;
    }

    constraint def VehicleMassConstraint {
        totalMass == dryMass + fuelMass
    }
}

package VehicleSystemModel {

    requirement def VehicleMassRequirement {
        doc /* The vehicle's total mass must not exceed 2000 kilograms when fully fueled, 
               and should not be higher than 1500 kilograms when empty. */

        attribute emptyMass : Real;
        attribute fullyFueledMass : Real;

        require constraint { emptyMass <= 1500.0 }
        require constraint { fullyFueledMass <= 2000.0 }
    }

    requirement def EngineTorqueTransmissionRequirement {
        doc /* The engine must be capable of effectively transmitting its generated torque 
               to the transmission system through a clutch interface. */
    }

    requirement def EngineTorquePerformanceRequirement {
        doc /* The torque generated by the engine should vary with speed, 
               conforming to the relevant performance table specifications. */
    }

    part def Vehicle {
        attribute emptyMass : Real;
        attribute fullyFueledMass : Real;

        part engine : Engine;

        satisfy vehicleMassReq : VehicleMassRequirement {
            :>> emptyMass = Vehicle::emptyMass;
            :>> fullyFueledMass = Vehicle::fullyFueledMass;
        }
    }

    part def Engine {
        port clutchInterface : ClutchPort;

        satisfy torqueTransmissionReq : EngineTorqueTransmissionRequirement;
        satisfy torquePerformanceReq : EngineTorquePerformanceRequirement;
    }

    port def ClutchPort {
        attribute torque : Real;
    }

    part def TransmissionSystem {
        port clutchInterface : ClutchPort;
    }

    connection def TorqueTransmission {
        end enginePort : ClutchPort;
        end transmissionPort : ClutchPort;
    }
}

