package VehicleDynamicPerformanceAnalysis {
    import ScalarValues::*;

    part def Vehicle {
        attribute curbWeight : Real;
        attribute payload : Real;
        attribute wheelDiameter : Real;
        attribute transmissionEfficiency : Real;
        attribute urbanFuelEconomy : Real;
        attribute highwayFuelEconomy : Real;

        attribute totalMass : Real = curbWeight + payload;

        port powerDemandPort : PowerPort;
        port accelerationPort : AccelerationPort;
        port speedPort : SpeedPort;
        port positionPort : PositionPort;
    }

    part def DrivingCycle {
        attribute timeStamps : Real[*] ordered;
        attribute positions : Real[*] ordered;
        attribute speeds : Real[*] ordered;
    }

    part def PerformanceAnalyzer {
        in item vehicleConfig : Vehicle;
        in item driveCycle : DrivingCycle;

        out attribute enginePower : Real[*] ordered;
        out attribute acceleration : Real[*] ordered;
        out attribute fuelConsumption : Real;
        out attribute fuelEconomy : Real;

        action calculateDynamics {
            in vehicle : Vehicle;
            in cycle : DrivingCycle;
            out power : Real[*] ordered;
            out accel : Real[*] ordered;
        }

        action calculateFuelEconomy {
            in vehicle : Vehicle;
            in cycle : DrivingCycle;
            in power : Real[*] ordered;
            out consumption : Real;
            out economy : Real;
        }
    }

    part def ComplianceVerifier {
        in item targetFuelEconomy : Real;
        in item actualFuelEconomy : Real;
        out attribute isCompliant : Boolean;

        action verifyCompliance {
            isCompliant := actualFuelEconomy >= targetFuelEconomy;
        }
    }

    port def PowerPort {
        attribute power : Real;
    }

    port def AccelerationPort {
        attribute acceleration : Real;
    }

    port def SpeedPort {
        attribute speed : Real;
    }

    port def PositionPort {
        attribute position : Real;
    }

    part vehicleDynamicSystem : VehicleDynamicSystem {
        part vehicle : Vehicle;
        part drivingCycle : DrivingCycle;
        part analyzer : PerformanceAnalyzer;
        part verifier : ComplianceVerifier;

        attribute targetEconomyMPG : Real = 30.0;

        connection : Connect {
            from vehicle to analyzer.vehicleConfig;
        }

        connection : Connect {
            from drivingCycle to analyzer.driveCycle;
        }

        connection : Connect {
            from analyzer.fuelEconomy to verifier.actualFuelEconomy;
        }

        connection : Connect {
            from targetEconomyMPG to verifier.targetFuelEconomy;
        }
    }

    part def VehicleDynamicSystem {
        part vehicle : Vehicle;
        part drivingCycle : DrivingCycle;
        part analyzer : PerformanceAnalyzer;
        part verifier : ComplianceVerifier;
    }
}

package VehicleFuelEconomyAnalysis {
    import ScalarValues::*;

    part def Vehicle {
        attribute curbWeight : Real;
        attribute load : Real;
        attribute wheelDiameter : Real;
        attribute transmissionEfficiency : Real;
        attribute totalMass : Real = curbWeight + load;
    }

    part def DrivingScenario {
        attribute scenarioType : String;
        attribute trajectory : Real[*];
        attribute speedProfile : Real[*];
    }

    part def SimulationAnalysis {
        attribute requiredWheelPower : Real;
        attribute acceleration : Real;
        attribute energyConsumption : Real;
        attribute enginePowerDemand : Real;
        attribute fuelConsumption : Real;
        attribute fuelEconomyL100km : Real;
        attribute fuelEconomyMPG : Real;
    }

    part def FuelEconomySystem {
        part vehicle : Vehicle;
        part urbanScenario : DrivingScenario {
            attribute :>> scenarioType = "Urban";
        }
        part highwayScenario : DrivingScenario {
            attribute :>> scenarioType = "Highway";
        }
        part urbanAnalysis : SimulationAnalysis;
        part highwayAnalysis : SimulationAnalysis;

        attribute targetFuelEconomyMPG : Real = 30.0;
        attribute meetsRequirement : Boolean;

        action calculateWheelPower {
            in scenario : DrivingScenario;
            in vehicleData : Vehicle;
            out power : Real;
        }

        action applyVehicleDynamics {
            in wheelPower : Real;
            in vehicleData : Vehicle;
            out acceleration : Real;
            out energyConsumption : Real;
        }

        action calculateEnginePower {
            in wheelPower : Real;
            in efficiency : Real;
            out enginePower : Real;
        }

        action estimateFuelConsumption {
            in enginePower : Real;
            in drivingTime : Real;
            out fuelConsumption : Real;
            out fuelEconomyL100km : Real;
            out fuelEconomyMPG : Real;
        }

        action performSimulation {
            in scenario : DrivingScenario;
            in vehicleData : Vehicle;
            out analysis : SimulationAnalysis;

            bind wheelPowerCalc = calculateWheelPower {
                in scenario = scenario;
                in vehicleData = vehicleData;
                out power = analysis.requiredWheelPower;
            }

            bind dynamicsCalc = applyVehicleDynamics {
                in wheelPower = analysis.requiredWheelPower;
                in vehicleData = vehicleData;
                out acceleration = analysis.acceleration;
                out energyConsumption = analysis.energyConsumption;
            }

            bind engineCalc = calculateEnginePower {
                in wheelPower = analysis.requiredWheelPower;
                in efficiency = vehicleData.transmissionEfficiency;
                out enginePower = analysis.enginePowerDemand;
            }

            bind fuelCalc = estimateFuelConsumption {
                in enginePower = analysis.enginePowerDemand;
                in drivingTime = 1.0;
                out fuelConsumption = analysis.fuelConsumption;
                out fuelEconomyL100km = analysis.fuelEconomyL100km;
                out fuelEconomyMPG = analysis.fuelEconomyMPG;
            }
        }

        action analyzeCompliance {
            bind urbanSim = performSimulation {
                in scenario = urbanScenario;
                in vehicleData = vehicle;
                out analysis = urbanAnalysis;
            }

            bind highwaySim = performSimulation {
                in scenario = highwayScenario;
                in vehicleData = vehicle;
                out analysis = highwayAnalysis;
            }

            then meetsRequirement = (urbanAnalysis.fuelEconomyMPG > targetFuelEconomyMPG) and 
                                   (highwayAnalysis.fuelEconomyMPG > targetFuelEconomyMPG);
        }
    }
}

